  ABSTRACT SUPERTYPE;
  assigned_action : action;
END_ENTITY; -- action_assignment

Edition 2 Express

ENTITY action_assignment ABSTRACT SUPERTYPE;
  assigned_action : action;
DERIVE
  role : object_role := get_role(SELF);
WHERE
  wr1 : SIZEOF(USEDIN(SELF, 
    'CONFIGURATION_CONTROL_3D_DESIGN_ED2_MIM_LF.ROLE_ASSOCIATION.ITEM_WITH_ROLE'
    )) >= 1;
END_ENTITY;

