  wr1 : SIZEOF(USEDIN(SELF, 

'CONFIGURATION_CONTROL_3D_DESIGN_ED2_MIM_LF.DESCRIPTION_ATTRIBUTE.DESCRIBED_ITEM'
    )) >= 1;
