DERIVE
  description : text := get_description_value(SELF);
  id : identifier := get_id_value(SELF);
INVERSE
  context_elements : SET [1:?] OF application_context_element FOR 
  frame_of_reference;
WHERE
  wr1 : SIZEOF(USEDIN(SELF, 

'CONFIGURATION_CONTROL_3D_DESIGN_ED2_MIM_LF.DESCRIPTION_ATTRIBUTE.DESCRIBED_ITEM'
    )) >= 1;
  wr2 : SIZEOF(USEDIN(SELF, 
    'CONFIGURATION_CONTROL_3D_DESIGN_ED2_MIM_LF.ID_ATTRIBUTE.IDENTIFIED_ITEM'))
     <= 1;
END_ENTITY;

