area_unit


Amendment 1 EXPRESS

ENTITY area_unit
  SUBTYPE OF (named_unit);
WHERE
  WR1: (SELF\named_unit.dimensions.length_exponent = 2) AND
       (SELF\named_unit.dimensions.mass_exponent = 0) AND
       (SELF\named_unit.dimensions.time_exponent = 0) AND
       (SELF\named_unit.dimensions.electric_current_exponent = 0) AND
       (SELF\named_unit.dimensions.
       thermodynamic_temperature_exponent = 0) AND
       (SELF\named_unit.dimensions.amount_of_substance_exponent = 0) AND
       (SELF\named_unit.dimensions.luminous_intensity_exponent = 0);
END_ENTITY; -- area_unit

Edition 2 Express

ENTITY area_unit
SUBTYPE OF (named_unit);
WHERE
  wr1 : (SELF\named_unit.dimensions.length_exponent = 2.0) AND (SELF\named_unit
    .dimensions.mass_exponent = 0.0) AND (SELF\named_unit.dimensions.
    time_exponent = 0.0) AND (SELF\named_unit.dimensions.
    electric_current_exponent = 0.0) AND (SELF\named_unit.dimensions.
    thermodynamic_temperature_exponent = 0.0) AND (SELF\named_unit.dimensions.
    amount_of_substance_exponent = 0.0) AND (SELF\named_unit.dimensions.
    luminous_intensity_exponent = 0.0);
END_ENTITY;

configuration_design


Amendment 1 EXPRESS

ENTITY configuration_design;
  configuration : configuration_item;
  design        : product_definition_formation;
UNIQUE
  UR1 : configuration, design;
END_ENTITY; -- configuration_design

Edition 2 Express

ENTITY configuration_design;
  configuration : configuration_item;
  design : configuration_design_item;
DERIVE
  name : label := get_name_value(SELF);
  description : text := get_description_value(SELF);
UNIQUE
  ur1 : configuration, design;
WHERE
  wr1 : SIZEOF(USEDIN(SELF, 
    'CONFIGURATION_CONTROL_3D_DESIGN_ED2_MIM_LF.NAME_ATTRIBUTE.NAMED_ITEM'))
     <= 1;
  wr2 : SIZEOF(USEDIN(SELF, 

'CONFIGURATION_CONTROL_3D_DESIGN_ED2_MIM_LF.DESCRIPTION_ATTRIBUTE.DESCRIBED_ITEM'
    )) <= 1;
END_ENTITY;


configuration_effectivity


Amendment 1 EXPRESS

ENTITY configuration_effectivity
  SUBTYPE OF (product_definition_effectivity);
  configuration : configuration_design;
UNIQUE
  UR1 : configuration, usage, id;
WHERE
  WR1: SELF\product_definition_effectivity.usage.
       relating_product_definition IN
       (USEDIN (SELF.configuration.design, 'CONFIG_CONTROL_DESIGN.' +
       'PRODUCT_DEFINITION.FORMATION') +
       relatives_of_product_definitions (bag_to_set (USEDIN
       (SELF.configuration.design, 'CONFIG_CONTROL_DESIGN.' +
       'PRODUCT_DEFINITION.FORMATION')), 'CONFIG_CONTROL_DESIGN.' +
       'PRODUCT_DEFINITION_USAGE'));
  WR2: 'CONFIG_CONTROL_DESIGN.PRODUCT_DEFINITION_USAGE' IN
       TYPEOF(SELF\product_definition_effectivity.usage);
END_ENTITY; -- configuration_effectivity

Edition 2 Express

ENTITY configuration_effectivity
SUBTYPE OF (product_definition_effectivity);
  configuration : configuration_design;
UNIQUE
  ur1 : configuration, usage, id;
WHERE
  wr1 : 'CONFIGURATION_CONTROL_3D_DESIGN_ED2_MIM_LF.PRODUCT_DEFINITION_USAGE' 
    IN TYPEOF(SELF\product_definition_effectivity.usage);
END_ENTITY;

