       TYPEOF(SELF.represented_product_relation.definition);
END_ENTITY; -- context_dependent_shape_representation

Edition 2 Express

ENTITY context_dependent_shape_representation;
  representation_relation : shape_representation_relationship;
  represented_product_relation : product_definition_shape;
DERIVE
  description : text := get_description_value(SELF);
  name : label := get_name_value(SELF);
WHERE
  wr1 : 
    'CONFIGURATION_CONTROL_3D_DESIGN_ED2_MIM_LF.PRODUCT_DEFINITION_RELATIONSHIP'
     IN TYPEOF(SELF.represented_product_relation.definition);
  wr2 : SIZEOF(USEDIN(SELF, 

'CONFIGURATION_CONTROL_3D_DESIGN_ED2_MIM_LF.DESCRIPTION_ATTRIBUTE.DESCRIBED_ITEM'
    )) <= 1;
  wr3 : SIZEOF(USEDIN(SELF, 
    'CONFIGURATION_CONTROL_3D_DESIGN_ED2_MIM_LF.NAME_ATTRIBUTE.NAMED_ITEM'))
     <= 1;
END_ENTITY;

coordinated_universal_time_offset


Amendment 1 EXPRESS

ENTITY coordinated_universal_time_offset;
  hour_offset   : hour_in_day;
  minute_offset : OPTIONAL minute_in_hour;
  sense         : ahead_or_behind;
END_ENTITY; -- coordinated_universal_time_offset

Edition 2 Express

ENTITY coordinated_universal_time_offset;
  hour_offset : INTEGER;
  minute_offset : OPTIONAL INTEGER;
  sense : ahead_or_behind;
DERIVE
  actual_minute_offset : INTEGER := NVL(minute_offset, 0);
WHERE
  wr1 : {0 <= hour_offset <= 24};
  wr2 : {0 <= actual_minute_offset <= 59};
  wr3 : NOT (((hour_offset <> 0) OR (actual_minute_offset <> 0)) AND (sense = 
    exact));
END_ENTITY;

curve_bounded_surface


Amendment 1 EXPRESS

ENTITY curve_bounded_surface
  SUBTYPE OF (bounded_surface);
  basis_surface  : surface;
  boundaries     : SET [1:?] OF boundary_curve;
  implicit_outer : BOOLEAN;
WHERE
  WR1: NOT (implicit_outer AND
       ('CONFIG_CONTROL_DESIGN.OUTER_BOUNDARY_CURVE' IN TYPEOF(boundaries)));
  WR2: (NOT implicit_outer) OR
       ('CONFIG_CONTROL_DESIGN.BOUNDED_SURFACE' IN TYPEOF(basis_surface));
  WR3: SIZEOF (QUERY (temp <* boundaries |
       'CONFIG_CONTROL_DESIGN.OUTER_BOUNDARY_CURVE' IN TYPEOF(temp))) <= 1;
  WR4: SIZEOF (QUERY (temp <* boundaries |
       temp\composite_curve_on_surface.basis_surface[1] :<>: 
       SELF.basis_surface)) = 0;
END_ENTITY; -- curve_bounded_surface

Edition 2 Express

ENTITY curve_bounded_surface
SUBTYPE OF (bounded_surface);
  basis_surface : surface;
  boundaries : SET [1:?] OF boundary_curve;
  implicit_outer : BOOLEAN;
WHERE
  wr1 : NOT implicit_outer OR (SIZEOF(QUERY(temp <* boundaries | 
    'CONFIGURATION_CONTROL_3D_DESIGN_ED2_MIM_LF.OUTER_BOUNDARY_CURVE' IN TYPEOF
    (temp))) = 0);
  wr2 : NOT implicit_outer OR (
    'CONFIGURATION_CONTROL_3D_DESIGN_ED2_MIM_LF.BOUNDED_SURFACE' IN TYPEOF(
    basis_surface));
  wr3 : SIZEOF(QUERY(temp <* boundaries | 
    'CONFIGURATION_CONTROL_3D_DESIGN_ED2_MIM_LF.OUTER_BOUNDARY_CURVE' IN TYPEOF
    (temp))) <= 1;
  wr4 : SIZEOF(QUERY(temp <* boundaries | temp\composite_curve_on_surface.
    basis_surface[1] <> SELF.basis_surface)) = 0;
END_ENTITY;

