WHERE
  WR1: SELF :<>: upper_usage;
  WR2: SELF\product_definition_relationship.relating_product_definition :=:
       upper_usage.relating_product_definition;
  WR3: SELF\product_definition_relationship.related_product_definition :=:
       next_usage.related_product_definition;
  WR4: upper_usage.related_product_definition :=:
       next_usage.relating_product_definition;
  WR5: NOT ('CONFIG_CONTROL_DESIGN.PROMISSORY_USAGE_OCCURRENCE' IN
       TYPEOF(upper_usage));
END_ENTITY; -- specified_higher_usage_occurrence

Edition 2 Express

ENTITY specified_higher_usage_occurrence
SUBTYPE OF (assembly_component_usage);
  upper_usage : assembly_component_usage;
  next_usage : next_assembly_usage_occurrence;
UNIQUE
  ur1 : upper_usage, next_usage;
WHERE
  wr1 : SELF :<>: upper_usage;
  wr2 : SELF\product_definition_relationship.relating_product_definition :=: 
    upper_usage.relating_product_definition;
  wr3 : SELF\product_definition_relationship.related_product_definition :=: 
    next_usage.related_product_definition;
  wr4 : (upper_usage.related_product_definition :=: next_usage.
    relating_product_definition) OR (SIZEOF(QUERY(pdr <* USEDIN(upper_usage.
    related_product_definition,  
'CONFIGURATION_CONTROL_3D_DESIGN_ED2_MIM_LF.PRODUCT_DEFINITION_RELATIONSHIP.RELATED_PRODUCT_DEFINITION'
    ) | pdr.relating_product_definition :=: next_usage.
    relating_product_definition)) = 1);
  wr5 : SIZEOF([    
'CONFIGURATION_CONTROL_3D_DESIGN_ED2_MIM_LF.NEXT_ASSEMBLY_USAGE_OCCURRENCE',    
'CONFIGURATION_CONTROL_3D_DESIGN_ED2_MIM_LF.SPECIFIED_HIGHER_USAGE_OCCURRENCE'] 
* TYPEOF(upper_usage)) = 1;
END_ENTITY;

