role_bag : BAG OF role_association := (USEDIN(obj, 'AP236_FURNITURE_CATALOG_AND_INTERIOR_DESIGN_MIM_LF.' + 'ROLE_ASSOCIATION.' + 'ITEM_WITH_ROLE'));
