IF SIZEOF(pd_set) < 1 THEN
RETURN(pdr_set);
END_IF;
REPEAT i := 1 TO HIINDEX(pd_set) BY 1;
pdr_set := pdr_set + bag_to_set(USEDIN(pd_set[i],'AP239_PART_DEFINITION_INFORMATION_MIM_LF.PROPERTY_DEFINITION_REPRESENTATION.DEFINITION'));
END_REPEAT;
RETURN(pdr_set);

