function.algorithm:
LOCAL
  dir2 : direction := dummy_gri||direction([1.000000, 0.000000, 0.000000]);
  dir3 : direction := dummy_gri||direction([1.000000, 0.000000, 0.000000]);
  dir4 : direction := dummy_gri||direction([1.000000, 0.000000, 0.000000]);
  val : REAL;
  mag : REAL;
END_LOCAL;

IF p1.dim <> 3 THEN
  RETURN (?);
END_IF;
REPEAT i := 1 TO 3;
  dir2.direction_ratios[i] := p2.coordinates[i] - p1.coordinates[i];
  dir3.direction_ratios[i] := p3.coordinates[i] - p1.coordinates[i];
  dir4.direction_ratios[i] := p4.coordinates[i] - p1.coordinates[i];
  mag := dir4.direction_ratios[i] * dir4.direction_ratios[i];
END_REPEAT;
mag := (mag);
val := mag * dot_product(dir4, cross_product(dir2, dir3).orientation);
RETURN (val);

LOCAL
  x : SET OF action_method_relationship;
END_LOCAL;

IF relation.relating_method IN relatives THEN
  RETURN (FALSE);
END_IF;
x := QUERY(am <* bag_to_set(USEDIN(relation.relating_method, 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.ACTION_METHOD_RELATIONSHIP.RELATED_METHOD')) | specific_relation IN TYPEOF(am));
REPEAT i := 1 TO HIINDEX(x);
  IF NOT acyclic_action_method_relationship(x[i], relatives + relation.relating_method, specific_relation) THEN
    RETURN (FALSE);
  END_IF;
END_REPEAT;
RETURN (TRUE);

LOCAL
  x : SET OF action_relationship;
END_LOCAL;

IF relation.relating_action IN relatives THEN
