x := QUERY(actn <* bag_to_set(USEDIN(relation.relating_action, 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.ACTION_RELATIONSHIP.RELATED_ACTION')) | specific_relation IN TYPEOF(actn));
REPEAT i := 1 TO HIINDEX(x);
  IF NOT acyclic_action_relationship(x[i], relatives + relation.relating_action, specific_relation) THEN
    RETURN (FALSE);
  END_IF;
END_REPEAT;
RETURN (TRUE);

