LOCAL
  item : identification_item;
  role : identification_role;
END_LOCAL;

role := aia.role;
IF role.name = 'alias' THEN
  REPEAT i := LOINDEX(aia.items) TO HIINDEX(aia.items);
    item := aia.items[i];
    IF SIZEOF(['AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.' + 'ALIASABLE_ITEM'] * TYPEOF(item)) = 0 THEN
      RETURN (FALSE);
    END_IF;
  END_REPEAT;
  RETURN (TRUE);
ELSE
  RETURN (TRUE);
END_IF;

LOCAL
  sdr_set : SET OF shape_definition_representation := [];
  srr_set : SET OF shape_representation_relationship := [];
  sdr1_set : SET OF shape_definition_representation := [];
  pd_set : SET OF property_definition := [];
  pdr_set : SET OF product_definition_relationship := [];
  pds_set : SET OF product_definition_shape := [];
  prop_set : SET OF property_definition := [];
END_LOCAL;

pd_set := bag_to_set(USEDIN(assy.related_product_definition, 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.PROPERTY_DEFINITION.DEFINITION'));
pdr_set := QUERY(pdr <* bag_to_set(USEDIN(assy.related_product_definition, 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.PRODUCT_DEFINITION_RELATIONSHIP.RELATED_PRODUCT_DEFINITION')) | SIZEOF(USEDIN(pdr, 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.PROPERTY_DEFINITION.DEFINITION')) > 0);
IF SIZEOF(pd_set) > 0 THEN
  REPEAT i := 1 TO HIINDEX(pd_set);
    sdr_set := sdr_set + QUERY(pdr <* USEDIN(pd_set[i], 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.PROPERTY_DEFINITION_REPRESENTATION.DEFINITION') | schema_name + '.SHAPE_DEFINITION_REPRESENTATION' IN TYPEOF(pdr));
  END_REPEAT;
END_IF;
IF SIZEOF(pdr_set) > 0 THEN
  REPEAT i := 1 TO HIINDEX(pdr_set);
    prop_set := prop_set + bag_to_set(USEDIN(pdr_set[i], 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.PROPERTY_DEFINITION.DEFINITION'));
  END_REPEAT;
  IF SIZEOF(prop_set) > 0 THEN
    REPEAT i := 1 TO HIINDEX(prop_set);
      sdr_set := sdr_set + QUERY(pdr <* USEDIN(prop_set[i], 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.PROPERTY_DEFINITION_REPRESENTATION.DEFINITION') | schema_name + '.SHAPE_DEFINITION_REPRESENTATION' IN TYPEOF(pdr));
    END_REPEAT;
  END_IF;
END_IF;
IF SIZEOF(sdr_set) > 0 THEN
  REPEAT i := 1 TO HIINDEX(sdr_set);
    srr_set := QUERY(rr <* bag_to_set(USEDIN(sdr_set[i]\property_definition_representation.used_representation, 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.REPRESENTATION_RELATIONSHIP.REP_2')) | schema_name + '.SHAPE_REPRESENTATION_RELATIONSHIP' IN TYPEOF(rr));
    pd_set := bag_to_set(USEDIN(assy.relating_product_definition, 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.PROPERTY_DEFINITION.DEFINITION'));
    IF SIZEOF(pd_set) > 0 THEN
      REPEAT i := 1 TO HIINDEX(pd_set);
        sdr1_set := sdr1_set + QUERY(pdr <* USEDIN(pd_set[i], 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.PROPERTY_DEFINITION_REPRESENTATION.DEFINITION') | schema_name + '.SHAPE_DEFINITION_REPRESENTATION' IN TYPEOF(pdr));
      END_REPEAT;
    END_IF;
    IF (SIZEOF(sdr_set) > 0) AND (SIZEOF(sdr1_set) > 0) THEN
      IF SIZEOF(srr_set) > 0 THEN
        REPEAT j := 1 TO HIINDEX(srr_set);
          IF SIZEOF(QUERY(pdr <* bag_to_set(USEDIN(srr_set[j]\representation_relationship.rep_1, 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.PROPERTY_DEFINITION_REPRESENTATION.USED_REPRESENTATION')) | schema_name + '.SHAPE_DEFINITION_REPRESENTATION' IN TYPEOF(pdr)) * sdr1_set) >= 1 THEN
            pds_set := QUERY(x <* bag_to_set(USEDIN(assy, 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.PROPERTY_DEFINITION.DEFINITION')) | schema_name + '.PRODUCT_DEFINITION_SHAPE' IN TYPEOF(x));
            IF SIZEOF(pds_set) = 0 THEN
              RETURN (FALSE);
            END_IF;
            REPEAT k := 1 TO HIINDEX(pds_set);
              IF SIZEOF(QUERY(cdsr <* USEDIN(pds_set[k], 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.CONTEXT_DEPENDENT_SHAPE_REPRESENTATION.REPRESENTED_PRODUCT_RELATION') | cdsr.representation_relation :=: srr_set[j])) > 0 THEN
                RETURN (FALSE);
              END_IF;
            END_REPEAT;
          END_IF;
        END_REPEAT;
      END_IF;
    END_IF;
  END_REPEAT;
END_IF;
RETURN (TRUE);

LOCAL
  surf : surface;
END_LOCAL;

IF 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.PCURVE' IN TYPEOF(arg) THEN
  surf := arg.basis_surface;
ELSE
  surf := arg;
END_IF;
RETURN (surf);

LOCAL
  the_set : SET OF GENERIC:intype := [];
END_LOCAL;

IF SIZEOF(the_bag) > 0 THEN
  REPEAT i := 1 TO HIINDEX(the_bag);
    the_set := the_set + the_bag[i];
  END_REPEAT;
END_IF;
RETURN (the_set);

LOCAL
  u : LIST [2:3] OF direction;
  factor : REAL;
  d1 : direction;
  d2 : direction;
END_LOCAL;

IF dim = 3 THEN
  d1 := NVL(normalise(axis3), dummy_gri||direction([0.000000, 0.000000, 1.000000]));
  d2 := first_proj_axis(d1, axis1);
  u := [d2, second_proj_axis(d1, d2, axis2), d1];
ELSE
  IF EXISTS(axis1) THEN
    d1 := normalise(axis1);
    u := [d1, orthogonal_complement(d1)];
    IF EXISTS(axis2) THEN
      factor := dot_product(axis2, u[2]);
      IF factor < 0.000000 THEN
        u[2].direction_ratios[1] :=  - u[2].direction_ratios[1];
        u[2].direction_ratios[2] :=  - u[2].direction_ratios[2];
      END_IF;
    END_IF;
  ELSE
    IF EXISTS(axis2) THEN
      d1 := normalise(axis2);
      u := [orthogonal_complement(d1), d1];
      u[1].direction_ratios[1] :=  - u[1].direction_ratios[1];
      u[1].direction_ratios[2] :=  - u[1].direction_ratios[2];
    ELSE
      u := [dummy_gri||direction([1.000000, 0.000000]), dummy_gri||direction([0.000000, 1.000000])];
    END_IF;
  END_IF;
END_IF;
RETURN (u);

IF b THEN
  RETURN (choice1);
ELSE
  RETURN (choice2);
END_IF;

LOCAL
  d : direction := NVL(normalise(ref_direction), dummy_gri||direction([1.000000, 0.000000]));
END_LOCAL;

RETURN ([d, orthogonal_complement(d)]);

LOCAL
  d1 : direction;
  d2 : direction;
END_LOCAL;

d1 := NVL(normalise(axis), dummy_gri||direction([0.000000, 0.000000, 1.000000]));
d2 := first_proj_axis(d1, ref_direction);
RETURN ([d2, normalise(cross_product(d1, d2)).orientation, d1]);

LOCAL
  s : SET [1:?] OF geometric_set_select := gset.elements;
  trset : SET OF geometric_set_select := [];
END_LOCAL;

REPEAT j := 1 TO SIZEOF(s);
  IF 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.CURVE' IN TYPEOF(s[j]) THEN
    trset := trset + dummy_gri||curve()||curve_replica(s[j], tr);
  ELSE
    IF 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.POINT' IN TYPEOF(s[j]) THEN
      trset := trset + dummy_gri||point()||point_replica(s[j], tr);
    ELSE
      IF 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.SURFACE' IN TYPEOF(s[j]) THEN
        trset := trset + dummy_gri||surface()||surface_replica(s[j], tr||cartesian_transformation_operator_3d(?));
      END_IF;
    END_IF;
  END_IF;
END_REPEAT;
RETURN (trset);

LOCAL
  category_assignments : BAG OF product_category;
  categories : SET OF STRING := [];
  i : INTEGER;
END_LOCAL;

category_assignments := USEDIN(obj, 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.PRODUCT_RELATED_PRODUCT_CATEGORY.PRODUCTS');
REPEAT i := LOINDEX(category_assignments) TO HIINDEX(category_assignments);
  categories := categories + category_assignments[i].name;
END_REPEAT;
RETURN (categories);

LOCAL
  the_reverse : oriented_closed_shell;
END_LOCAL;

IF 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.ORIENTED_CLOSED_SHELL' IN TYPEOF(a_shell) THEN
  the_reverse := dummy_tri||connected_face_set(a_shell\connected_face_set.cfs_faces)||closed_shell()||oriented_closed_shell(a_shell\oriented_closed_shell.closed_shell_element, NOT a_shell\oriented_closed_shell.orientation);
ELSE
  the_reverse := dummy_tri||connected_face_set(a_shell\connected_face_set.cfs_faces)||closed_shell()||oriented_closed_shell(a_shell, FALSE);
END_IF;
RETURN (the_reverse);

IF p THEN
  RETURN (an_item);
ELSE
  RETURN (topology_reversed(an_item));
END_IF;

LOCAL
  n_segments : INTEGER := SIZEOF(c.segments);
END_LOCAL;

REPEAT k := 1 TO n_segments;
  IF NOT ('AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.PCURVE' IN TYPEOF(c\composite_curve.segments[k].parent_curve)) AND NOT ('AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.SURFACE_CURVE' IN TYPEOF(c\composite_curve.segments[k].parent_curve)) AND NOT ('AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.COMPOSITE_CURVE_ON_SURFACE' IN TYPEOF(c\composite_curve.segments[k].parent_curve)) THEN
    RETURN (FALSE);
  END_IF;
END_REPEAT;
RETURN (TRUE);

LOCAL
  result : BOOLEAN := TRUE;
END_LOCAL;

REPEAT j := 1 TO SIZEOF(m.sbsm_boundary);
  IF NOT ('AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.OPEN_SHELL' IN TYPEOF(m.sbsm_boundary[j])) AND NOT ('AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.CLOSED_SHELL' IN TYPEOF(m.sbsm_boundary[j])) THEN
    result := FALSE;
    RETURN (result);
  END_IF;
END_REPEAT;
RETURN (result);

LOCAL
  result : BOOLEAN := TRUE;
END_LOCAL;

REPEAT j := 1 TO SIZEOF(m.sbwm_boundary);
  IF NOT ('AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.WIRE_SHELL' IN TYPEOF(m.sbwm_boundary[j])) AND NOT ('AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.VERTEX_SHELL' IN TYPEOF(m.sbwm_boundary[j])) THEN
    result := FALSE;
    RETURN (result);
  END_IF;
END_REPEAT;
RETURN (result);

LOCAL
  result : BOOLEAN := TRUE;
  k : INTEGER;
  sum : INTEGER;
END_LOCAL;

sum := knot_mult[1];
REPEAT i := 2 TO up_knots;
  sum := sum + knot_mult[i];
END_REPEAT;
IF (degree < 1) OR (up_knots < 2) OR (up_cp < degree) OR (sum <> degree + up_cp + 2) THEN
  result := FALSE;
  RETURN (result);
END_IF;
k := knot_mult[1];
IF (k < 1) OR (k > degree + 1) THEN
  result := FALSE;
  RETURN (result);
END_IF;
REPEAT i := 2 TO up_knots;
  IF (knot_mult[i] < 1) OR (knots[i] <= knots[i - 1]) THEN
    result := FALSE;
    RETURN (result);
  END_IF;
  k := knot_mult[i];
  IF (i < up_knots) AND (k > degree) THEN
    result := FALSE;
    RETURN (result);
  END_IF;
  IF (i = up_knots) AND (k > degree + 1) THEN
    result := FALSE;
    RETURN (result);
  END_IF;
END_REPEAT;
RETURN (result);

REPEAT i := 1 TO s.n_u;
  REPEAT j := 1 TO s.n_v;
    IF NOT (('AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.B_SPLINE_SURFACE' IN TYPEOF(s.segments[i][j].parent_surface)) OR ('AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.RECTANGULAR_TRIMMED_SURFACE' IN TYPEOF(s.segments[i][j].parent_surface))) THEN
      RETURN (FALSE);
    END_IF;
  END_REPEAT;
END_REPEAT;
REPEAT i := 1 TO s.n_u - 1;
  REPEAT j := 1 TO s.n_v;
    IF s.segments[i][j].u_transition = discontinuous THEN
      RETURN (FALSE);
    END_IF;
  END_REPEAT;
END_REPEAT;
REPEAT i := 1 TO s.n_u;
  REPEAT j := 1 TO s.n_v - 1;
    IF s.segments[i][j].v_transition = discontinuous THEN
      RETURN (FALSE);
    END_IF;
  END_REPEAT;
END_REPEAT;
RETURN (TRUE);

LOCAL
  mag : REAL;
  res : direction;
  v1 : LIST [3:3] OF REAL;
  v2 : LIST [3:3] OF REAL;
  result : vector;
END_LOCAL;

IF NOT EXISTS(arg1) OR (arg1.dim = 2) OR NOT EXISTS(arg2) OR (arg2.dim = 2) THEN
  RETURN (?);
ELSE
  v1 := normalise(arg1).direction_ratios;
  v2 := normalise(arg2).direction_ratios;
  res := dummy_gri||direction([v1[2] * v2[3] - v1[3] * v2[2], v1[3] * v2[1] - v1[1] * v2[3], v1[1] * v2[2] - v1[2] * v2[1]]);
  mag := 0.000000;
  REPEAT i := 1 TO 3;
    mag := mag + res.direction_ratios[i] * res.direction_ratios[i];
  END_REPEAT;
  IF mag > 0.000000 THEN
    result := dummy_gri||vector(res, (mag));
  ELSE
    result := dummy_gri||vector(arg1, 0.000000);
  END_IF;
  RETURN (result);
END_IF;

LOCAL
  result : BOOLEAN := TRUE;
END_LOCAL;

REPEAT i := 0 TO b.upper_index_on_control_points;
  IF b.weights[i] <= 0.000000 THEN
    result := FALSE;
    RETURN (result);
  END_IF;
END_REPEAT;
RETURN (result);

LOCAL
  knot_mult : LIST [1:up_knots] OF INTEGER;
END_LOCAL;

IF uniform = uniform_knots THEN
  knot_mult := [1:up_knots];
ELSE
  IF uniform = quasi_uniform_knots THEN
    knot_mult := [1:up_knots];
    knot_mult[1] := degree + 1;
    knot_mult[up_knots] := degree + 1;
  ELSE
    IF uniform = piecewise_bezier_knots THEN
      knot_mult := [degree:up_knots];
      knot_mult[1] := degree + 1;
      knot_mult[up_knots] := degree + 1;
    ELSE
      knot_mult := [0:up_knots];
    END_IF;
  END_IF;
END_IF;
RETURN (knot_mult);

LOCAL
  knots : LIST [1:up_knots] OF parameter_value := [0:up_knots];
  ishift : INTEGER := 1;
END_LOCAL;

IF uniform = uniform_knots THEN
  ishift := degree + 1;
END_IF;
IF (uniform = uniform_knots) OR (uniform = quasi_uniform_knots) OR (uniform = piecewise_bezier_knots) THEN
  REPEAT i := 1 TO up_knots;
    knots[i] := i - ishift;
  END_REPEAT;
END_IF;
RETURN (knots);

LOCAL
  result : dimensional_exponents := dimensional_exponents(0.000000, 0.000000, 0.000000, 0.000000, 0.000000, 0.000000, 0.000000);
END_LOCAL;

IF 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.DERIVED_UNIT' IN TYPEOF(x) THEN
  REPEAT i := LOINDEX(x.elements) TO HIINDEX(x.elements);
    result.length_exponent := result.length_exponent + x.elements[i].exponent * x.elements[i].unit.dimensions.length_exponent;
    result.mass_exponent := result.mass_exponent + x.elements[i].exponent * x.elements[i].unit.dimensions.mass_exponent;
    result.time_exponent := result.time_exponent + x.elements[i].exponent * x.elements[i].unit.dimensions.time_exponent;
    result.electric_current_exponent := result.electric_current_exponent + x.elements[i].exponent * x.elements[i].unit.dimensions.electric_current_exponent;
    result.thermodynamic_temperature_exponent := result.thermodynamic_temperature_exponent + x.elements[i].exponent * x.elements[i].unit.dimensions.thermodynamic_temperature_exponent;
    result.amount_of_substance_exponent := result.amount_of_substance_exponent + x.elements[i].exponent * x.elements[i].unit.dimensions.amount_of_substance_exponent;
    result.luminous_intensity_exponent := result.luminous_intensity_exponent + x.elements[i].exponent * x.elements[i].unit.dimensions.luminous_intensity_exponent;
  END_REPEAT;
ELSE
  result := x.dimensions;
END_IF;
RETURN (result);

LOCAL
  x : SET OF representation;
  y : representation_context;
  dim : dimension_count;
END_LOCAL;

IF 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.CARTESIAN_POINT' IN TYPEOF(item) THEN
  dim := SIZEOF(item\cartesian_point.coordinates);
  RETURN (dim);
END_IF;
IF 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.DIRECTION' IN TYPEOF(item) THEN
  dim := SIZEOF(item\direction.direction_ratios);
  RETURN (dim);
END_IF;
IF 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.VECTOR' IN TYPEOF(item) THEN
  dim := SIZEOF(item\vector.orientation\direction.direction_ratios);
  RETURN (dim);
END_IF;
x := using_representations(item);
y := x[1].context_of_items;
dim := y\geometric_representation_context.coordinate_space_dimension;
RETURN (dim);

LOCAL
  scalar : REAL;
  vec1 : direction;
  vec2 : direction;
  ndim : INTEGER;
END_LOCAL;

IF NOT EXISTS(arg1) OR NOT EXISTS(arg2) THEN
  scalar := ?;
ELSE
  IF arg1.dim <> arg2.dim THEN
    scalar := ?;
  ELSE
    vec1 := normalise(arg1);
    vec2 := normalise(arg2);
    ndim := arg1.dim;
    scalar := 0.000000;
    REPEAT i := 1 TO ndim;
      scalar := scalar + vec1.direction_ratios[i] * vec2.direction_ratios[i];
    END_REPEAT;
  END_IF;
END_IF;
RETURN (scalar);

LOCAL
  a_curve : curve;
  result : SET OF pcurve;
  the_geometry : LIST [1:2] OF pcurve_or_surface;
END_LOCAL;

a_curve := an_edge.edge_geometry;
result := [];
IF 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.PCURVE' IN TYPEOF(a_curve) THEN
  result := result + a_curve;
ELSE
  IF 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.SURFACE_CURVE' IN TYPEOF(a_curve) THEN
    the_geometry := a_curve\surface_curve.associated_geometry;
    REPEAT k := 1 TO SIZEOF(the_geometry);
      IF 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.PCURVE' IN TYPEOF(the_geometry[k]) THEN
        result := result + the_geometry[k];
      END_IF;
    END_REPEAT;
  ELSE
    REPEAT j := 1 TO SIZEOF(the_surface_curves);
      the_geometry := the_surface_curves[j].associated_geometry;
      IF the_surface_curves[j].curve_3d :=: a_curve THEN
        REPEAT k := 1 TO SIZEOF(the_geometry);
          IF 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.PCURVE' IN TYPEOF(the_geometry[k]) THEN
            result := result + the_geometry[k];
          END_IF;
        END_REPEAT;
      END_IF;
    END_REPEAT;
  END_IF;
END_IF;
RETURN (result);

LOCAL
  the_reverse : oriented_edge;
END_LOCAL;

IF 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.ORIENTED_EDGE' IN TYPEOF(an_edge) THEN
  the_reverse := dummy_tri||edge(an_edge.edge_end, an_edge.edge_start)||oriented_edge(an_edge\oriented_edge.edge_element, NOT an_edge\oriented_edge.orientation);
ELSE
  the_reverse := dummy_tri||edge(an_edge.edge_end, an_edge.edge_start)||oriented_edge(an_edge, FALSE);
END_IF;
RETURN (the_reverse);

LOCAL
  the_reverse : face_bound;
END_LOCAL;

IF 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.FACE_OUTER_BOUND' IN TYPEOF(a_face_bound) THEN
  the_reverse := dummy_tri||face_bound(a_face_bound\face_bound.bound, NOT a_face_bound\face_bound.orientation)||face_outer_bound();
ELSE
  the_reverse := dummy_tri||face_bound(a_face_bound.bound, NOT a_face_bound.orientation);
END_IF;
RETURN (the_reverse);

LOCAL
  the_reverse : oriented_face;
END_LOCAL;

IF 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.ORIENTED_FACE' IN TYPEOF(a_face) THEN
  the_reverse := dummy_tri||face(set_of_topology_reversed(a_face.bounds))||oriented_face(a_face\oriented_face.face_element, NOT a_face\oriented_face.orientation);
ELSE
  the_reverse := dummy_tri||face(set_of_topology_reversed(a_face.bounds))||oriented_face(a_face, FALSE);
END_IF;
RETURN (the_reverse);

LOCAL
  x_axis : direction;
  v : direction;
  z : direction;
  x_vec : vector;
END_LOCAL;

IF NOT EXISTS(z_axis) THEN
  RETURN (?);
ELSE
  z := normalise(z_axis);
  IF NOT EXISTS(arg) THEN
    IF (z.direction_ratios <> [1.000000, 0.000000, 0.000000]) AND (z.direction_ratios <> [ - 1.000000, 0.000000, 0.000000]) THEN
      v := dummy_gri||direction([1.000000, 0.000000, 0.000000]);
    ELSE
      v := dummy_gri||direction([0.000000, 1.000000, 0.000000]);
    END_IF;
  ELSE
    IF arg.dim <> 3 THEN
      RETURN (?);
    END_IF;
    IF cross_product(arg, z).magnitude = 0.000000 THEN
      RETURN (?);
    ELSE
      v := normalise(arg);
    END_IF;
  END_IF;
  x_vec := scalar_times_vector(dot_product(v, z), z);
  x_axis := vector_difference(v, x_vec).orientation;
  x_axis := normalise(x_axis);
END_IF;
RETURN (x_axis);

LOCAL
  surfs : SET [0:2] OF surface;
  n : INTEGER;
END_LOCAL;

surfs := [];
IF 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.PCURVE' IN TYPEOF(c) THEN
  surfs := [c\pcurve.basis_surface];
ELSE
  IF 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.SURFACE_CURVE' IN TYPEOF(c) THEN
    n := SIZEOF(c\surface_curve.associated_geometry);
    REPEAT i := 1 TO n;
      surfs := surfs + associated_surface(c\surface_curve.associated_geometry[i]);
    END_REPEAT;
  END_IF;
END_IF;
IF 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.COMPOSITE_CURVE_ON_SURFACE' IN TYPEOF(c) THEN
  n := SIZEOF(c\composite_curve.segments);
  surfs := get_basis_surface(c\composite_curve.segments[1].parent_curve);
  IF n > 1 THEN
    REPEAT i := 2 TO n;
      surfs := surfs * get_basis_surface(c\composite_curve.segments[i].parent_curve);
    END_REPEAT;
  END_IF;
END_IF;
RETURN (surfs);

