IF SIZEOF(name_bag) = 1 THEN
  RETURN (name_bag[1].attribute_value);
ELSE
  RETURN (?);
END_IF;

LOCAL
  pd_set : SET OF product_definition_formation := [];
  pdr_set : SET OF product_definition := [];
END_LOCAL;

pd_set := bag_to_set(USEDIN(c_def_instance, 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.PRODUCT_DEFINITION_FORMATION.OF_PRODUCT'));
IF SIZEOF(pd_set) < 1 THEN
  RETURN (pdr_set);
END_IF;
REPEAT i := 1 TO HIINDEX(pd_set);
  pdr_set := pdr_set + bag_to_set(USEDIN(pd_set[i], 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.PRODUCT_DEFINITION.FORMATION'));
END_REPEAT;
RETURN (pdr_set);

