pd_set := bag_to_set(USEDIN(c_def_instance, 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.PROPERTY_DEFINITION.DEFINITION'));
IF SIZEOF(pd_set) < 1 THEN
  RETURN (pdr_set);
