REPEAT i := 1 TO HIINDEX(pd_set);
  pdr_set := pdr_set + bag_to_set(USEDIN(pd_set[i], 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.PROPERTY_DEFINITION_REPRESENTATION.DEFINITION'));
END_REPEAT;
RETURN (pdr_set);

