LOCAL
  pd_set : SET OF product_definition_shape := [];
  pdr_set : SET OF shape_aspect := [];
END_LOCAL;

pd_set := bag_to_set(QUERY(pd <* USEDIN(c_def_instance, 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.PROPERTY_DEFINITION.DEFINITION') | 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.PRODUCT_DEFINITION_SHAPE' IN TYPEOF(pd)));
IF SIZEOF(pd_set) < 1 THEN
  RETURN (pdr_set);
END_IF;
REPEAT i := 1 TO HIINDEX(pd_set);
  pdr_set := pdr_set + bag_to_set(USEDIN(pd_set[i], 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.SHAPE_ASPECT.OF_SHAPE'));
END_REPEAT;
RETURN (pdr_set);

LOCAL
  initial_context : product_definition_context;
  context_associations : SET OF product_definition_context_association;
END_LOCAL;

initial_context := pd.frame_of_reference;
context_associations := QUERY(pdca <* bag_to_set(USEDIN(pd, 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.PRODUCT_DEFINITION_CONTEXT_ASSOCIATION.DEFINITION')) | (pdca.role.name = 'additional context') AND (pdca.frame_of_reference :=: initial_context));
RETURN (SIZEOF(context_associations) > 0);

LOCAL
  y : BAG OF representation_item;
END_LOCAL;

IF SIZEOF(USEDIN(item, 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.REPRESENTATION.ITEMS') * cntxt.representations_in_context) > 0 THEN
  RETURN (TRUE);
ELSE
  y := QUERY(z <* USEDIN(item, '') | 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.REPRESENTATION_ITEM' IN TYPEOF(z));
  IF SIZEOF(y) > 0 THEN
    REPEAT i := 1 TO HIINDEX(y);
      IF item_in_context(y[i], cntxt) THEN
        RETURN (TRUE);
      END_IF;
    END_REPEAT;
  END_IF;
END_IF;
RETURN (FALSE);

IF (year MOD 4 = 0) AND (year MOD 100 <> 0) OR (year MOD 400 = 0) THEN
  RETURN (TRUE);
ELSE
  RETURN (FALSE);
END_IF;

LOCAL
  loops : LIST OF loop := [];
END_LOCAL;

REPEAT i := 1 TO SIZEOF(f.bounds);
  loops := loops + f.bounds[i].bound;
END_REPEAT;
RETURN (loops);

