  edges : LIST OF edge := [];
END_LOCAL;

IF 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.EDGE_LOOP' IN TYPEOF(l) THEN
  REPEAT i := 1 TO SIZEOF(l\path.edge_list);
    edges := edges + l\path.edge_list[i].edge_element;
  END_REPEAT;
END_IF;
RETURN (edges);

