  edges : LIST OF edge := [];
END_LOCAL;

REPEAT i := 1 TO SIZEOF(list_shell_loops(s));
  edges := edges + list_loop_edges(list_shell_loops(s)[i]);
END_REPEAT;
RETURN (edges);

LOCAL
  faces : LIST OF face := [];
END_LOCAL;

IF ('AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.CLOSED_SHELL' IN TYPEOF(s)) OR ('AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.OPEN_SHELL' IN TYPEOF(s)) THEN
  REPEAT i := 1 TO SIZEOF(s\connected_face_set.cfs_faces);
    faces := faces + s\connected_face_set.cfs_faces[i];
  END_REPEAT;
END_IF;
RETURN (faces);

LOCAL
  loops : LIST OF loop := [];
END_LOCAL;

IF 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.VERTEX_SHELL' IN TYPEOF(s) THEN
  loops := loops + s.vertex_shell_extent;
END_IF;
IF 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.WIRE_SHELL' IN TYPEOF(s) THEN
  REPEAT i := 1 TO SIZEOF(s.wire_shell_extent);
    loops := loops + s.wire_shell_extent[i];
  END_REPEAT;
END_IF;
IF ('AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.OPEN_SHELL' IN TYPEOF(s)) OR ('AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.CLOSED_SHELL' IN TYPEOF(s)) THEN
  REPEAT i := 1 TO SIZEOF(s.cfs_faces);
    loops := loops + list_face_loops(s.cfs_faces[i]);
  END_REPEAT;
END_IF;
RETURN (loops);

LOCAL
  n : INTEGER;
  res : ARRAY [low:u] OF GENERIC:t;
END_LOCAL;

n := SIZEOF(lis);
IF n <> u - low + 1 THEN
  RETURN (?);
ELSE
  res := [lis[1]:n];
  REPEAT i := 2 TO n;
    res[low + i - 1] := lis[i];
  END_REPEAT;
  RETURN (res);
END_IF;

LOCAL
  s : SET OF GENERIC:t := [];
END_LOCAL;

REPEAT i := 1 TO SIZEOF(l);
  s := s + l[i];
END_REPEAT;
RETURN (s);

LOCAL
  local_def : SET OF product_definition := [];
  local_pdr : SET OF product_definition_relationship := [];
  local_total : SET OF product_definition := [];
END_LOCAL;

REPEAT i := 1 TO HIINDEX(definition_set);
  local_pdr := local_pdr + bag_to_set(USEDIN(definition_set[i], relation_subtype + '.RELATING_PRODUCT_DEFINITION'));
END_REPEAT;
REPEAT i := 1 TO HIINDEX(local_pdr);
  local_def := local_def + local_pdr[i].related_product_definition;
END_REPEAT;
IF SIZEOF(local_def) - SIZEOF(total_definitions) = 0 THEN
  RETURN (local_def);
ELSE
  local_total := total_definitions + local_def;
  RETURN (local_def + local_relatives_of_product_definitions(local_def - total_definitions, local_total, relation_subtype));
END_IF;

LOCAL
  local_shape_rep : SET OF shape_representation := [];
  local_srr : SET OF shape_representation_relationship := [];
  local_total : SET OF shape_representation := [];
END_LOCAL;

REPEAT i := 1 TO HIINDEX(shape_representation_set);
  local_srr := local_srr + QUERY(rr <* bag_to_set(USEDIN(shape_representation_set[i], 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.REPRESENTATION_RELATIONSHIP.REP_1')) | 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.SHAPE_REPRESENTATION_RELATIONSHIP' IN TYPEOF(rr));
END_REPEAT;
REPEAT i := 1 TO HIINDEX(local_srr);
  IF 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.' + 'SHAPE_REPRESENTATION_RELATIONSHIP' IN TYPEOF(local_srr[i]) THEN
    local_shape_rep := local_shape_rep + local_srr[i].rep_2;
  END_IF;
END_REPEAT;
IF SIZEOF(local_shape_rep - total_reps) = 0 THEN
  RETURN (shape_representation_set);
ELSE
  local_total := total_reps + local_shape_rep;
  RETURN (local_shape_rep + local_relatives_of_shape_representations(local_shape_rep - total_reps, local_total));
END_IF;

LOCAL
  res : ARRAY [low1:u1] OF ARRAY [low2:u2] OF GENERIC:t;
END_LOCAL;

IF u1 - low1 + 1 <> SIZEOF(lis) THEN
  RETURN (?);
END_IF;
IF u2 - low2 + 1 <> SIZEOF(lis[1]) THEN
  RETURN (?);
END_IF;
res := [list_to_array(lis[1], low2, u2):u1 - low1 + 1];
REPEAT i := 2 TO HIINDEX(lis);
  IF u2 - low2 + 1 <> SIZEOF(lis[i]) THEN
    RETURN (?);
  END_IF;
  res[low1 + i - 1] := list_to_array(lis[i], low2, u2);
END_REPEAT;
RETURN (res);

LOCAL
  res : ARRAY [low1:u1] OF ARRAY [low2:u2] OF ARRAY [low3:u3] OF GENERIC:t;
END_LOCAL;

IF u1 - low1 + 1 <> SIZEOF(lis) THEN
  RETURN (?);
END_IF;
IF u2 - low2 + 1 <> SIZEOF(lis[1]) THEN
  RETURN (?);
END_IF;
res := [make_array_of_array(lis[1], low2, u2, low3, u3):u1 - low1 + 1];
REPEAT i := 2 TO HIINDEX(lis);
  IF u2 - low2 + 1 <> SIZEOF(lis[i]) THEN
    RETURN (?);
  END_IF;
  res[low1 + i - 1] := make_array_of_array(lis[i], low2, u2, low3, u3);
END_REPEAT;
RETURN (res);

LOCAL
  poly_loop_type : LOGICAL;
END_LOCAL;

IF SIZEOF(l) <= 1 THEN
  RETURN (FALSE);
END_IF;
poly_loop_type := 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.POLY_LOOP' IN TYPEOF(l[1]);
REPEAT i := 2 TO SIZEOF(l);
  IF ('AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.POLY_LOOP' IN TYPEOF(l[i])) <> poly_loop_type THEN
    RETURN (TRUE);
  END_IF;
END_REPEAT;
RETURN (FALSE);

IF SIZEOF(QUERY(msbtype <* TYPEOF(brep) | msbtype LIKE '*BREP_WITH_VOIDS')) >= 1 THEN
  RETURN (brep\brep_with_voids.voids + brep.outer);
ELSE
  RETURN ([brep.outer]);
END_IF;

LOCAL
  ndim : INTEGER;
  v : direction;
  result : vector_or_direction;
  vec : vector;
  mag : REAL;
END_LOCAL;

IF NOT EXISTS(arg) THEN
  result := ?;
ELSE
  ndim := arg.dim;
  IF 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.VECTOR' IN TYPEOF(arg) THEN
    v := dummy_gri||direction(arg.orientation.direction_ratios);
    IF arg.magnitude = 0.000000 THEN
      RETURN (?);
    ELSE
      vec := dummy_gri||vector(v, 1.000000);
    END_IF;
  ELSE
    v := dummy_gri||direction(arg.direction_ratios);
  END_IF;
  mag := 0.000000;
  REPEAT i := 1 TO ndim;
    mag := mag + v.direction_ratios[i] * v.direction_ratios[i];
  END_REPEAT;
  IF mag > 0.000000 THEN
    mag := (mag);
    REPEAT i := 1 TO ndim;
      v.direction_ratios[i] := v.direction_ratios[i] / mag;
    END_REPEAT;
    IF 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.VECTOR' IN TYPEOF(arg) THEN
      vec.orientation := v;
      result := vec;
    ELSE
      result := v;
    END_IF;
  ELSE
    RETURN (?);
  END_IF;
END_IF;
RETURN (result);

LOCAL
  the_reverse : oriented_open_shell;
END_LOCAL;

IF 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.ORIENTED_OPEN_SHELL' IN TYPEOF(a_shell) THEN
  the_reverse := dummy_tri||connected_face_set(a_shell\connected_face_set.cfs_faces)||open_shell()||oriented_open_shell(a_shell\oriented_open_shell.open_shell_element, NOT a_shell\oriented_open_shell.orientation);
ELSE
  the_reverse := dummy_tri||connected_face_set(a_shell\connected_face_set.cfs_faces)||open_shell()||oriented_open_shell(a_shell, FALSE);
END_IF;
RETURN (the_reverse);

LOCAL
  result : direction;
END_LOCAL;

IF (vec.dim <> 2) OR NOT EXISTS(vec) THEN
  RETURN (?);
ELSE
  result := dummy_gri||direction([ - vec.direction_ratios[2], vec.direction_ratios[1]]);
  RETURN (result);
END_IF;

LOCAL
  n : INTEGER;
  p : LOGICAL := TRUE;
END_LOCAL;

n := SIZEOF(a_path.edge_list);
REPEAT i := 2 TO n;
  p := p AND (a_path.edge_list[i - 1].edge_end :=: a_path.edge_list[i].edge_start);
END_REPEAT;
RETURN (p);

LOCAL
  the_reverse : oriented_path;
END_LOCAL;

IF 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.ORIENTED_PATH' IN TYPEOF(a_path) THEN
  the_reverse := dummy_tri||path(list_of_topology_reversed(a_path.edge_list))||oriented_path(a_path\oriented_path.path_element, NOT a_path\oriented_path.orientation);
ELSE
  the_reverse := dummy_tri||path(list_of_topology_reversed(a_path.edge_list))||oriented_path(a_path, FALSE);
END_IF;
RETURN (the_reverse);

LOCAL
  val1 : REAL;
  val2 : REAL;
  n : INTEGER;
END_LOCAL;

IF (plane_pts[1].dim = 2) OR (test_points[1].dim = 2) THEN
  RETURN (?);
END_IF;
n := SIZEOF(test_points);
val1 := above_plane(plane_pts[1], plane_pts[2], plane_pts[3], test_points[1]);
REPEAT i := 2 TO n;
  val2 := above_plane(plane_pts[1], plane_pts[2], plane_pts[3], test_points[i]);
  IF val1 * val2 <= 0.000000 THEN
    RETURN (FALSE);
  END_IF;
END_REPEAT;
RETURN (TRUE);

LOCAL
  v : direction;
  mag : REAL;
  result : vector;
END_LOCAL;

IF NOT EXISTS(scalar) OR NOT EXISTS(vec) THEN
  RETURN (?);
ELSE
  IF 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.VECTOR' IN TYPEOF(vec) THEN
    v := dummy_gri||direction(vec.orientation.direction_ratios);
    mag := scalar * vec.magnitude;
  ELSE
    v := dummy_gri||direction(vec.direction_ratios);
    mag := scalar;
  END_IF;
  IF mag < 0.000000 THEN
    REPEAT i := 1 TO SIZEOF(v.direction_ratios);
      v.direction_ratios[i] :=  - v.direction_ratios[i];
    END_REPEAT;
    mag :=  - mag;
  END_IF;
  result := dummy_gri||vector(normalise(v), mag);
END_IF;
RETURN (result);

LOCAL
  y_axis : vector;
  v : direction;
  temp : vector;
END_LOCAL;

IF NOT EXISTS(arg) THEN
  v := dummy_gri||direction([0.000000, 1.000000, 0.000000]);
ELSE
  v := arg;
END_IF;
temp := scalar_times_vector(dot_product(v, z_axis), z_axis);
y_axis := vector_difference(v, temp);
temp := scalar_times_vector(dot_product(v, x_axis), x_axis);
y_axis := vector_difference(y_axis, temp);
y_axis := normalise(y_axis);
RETURN (y_axis.orientation);

LOCAL
  the_reverse : set_of_reversible_topology_item;
END_LOCAL;

the_reverse := [];
REPEAT i := 1 TO SIZEOF(a_set);
  the_reverse := the_reverse + topology_reversed(a_set[i]);
END_REPEAT;
RETURN (the_reverse);

IF 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.OPEN_SHELL' IN TYPEOF(a_shell) THEN
  RETURN (open_shell_reversed(a_shell));
ELSE
  IF 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.CLOSED_SHELL' IN TYPEOF(a_shell) THEN
    RETURN (closed_shell_reversed(a_shell));
  ELSE
    RETURN (?);
  END_IF;
END_IF;

LOCAL
  result : BOOLEAN := TRUE;
END_LOCAL;

REPEAT i := 0 TO b.u_upper;
  REPEAT j := 0 TO b.v_upper;
    IF b.weights[i][j] <= 0.000000 THEN
      result := FALSE;
      RETURN (result);
    END_IF;
  END_REPEAT;
END_REPEAT;
RETURN (result);

IF 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.EDGE' IN TYPEOF(an_item) THEN
  RETURN (edge_reversed(an_item));
END_IF;
IF 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.PATH' IN TYPEOF(an_item) THEN
  RETURN (path_reversed(an_item));
END_IF;
IF 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.FACE_BOUND' IN TYPEOF(an_item) THEN
  RETURN (face_bound_reversed(an_item));
END_IF;
IF 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.FACE' IN TYPEOF(an_item) THEN
  RETURN (face_reversed(an_item));
END_IF;
IF 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.SHELL' IN TYPEOF(an_item) THEN
  RETURN;
END_IF;
IF 'SET' IN TYPEOF(an_item) THEN
  RETURN (set_of_topology_reversed(an_item));
END_IF;
IF 'LIST' IN TYPEOF(an_item) THEN
  RETURN (list_of_topology_reversed(an_item));
END_IF;
RETURN (?);

IF NOT EXISTS(the_type) OR NOT ({0 <= criterion <= 3}) OR (SIZEOF(sub_names) = 0) THEN
  RETURN (UNKNOWN);
ELSE
  CASE criterion OF 
    0:
      RETURN (SIZEOF(sub_names * TYPEOF(the_type)) > 0);
    1:
      RETURN (SIZEOF(sub_names * TYPEOF(the_type)) = 0);
    2:
      RETURN (SIZEOF(sub_names * TYPEOF(the_type)) = 1);
    3:
      RETURN (SIZEOF(sub_names * TYPEOF(the_type)) <= 1);
  END_CASE;
END_IF;

LOCAL
  new_check_items : SET OF founded_item_select;
  result_items : SET OF founded_item_select;
  next_items : SET OF founded_item_select;
END_LOCAL;

result_items := [];
new_check_items := checked_items + item;
next_items := QUERY(z <* bag_to_set(USEDIN(item, '')) | ('AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.REPRESENTATION_ITEM' IN TYPEOF(z)) OR ('AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.FOUNDED_ITEM' IN TYPEOF(z)));
IF SIZEOF(next_items) > 0 THEN
  REPEAT i := 1 TO HIINDEX(next_items);
    IF NOT (next_items[i] IN new_check_items) THEN
      result_items := result_items + next_items[i] + using_items(next_items[i], new_check_items);
    END_IF;
  END_REPEAT;
END_IF;
RETURN (result_items);

LOCAL
  results : SET OF representation;
  result_bag : BAG OF representation;
  intermediate_items : SET OF founded_item_select;
END_LOCAL;

results := [];
result_bag := USEDIN(item, 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.REPRESENTATION.ITEMS');
IF SIZEOF(result_bag) > 0 THEN
  REPEAT i := 1 TO HIINDEX(result_bag);
    results := results + result_bag[i];
  END_REPEAT;
END_IF;
intermediate_items := using_items(item, []);
IF SIZEOF(intermediate_items) > 0 THEN
  REPEAT i := 1 TO HIINDEX(intermediate_items);
    result_bag := USEDIN(intermediate_items[i], 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.REPRESENTATION.ITEMS');
    IF SIZEOF(result_bag) > 0 THEN
      REPEAT j := 1 TO HIINDEX(result_bag);
        results := results + result_bag[j];
      END_REPEAT;
    END_IF;
  END_REPEAT;
END_IF;
RETURN (results);

CASE date.month_component OF 
  1:
    RETURN ({1 <= date.day_component <= 31});
  2:
    BEGIN
      IF leap_year(date.year_component) THEN
        RETURN ({1 <= date.day_component <= 29});
      ELSE
        RETURN ({1 <= date.day_component <= 28});
      END_IF;
    END;
  3:
    RETURN ({1 <= date.day_component <= 31});
  4:
    RETURN ({1 <= date.day_component <= 30});
  5:
    RETURN ({1 <= date.day_component <= 31});
  6:
    RETURN ({1 <= date.day_component <= 30});
  7:
    RETURN ({1 <= date.day_component <= 31});
  8:
    RETURN ({1 <= date.day_component <= 31});
  9:
    RETURN ({1 <= date.day_component <= 30});
  10:
    RETURN ({1 <= date.day_component <= 31});
  11:
    RETURN ({1 <= date.day_component <= 30});
  12:
    RETURN ({1 <= date.day_component <= 31});
END_CASE;
RETURN (FALSE);

