LOCAL
  result : vector;
  res : direction;
  vec1 : direction;
  vec2 : direction;
  mag : REAL;
  mag1 : REAL;
  mag2 : REAL;
  ndim : INTEGER;
END_LOCAL;

IF NOT EXISTS(arg1) OR NOT EXISTS(arg2) OR (arg1.dim <> arg2.dim) THEN
  RETURN (?);
ELSE
  IF 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.VECTOR' IN TYPEOF(arg1) THEN
    mag1 := arg1.magnitude;
    vec1 := arg1.orientation;
  ELSE
    mag1 := 1.000000;
    vec1 := arg1;
  END_IF;
  IF 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.VECTOR' IN TYPEOF(arg2) THEN
    mag2 := arg2.magnitude;
    vec2 := arg2.orientation;
  ELSE
    mag2 := 1.000000;
    vec2 := arg2;
  END_IF;
  vec1 := normalise(vec1);
  vec2 := normalise(vec2);
  ndim := SIZEOF(vec1.direction_ratios);
  mag := 0.000000;
  res := dummy_gri||direction(vec1.direction_ratios);
  REPEAT i := 1 TO ndim;
    res.direction_ratios[i] := mag1 * vec1.direction_ratios[i] + mag2 * vec2.direction_ratios[i];
    mag := mag + res.direction_ratios[i] * res.direction_ratios[i];
  END_REPEAT;
  IF mag > 0.000000 THEN
    result := dummy_gri||vector(res, (mag));
  ELSE
    result := dummy_gri||vector(vec1, 0.000000);
  END_IF;
END_IF;
RETURN (result);

LOCAL
  result : vector;
  res : direction;
  vec1 : direction;
  vec2 : direction;
  mag : REAL;
  mag1 : REAL;
  mag2 : REAL;
  ndim : INTEGER;
END_LOCAL;

IF NOT EXISTS(arg1) OR NOT EXISTS(arg2) OR (arg1.dim <> arg2.dim) THEN
  RETURN (?);
ELSE
  IF 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.VECTOR' IN TYPEOF(arg1) THEN
    mag1 := arg1.magnitude;
    vec1 := arg1.orientation;
  ELSE
    mag1 := 1.000000;
    vec1 := arg1;
  END_IF;
  IF 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.VECTOR' IN TYPEOF(arg2) THEN
    mag2 := arg2.magnitude;
    vec2 := arg2.orientation;
  ELSE
    mag2 := 1.000000;
    vec2 := arg2;
  END_IF;
  vec1 := normalise(vec1);
  vec2 := normalise(vec2);
  ndim := SIZEOF(vec1.direction_ratios);
  mag := 0.000000;
  res := dummy_gri||direction(vec1.direction_ratios);
  REPEAT i := 1 TO ndim;
    res.direction_ratios[i] := mag1 * vec1.direction_ratios[i] + mag2 * vec2.direction_ratios[i];
    mag := mag + res.direction_ratios[i] * res.direction_ratios[i];
  END_REPEAT;
  IF mag > 0.000000 THEN
    result := dummy_gri||vector(res, (mag));
  ELSE
    result := dummy_gri||vector(vec1, 0.000000);
  END_IF;
END_IF;
RETURN (result);

LOCAL
  item : identification_item;
  role : identification_role;
END_LOCAL;

role := aia.role;
IF role.name = 'version' THEN
  REPEAT i := LOINDEX(aia.items) TO HIINDEX(aia.items);
    item := aia.items[i];
    IF SIZEOF(['AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.' + 'VERSIONABLE_ITEM'] * TYPEOF(item)) = 0 THEN
      RETURN (FALSE);
    END_IF;
  END_REPEAT;
  RETURN (TRUE);
ELSE
  RETURN (TRUE);
END_IF;

LOCAL
  a_point : point;
  result : SET OF degenerate_pcurve;
END_LOCAL;

a_point := a_vertex.vertex_geometry;
result := [];
IF 'AP239_PRODUCT_LIFE_CYCLE_SUPPORT_MIM_LF.DEGENERATE_PCURVE' IN TYPEOF(a_point) THEN
  result := result + a_point;
ELSE
  REPEAT j := 1 TO SIZEOF(the_degenerates);
    IF the_degenerates[j].equivalent_point :=: a_point THEN
      result := result + the_degenerates[j];
    END_IF;
  END_REPEAT;
END_IF;
RETURN (result);

LOCAL
  result : BOOLEAN := TRUE;
END_LOCAL;

REPEAT i := 0 TO b.u_upper;
  REPEAT j := 0 TO b.v_upper;
    REPEAT k := 0 TO b.w_upper;
      IF b.weights[i][j][k] <= 0.000000 THEN
        result := FALSE;
        RETURN (result);
      END_IF;
    END_REPEAT;
  END_REPEAT;
END_REPEAT;
RETURN (result);
