issue.description:
Usage guide contains the following undocumented express. Remove these. Add a proper usage guide when available.
RULE compatible_dimension FOR (cartesian_point, direction, representation_context, geometric_representation_context);
WHERE wr1: (SIZEOF(QUERY ( x <* cartesian_point | (SIZEOF(QUERY ( y <* geometric_representation_context | (item_in_context(x,y) AND ( HIINDEX(x.coordinates) <> y.coordinate_space_dimension)) ))> 0) )) = 0); wr2: (SIZEOF(QUERY ( x <* direction | (SIZEOF(QUERY ( y <* geometric_representation_context | (item_in_context(x,y) AND ( HIINDEX(x.direction_ratios) <> y.coordinate_space_dimension)) )) > 0) )) = 0);
END_RULE; -- compatible_dimension
RULE dimensionality_is_two_or_three FOR ( geometric_representation_context);
WHERE wr1: (SIZEOF(QUERY ( g <* geometric_representation_context | (NOT ((g. coordinate_space_dimension = 2) OR (g. coordinate_space_dimension = 3))) )) = 0);
END_RULE; -- dimensionality_is_two_or_three
RULE geometric_representation_item_3d FOR (geometric_representation_item);
WHERE wr1: (SIZEOF(QUERY ( gri <* geometric_representation_item | (NOT (( dimension_of(gri) = 3) OR (SIZEOF(bag_to_set(USEDIN(gri,'')) - bag_to_set(USEDIN(gri, 'CCD_CLA_GVP_AST_ASD.DEFINITIONAL_REPRESENTATION.ITEMS'))) = 0))) )) = 0);
END_RULE; -- geometric_representation_item_3d
RULE no_shape_for_make_from FOR (design_make_from_relationship);
WHERE wr1: (SIZEOF(QUERY ( dmfr <* design_make_from_relationship | (NOT ( SIZEOF(QUERY ( pd <* USEDIN(dmfr,'CCD_CLA_GVP_AST_ASD.' + 'PROPERTY_DEFINITION.DEFINITION') | ( 'CCD_CLA_GVP_AST_ASD.PRODUCT_DEFINITION_SHAPE' IN TYPEOF(pd)) )) = 0)) )) = 0);
END_RULE; -- no_shape_for_make_from
RULE no_shape_for_supplied_part FOR (supplied_part_relationship);
WHERE wr1: (SIZEOF(QUERY ( spr <* supplied_part_relationship | (NOT (SIZEOF( QUERY ( pd <* USEDIN(spr,'CCD_CLA_GVP_AST_ASD.' + 'PROPERTY_DEFINITION.DEFINITION') | ( 'CCD_CLA_GVP_AST_ASD.PRODUCT_DEFINITION_SHAPE' IN TYPEOF(pd)) )) = 0)) )) = 0);
END_RULE; -- no_shape_for_supplied_part
