function.algorithm:
LOCAL
  x : SET OF representation_item;
  y : SET OF representation_item;
END_LOCAL;
x := QUERY ( z <* children_set | (
    'ELEMENTAL_GEOMETRIC_SHAPE_MIM.MAPPED_ITEM' IN TYPEOF(z)) );
IF SIZEOF(x) > 0 THEN
  REPEAT i := 1 TO HIINDEX(x) BY 1;
    IF x[i]\mapped_item.mapping_source.mapped_representation IN 
        parent_set THEN
      RETURN(FALSE);
    END_IF;
    IF NOT acyclic_mapped_representation(parent_set + x[i]\mapped_item
        .mapping_source.mapped_representation,x[i]\mapped_item.
        mapping_source.mapped_representation.items) THEN
      RETURN(FALSE);
    END_IF;
  END_REPEAT;
END_IF;
x := children_set - x;
IF SIZEOF(x) > 0 THEN
  REPEAT i := 1 TO HIINDEX(x) BY 1;
    y := QUERY ( z <* bag_to_set(USEDIN(x[i],'')) | (
        'ELEMENTAL_GEOMETRIC_SHAPE_MIM.REPRESENTATION_ITEM' IN TYPEOF(z)) );
    IF NOT acyclic_mapped_representation(parent_set,y) THEN
      RETURN(FALSE);
    END_IF;
  END_REPEAT;
END_IF;
RETURN(TRUE);

LOCAL
  the_set : SET OF GENERIC:intype := [];
END_LOCAL;
IF SIZEOF(the_bag) > 0 THEN
  REPEAT i := 1 TO HIINDEX(the_bag) BY 1;
    the_set := the_set + the_bag[i];
  END_REPEAT;
END_IF;
RETURN(the_set);

LOCAL
  u      : LIST [2:3] OF direction;
  d1     : direction;
  d2     : direction;
  factor : REAL;
END_LOCAL;
IF dim = 3 THEN
  d1 := NVL(normalise(axis3),dummy_gri || direction([0,0,1]));
  d2 := first_proj_axis(d1,axis1);
  u := [d2,second_proj_axis(d1,d2,axis2),d1];
ELSE
  IF EXISTS(axis1) THEN
    d1 := normalise(axis1);
    u := [d1,orthogonal_complement(d1)];
    IF EXISTS(axis2) THEN
      factor := dot_product(axis2,u[2]);
      IF factor < 0 THEN
        u[2].direction_ratios[1] := -u[2].direction_ratios[1];
        u[2].direction_ratios[2] := -u[2].direction_ratios[2];
      END_IF;
    END_IF;
  ELSE
    IF EXISTS(axis2) THEN
      d1 := normalise(axis2);
      u := [orthogonal_complement(d1),d1];
      u[1].direction_ratios[1] := -u[1].direction_ratios[1];
      u[1].direction_ratios[2] := -u[1].direction_ratios[2];
    ELSE
      u := [dummy_gri || direction([1,0]),dummy_gri || direction([0,1])];
    END_IF;
  END_IF;
END_IF;
RETURN(u);

LOCAL
  d : direction := NVL(normalise(ref_direction),dummy_gri || 
       direction([1,0]));
END_LOCAL;
RETURN([d,orthogonal_complement(d)]);

LOCAL
  d1 : direction;
  d2 : direction;
END_LOCAL;
d1 := NVL(normalise(axis),dummy_gri || direction([0,0,1]));
d2 := first_proj_axis(d1,ref_direction);
RETURN([d2,normalise(cross_product(d1,d2)).orientation,d1]);

LOCAL
  v2     : LIST [3:3] OF REAL;
  v1     : LIST [3:3] OF REAL;
  mag    : REAL;
  res    : direction;
  result : vector;
END_LOCAL;
IF (NOT EXISTS(arg1)) OR (arg1.dim = 2) OR (NOT EXISTS(arg2)) OR (arg2
    .dim = 2) THEN
  RETURN(?);
ELSE
  BEGIN
    v1 := normalise(arg1).direction_ratios;
    v2 := normalise(arg2).direction_ratios;
    res := dummy_gri || direction([(v1[2] * v2[3]) - (v1[3] * v2[2]),(
        v1[3] * v2[1]) - (v1[1] * v2[3]),(v1[1] * v2[2]) - (v1[2] * v2[
        1])]);
    mag := 0;
    REPEAT i := 1 TO 3 BY 1;
      mag := mag + (res.direction_ratios[i] * res.direction_ratios[i]);
    END_REPEAT;
    IF mag > 0 THEN
      result := dummy_gri || vector(res,SQRT(mag));
    ELSE
      result := dummy_gri || vector(arg1,0);
    END_IF;
    RETURN(result);
  END;
END_IF;

LOCAL
  result : dimensional_exponents := dimensional_exponents(0,0,0,0,0,0,
            0);
END_LOCAL;
IF 'ELEMENTAL_GEOMETRIC_SHAPE_MIM.DERIVED_UNIT' IN TYPEOF(x) THEN
  REPEAT i := LOINDEX(x.elements) TO HIINDEX(x.elements) BY 1;
    result.length_exponent := result.length_exponent + (x.elements[i].
        exponent * x.elements[i].unit.dimensions.length_exponent);
    result.mass_exponent := result.mass_exponent + (x.elements[i].
        exponent * x.elements[i].unit.dimensions.mass_exponent);
    result.time_exponent := result.time_exponent + (x.elements[i].
        exponent * x.elements[i].unit.dimensions.time_exponent);
    result.electric_current_exponent := result.
        electric_current_exponent + (x.elements[i].exponent * x.
        elements[i].unit.dimensions.electric_current_exponent);
    result.thermodynamic_temperature_exponent := result.
        thermodynamic_temperature_exponent + (x.elements[i].exponent * 
        x.elements[i].unit.dimensions.
        thermodynamic_temperature_exponent);
    result.amount_of_substance_exponent := result.
        amount_of_substance_exponent + (x.elements[i].exponent * x.
        elements[i].unit.dimensions.amount_of_substance_exponent);
    result.luminous_intensity_exponent := result.
        luminous_intensity_exponent + (x.elements[i].exponent * x.
        elements[i].unit.dimensions.luminous_intensity_exponent);
  END_REPEAT;
ELSE
  result := x.dimensions;
END_IF;
RETURN(result);

LOCAL
  x : SET OF representation;
  y : representation_context;
END_LOCAL;
x := using_representations(item);
y := x[1].context_of_items;
RETURN(y\geometric_representation_context.coordinate_space_dimension);

LOCAL
  ndim   : INTEGER;
  scalar : REAL;
  vec1   : direction;
  vec2   : direction;
END_LOCAL;
IF (NOT EXISTS(arg1)) OR (NOT EXISTS(arg2)) THEN
  scalar := ?;
ELSE
  IF arg1.dim <> arg2.dim THEN
    scalar := ?;
  ELSE
    BEGIN
      vec1 := normalise(arg1);
      vec2 := normalise(arg2);
      ndim := arg1.dim;
      scalar := 0;
      REPEAT i := 1 TO ndim BY 1;
        scalar := scalar + (vec1.direction_ratios[i] * vec2.
            direction_ratios[i]);
      END_REPEAT;
    END;
  END_IF;
END_IF;
RETURN(scalar);

LOCAL
  x_vec  : vector;
  v      : direction;
  z      : direction;
  x_axis : direction;
END_LOCAL;
IF NOT EXISTS(z_axis) THEN
  RETURN(?);
ELSE
  z := normalise(z_axis);
  IF NOT EXISTS(arg) THEN
    IF z.direction_ratios <> [1,0,0] THEN
      v := dummy_gri || direction([1,0,0]);
    ELSE
      v := dummy_gri || direction([0,1,0]);
    END_IF;
  ELSE
    IF arg.dim <> 3 THEN
      RETURN(?);
    END_IF;
    IF cross_product(arg,z).magnitude = 0 THEN
      RETURN(?);
    ELSE
      v := normalise(arg);
    END_IF;
  END_IF;
  x_vec := scalar_times_vector(dot_product(v,z),z);
  x_axis := vector_difference(v,x_vec).orientation;
  x_axis := normalise(x_axis);
END_IF;
RETURN(x_axis);

LOCAL
  description_bag : BAG OF description_attribute := USEDIN(obj,
                     'ELEMENTAL_GEOMETRIC_SHAPE_MIM.' + 
                     'DESCRIPTION_ATTRIBUTE.' + 'DESCRIBED_ITEM');
END_LOCAL;
IF SIZEOF(description_bag) = 1 THEN
  RETURN(description_bag[1].attribute_value);
ELSE
  RETURN(?);
END_IF;

LOCAL
  id_bag : BAG OF id_attribute := USEDIN(obj,
            'ELEMENTAL_GEOMETRIC_SHAPE_MIM.' + 'ID_ATTRIBUTE.' + 
            'IDENTIFIED_ITEM');
END_LOCAL;
IF SIZEOF(id_bag) = 1 THEN
  RETURN(id_bag[1].attribute_value);
ELSE
  RETURN(?);
END_IF;

LOCAL
  name_bag : BAG OF name_attribute := USEDIN(obj,
              'ELEMENTAL_GEOMETRIC_SHAPE_MIM.' + 'NAME_ATTRIBUTE.' + 
              'NAMED_ITEM');
END_LOCAL;
IF SIZEOF(name_bag) = 1 THEN
  RETURN(name_bag[1].attribute_value);
ELSE
  RETURN(?);
END_IF;

LOCAL
  y : BAG OF representation_item;
END_LOCAL;
IF SIZEOF(USEDIN(item,
    'ELEMENTAL_GEOMETRIC_SHAPE_MIM.REPRESENTATION.ITEMS') * cntxt.
    representations_in_context) > 0 THEN
  RETURN(TRUE);
ELSE
  y := QUERY ( z <* USEDIN(item,'') | (
      'ELEMENTAL_GEOMETRIC_SHAPE_MIM.REPRESENTATION_ITEM' IN TYPEOF(z)) );
  IF SIZEOF(y) > 0 THEN
    REPEAT i := 1 TO HIINDEX(y) BY 1;
      IF item_in_context(y[i],cntxt) THEN
        RETURN(TRUE);
      END_IF;
    END_REPEAT;
  END_IF;
END_IF;
RETURN(FALSE);

LOCAL
  ndim   : INTEGER;
  v      : direction;
  vec    : vector;
  mag    : REAL;
  result : vector_or_direction;
END_LOCAL;
IF NOT EXISTS(arg) THEN
  result := ?;
ELSE
  ndim := arg.dim;
  IF 'ELEMENTAL_GEOMETRIC_SHAPE_MIM.VECTOR' IN TYPEOF(arg) THEN
    BEGIN
      v := dummy_gri || direction(arg.orientation.direction_ratios);
      IF arg.magnitude = 0 THEN
        RETURN(?);
      ELSE
        vec := dummy_gri || vector(v,1);
      END_IF;
    END;
  ELSE
    v := dummy_gri || direction(arg.direction_ratios);
  END_IF;
  mag := 0;
  REPEAT i := 1 TO ndim BY 1;
    mag := mag + (v.direction_ratios[i] * v.direction_ratios[i]);
  END_REPEAT;
  IF mag > 0 THEN
    mag := SQRT(mag);
    REPEAT i := 1 TO ndim BY 1;
      v.direction_ratios[i] := v.direction_ratios[i] / mag;
    END_REPEAT;
    IF 'ELEMENTAL_GEOMETRIC_SHAPE_MIM.VECTOR' IN TYPEOF(arg) THEN
      vec.orientation := v;
      result := vec;
    ELSE
      result := v;
    END_IF;
  ELSE
    RETURN(?);
  END_IF;
END_IF;
RETURN(result);

LOCAL
  result : direction;
END_LOCAL;
IF (vec.dim <> 2) OR (NOT EXISTS(vec)) THEN
  RETURN(?);
ELSE
  result := dummy_gri || direction([-vec.direction_ratios[2],vec.
      direction_ratios[1]]);
  RETURN(result);
END_IF;

LOCAL
  v      : direction;
  mag    : REAL;
  result : vector;
END_LOCAL;
IF (NOT EXISTS(scalar)) OR (NOT EXISTS(vec)) THEN
  RETURN(?);
ELSE
  IF 'ELEMENTAL_GEOMETRIC_SHAPE_MIM.VECTOR' IN TYPEOF(vec) THEN
    v := dummy_gri || direction(vec.orientation.direction_ratios);
    mag := scalar * vec.magnitude;
  ELSE
    v := dummy_gri || direction(vec.direction_ratios);
    mag := scalar;
  END_IF;
  IF mag < 0 THEN
    REPEAT i := 1 TO SIZEOF(v.direction_ratios) BY 1;
      v.direction_ratios[i] := -v.direction_ratios[i];
    END_REPEAT;
    mag := -mag;
  END_IF;
  result := dummy_gri || vector(normalise(v),mag);
END_IF;
RETURN(result);

LOCAL
  temp   : vector;
  v      : direction;
  y_axis : vector;
END_LOCAL;
IF NOT EXISTS(arg) THEN
  v := dummy_gri || direction([0,1,0]);
ELSE
  v := arg;
END_IF;
temp := scalar_times_vector(dot_product(v,z_axis),z_axis);
y_axis := vector_difference(v,temp);
temp := scalar_times_vector(dot_product(v,x_axis),x_axis);
y_axis := vector_difference(y_axis,temp);
y_axis := normalise(y_axis);
RETURN(y_axis.orientation);

LOCAL
  next_items      : SET OF founded_item_select;
  new_check_items : SET OF founded_item_select;
  result_items    : SET OF founded_item_select;
END_LOCAL;
result_items := [];
new_check_items := checked_items + item;
next_items := QUERY ( z <* bag_to_set(USEDIN(item,'')) | ((
    'ELEMENTAL_GEOMETRIC_SHAPE_MIM.REPRESENTATION_ITEM' IN TYPEOF(z)) 
    OR ('ELEMENTAL_GEOMETRIC_SHAPE_MIM.FOUNDED_ITEM' IN TYPEOF(z))) );
IF SIZEOF(next_items) > 0 THEN
  REPEAT i := 1 TO HIINDEX(next_items) BY 1;
    IF NOT (next_items[i] IN new_check_items) THEN
      result_items := result_items + next_items[i] + using_items(
          next_items[i],new_check_items);
    END_IF;
  END_REPEAT;
END_IF;
RETURN(result_items);

LOCAL
  results            : SET OF representation;
  intermediate_items : SET OF founded_item_select;
  result_bag         : BAG OF representation;
END_LOCAL;
results := [];
result_bag := USEDIN(item,
    'ELEMENTAL_GEOMETRIC_SHAPE_MIM.REPRESENTATION.ITEMS');
IF SIZEOF(result_bag) > 0 THEN
  REPEAT i := 1 TO HIINDEX(result_bag) BY 1;
    results := results + result_bag[i];
  END_REPEAT;
END_IF;
intermediate_items := using_items(item,[]);
IF SIZEOF(intermediate_items) > 0 THEN
  REPEAT i := 1 TO HIINDEX(intermediate_items) BY 1;
    result_bag := USEDIN(intermediate_items[i],
        'ELEMENTAL_GEOMETRIC_SHAPE_MIM.REPRESENTATION.ITEMS');
    IF SIZEOF(result_bag) > 0 THEN
      REPEAT j := 1 TO HIINDEX(result_bag) BY 1;
        results := results + result_bag[j];
      END_REPEAT;
    END_IF;
  END_REPEAT;
END_IF;
RETURN(results);

IF 'REAL' IN TYPEOF(m) THEN
  RETURN(m > 0);
ELSE
  IF 'INTEGER' IN TYPEOF(m) THEN
    RETURN(m > 0);
  ELSE
    RETURN(TRUE);
  END_IF;
END_IF;

IF 'ELEMENTAL_GEOMETRIC_SHAPE_MIM.LENGTH_MEASURE' IN TYPEOF(m.
    value_component) THEN
  IF derive_dimensional_exponents(m.unit_component) <> 
      dimensional_exponents(1,0,0,0,0,0,0) THEN
    RETURN(FALSE);
  END_IF;
END_IF;
IF 'ELEMENTAL_GEOMETRIC_SHAPE_MIM.MASS_MEASURE' IN TYPEOF(m.
    value_component) THEN
  IF derive_dimensional_exponents(m.unit_component) <> 
      dimensional_exponents(0,1,0,0,0,0,0) THEN
    RETURN(FALSE);
  END_IF;
END_IF;
IF 'ELEMENTAL_GEOMETRIC_SHAPE_MIM.TIME_MEASURE' IN TYPEOF(m.
    value_component) THEN
  IF derive_dimensional_exponents(m.unit_component) <> 
      dimensional_exponents(0,0,1,0,0,0,0) THEN
    RETURN(FALSE);
  END_IF;
END_IF;
IF 'ELEMENTAL_GEOMETRIC_SHAPE_MIM.ELECTRIC_CURRENT_MEASURE' IN TYPEOF(
    m.value_component) THEN
  IF derive_dimensional_exponents(m.unit_component) <> 
      dimensional_exponents(0,0,0,1,0,0,0) THEN
    RETURN(FALSE);
  END_IF;
END_IF;
IF 'ELEMENTAL_GEOMETRIC_SHAPE_MIM.THERMODYNAMIC_TEMPERATURE_MEASURE' 
    IN TYPEOF(m.value_component) THEN
  IF derive_dimensional_exponents(m.unit_component) <> 
      dimensional_exponents(0,0,0,0,1,0,0) THEN
    RETURN(FALSE);
  END_IF;
END_IF;
IF 'ELEMENTAL_GEOMETRIC_SHAPE_MIM.AMOUNT_OF_SUBSTANCE_MEASURE' IN 
    TYPEOF(m.value_component) THEN
  IF derive_dimensional_exponents(m.unit_component) <> 
      dimensional_exponents(0,0,0,0,0,1,0) THEN
    RETURN(FALSE);
  END_IF;
END_IF;
IF 'ELEMENTAL_GEOMETRIC_SHAPE_MIM.LUMINOUS_INTENSITY_MEASURE' IN 
    TYPEOF(m.value_component) THEN
  IF derive_dimensional_exponents(m.unit_component) <> 
      dimensional_exponents(0,0,0,0,0,0,1) THEN
    RETURN(FALSE);
  END_IF;
END_IF;
IF 'ELEMENTAL_GEOMETRIC_SHAPE_MIM.PLANE_ANGLE_MEASURE' IN TYPEOF(m.
    value_component) THEN
  IF derive_dimensional_exponents(m.unit_component) <> 
      dimensional_exponents(0,0,0,0,0,0,0) THEN
    RETURN(FALSE);
  END_IF;
END_IF;
IF 'ELEMENTAL_GEOMETRIC_SHAPE_MIM.SOLID_ANGLE_MEASURE' IN TYPEOF(m.
    value_component) THEN
  IF derive_dimensional_exponents(m.unit_component) <> 
      dimensional_exponents(0,0,0,0,0,0,0) THEN
    RETURN(FALSE);
  END_IF;
END_IF;
IF 'ELEMENTAL_GEOMETRIC_SHAPE_MIM.AREA_MEASURE' IN TYPEOF(m.
    value_component) THEN
  IF derive_dimensional_exponents(m.unit_component) <> 
      dimensional_exponents(2,0,0,0,0,0,0) THEN
    RETURN(FALSE);
  END_IF;
END_IF;
IF 'ELEMENTAL_GEOMETRIC_SHAPE_MIM.VOLUME_MEASURE' IN TYPEOF(m.
    value_component) THEN
  IF derive_dimensional_exponents(m.unit_component) <> 
      dimensional_exponents(3,0,0,0,0,0,0) THEN
    RETURN(FALSE);
  END_IF;
END_IF;
IF 'ELEMENTAL_GEOMETRIC_SHAPE_MIM.RATIO_MEASURE' IN TYPEOF(m.
    value_component) THEN
  IF derive_dimensional_exponents(m.unit_component) <> 
      dimensional_exponents(0,0,0,0,0,0,0) THEN
    RETURN(FALSE);
  END_IF;
END_IF;
IF 'ELEMENTAL_GEOMETRIC_SHAPE_MIM.POSITIVE_LENGTH_MEASURE' IN TYPEOF(m
    .value_component) THEN
  IF derive_dimensional_exponents(m.unit_component) <> 
      dimensional_exponents(1,0,0,0,0,0,0) THEN
    RETURN(FALSE);
  END_IF;
END_IF;
IF 'ELEMENTAL_GEOMETRIC_SHAPE_MIM.POSITIVE_PLANE_ANGLE_MEASURE' IN 
    TYPEOF(m.value_component) THEN
  IF derive_dimensional_exponents(m.unit_component) <> 
      dimensional_exponents(0,0,0,0,0,0,0) THEN
    RETURN(FALSE);
  END_IF;
END_IF;
RETURN(TRUE);

LOCAL
  ndim   : INTEGER;
  mag2   : REAL;
  mag1   : REAL;
  mag    : REAL;
  res    : direction;
  vec1   : direction;
  vec2   : direction;
  result : vector;
END_LOCAL;
IF (NOT EXISTS(arg1)) OR (NOT EXISTS(arg2)) OR (arg1.dim <> arg2.dim)
     THEN
  RETURN(?);
ELSE
  BEGIN
    IF 'ELEMENTAL_GEOMETRIC_SHAPE_MIM.VECTOR' IN TYPEOF(arg1) THEN
      mag1 := arg1.magnitude;
      vec1 := arg1.orientation;
    ELSE
      mag1 := 1;
      vec1 := arg1;
    END_IF;
    IF 'ELEMENTAL_GEOMETRIC_SHAPE_MIM.VECTOR' IN TYPEOF(arg2) THEN
      mag2 := arg2.magnitude;
      vec2 := arg2.orientation;
    ELSE
      mag2 := 1;
      vec2 := arg2;
    END_IF;
    vec1 := normalise(vec1);
    vec2 := normalise(vec2);
    ndim := SIZEOF(vec1.direction_ratios);
    mag := 0;
    res := dummy_gri || direction(vec1.direction_ratios);
    REPEAT i := 1 TO ndim BY 1;
      res.direction_ratios[i] := (mag1 * vec1.direction_ratios[i]) + (
          mag2 * vec2.direction_ratios[i]);
      mag := mag + (res.direction_ratios[i] * res.direction_ratios[i]);
    END_REPEAT;
    IF mag > 0 THEN
      result := dummy_gri || vector(res,SQRT(mag));
    ELSE
      result := dummy_gri || vector(vec1,0);
    END_IF;
  END;
END_IF;
RETURN(result);
