function.algorithm:
LOCAL
	i: INTEGER;
END_LOCAL;

IF 'Expression_arm_EXPRESSIONS_SCHEMA.Int_literal' IN TYPEOF(arg) 
THEN 
	RETURN (TRUE); 
END_IF;
IF 'Expression_arm_EXPRESSIONS_SCHEMA.Real_literal' IN TYPEOF(arg) 
THEN 
	RETURN (FALSE); 
END_IF;
IF 'Expression_arm_EXPRESSIONS_SCHEMA.INT_NUMERIC_VARIABLE' IN TYPEOF(arg) 
THEN 
	RETURN (TRUE); 
END_IF;
IF 'Expression_arm_EXPRESSIONS_SCHEMA.REAL_NUMERIC_VARIABLE' IN TYPEOF(arg) 
THEN 
	RETURN (FALSE); 
END_IF;
IF 'Expression_arm_EXPRESSIONS_SCHEMA.ABS_FUNCTION' IN TYPEOF(arg) 
THEN 
	RETURN (Is_int_expr(arg\Unary_numeric_expression.operand));
END_IF;
IF 'Expression_arm_EXPRESSIONS_SCHEMA.MINUS_FUNCTION' IN TYPEOF(arg) 
THEN 
	RETURN (Is_int_expr(arg\Unary_numeric_expression.operand)); 
END_IF;
IF ('Expression_arm_EXPRESSIONS_SCHEMA.SIN_FUNCTION' IN TYPEOF(arg)) 
		OR ('Expression_arm_EXPRESSIONS_SCHEMA.COS_FUNCTION' IN TYPEOF(arg))
		OR ('Expression_arm_EXPRESSIONS_SCHEMA.TAN_FUNCTION' IN TYPEOF(arg))
		OR ('Expression_arm_EXPRESSIONS_SCHEMA.ASIN_FUNCTION' IN TYPEOF(arg))
		OR ('Expression_arm_EXPRESSIONS_SCHEMA.ACOS_FUNCTION' IN TYPEOF(arg))
		OR ('Expression_arm_EXPRESSIONS_SCHEMA.Atan_function' IN TYPEOF(arg))
		OR ('Expression_arm_EXPRESSIONS_SCHEMA.EXP_FUNCTION' IN TYPEOF(arg))
		OR ('Expression_arm_EXPRESSIONS_SCHEMA.LOG_FUNCTION' IN TYPEOF(arg))
		OR ('Expression_arm_EXPRESSIONS_SCHEMA.LOG2_FUNCTION' IN TYPEOF(arg))
		OR ('Expression_arm_EXPRESSIONS_SCHEMA.LOG10_FUNCTION' IN TYPEOF(arg))
		OR ('Expression_arm_EXPRESSIONS_SCHEMA.SQUARE_ROOT_FUNCTION' 
								IN TYPEOF(arg))
THEN 
	RETURN (FALSE);
END_IF;
IF 	('Expression_arm_EXPRESSIONS_SCHEMA.Plus_expression' IN TYPEOF(arg))
		OR ('Expression_arm_EXPRESSIONS_SCHEMA.Mult_expression' 
								IN TYPEOF(arg))
		OR ('Expression_arm_EXPRESSIONS_SCHEMA.Maximum_function'
								IN TYPEOF(arg))
		OR ('Expression_arm_EXPRESSIONS_SCHEMA.Maximum_function'
								IN TYPEOF(arg)) 
THEN 
	REPEAT i :=1 TO SIZEOF (
			arg\Multiple_arity_numeric_expression.operands);
	IF NOT 
		Is_int_expr(arg\Multiple_arity_numeric_expression.operands[i]) 
	THEN 
		RETURN (FALSE);
	END_IF;
	END_REPEAT;
	RETURN (TRUE);
END_IF;
IF 	('Expression_arm_EXPRESSIONS_SCHEMA.Minus_expression' IN TYPEOF(arg))
		OR ('Expression_arm_EXPRESSIONS_SCHEMA.Power_expression' 
								IN TYPEOF(arg))
THEN 
	RETURN (Is_int_expr(arg\Binary_numeric_expression.operands[1])
		AND Is_int_expr(arg\Binary_numeric_expression.operands[2]));
END_IF;
IF	('Expression_arm_EXPRESSIONS_SCHEMA.Div_expression' IN TYPEOF(arg))
		OR ('Expression_arm_EXPRESSIONS_SCHEMA.Mod_expression' IN TYPEOF(arg))
THEN 
	RETURN(TRUE);	
END_IF;
IF 'Expression_arm_EXPRESSIONS_SCHEMA.Slash_expression' IN TYPEOF(arg) 
THEN 
	RETURN (FALSE); 	
END_IF;
IF 'Expression_arm_EXPRESSIONS_SCHEMA.Length_function' IN TYPEOF(arg) 
THEN 
	RETURN (TRUE); 
END_IF;
IF 'Expression_arm_EXPRESSIONS_SCHEMA.Value_function' IN TYPEOF(arg) 
THEN 
	IF 'Expression_arm_EXPRESSIONS_SCHEMA.Int_value_function' 
								IN TYPEOF(arg) 
	THEN 
		RETURN (TRUE); 
	ELSE 
		RETURN (FALSE); 
	END_IF;
END_IF;
IF 'Expression_arm_EXPRESSIONS_SCHEMA.INTEGER_DEFINED_FUNCTION' 
								IN TYPEOF(arg)
THEN 
	RETURN(TRUE) ;
END_IF;
IF'Expression_arm_EXPRESSIONS_SCHEMA.REAL_DEFINED_FUNCTION' IN TYPEOF(arg) 
THEN 
	RETURN(FALSE) ;
END_IF ;
IF 'Expression_arm_EXPRESSIONS_SCHEMA.BOOLEAN_DEFINED_FUNCTION' 
								IN TYPEOF(arg)
THEN 
	RETURN(FALSE) ;
END_IF ;
IF 'Expression_arm_EXPRESSIONS_SCHEMA.STRING_DEFINED_FUNCTION' 
								IN TYPEOF(arg)
THEN 
	RETURN (FALSE) ;
END_IF ;

RETURN (FALSE);
