function.algorithm:
BEGIN IF (sizeof(agg) = 2) AND ((sizeof(QUERY(i1 <* agg | ('value_with_unit_mim.MEASURE_REPRESENTATION_ITEM' IN TYPEOF(i1)))) = 2) OR (sizeof(QUERY(i2 <* agg | ('value_with_unit_mim.VALUE_REPRESENTATION_ITEM' IN TYPEOF(i2)))) = 2)) THEN RETURN(TRUE)
ELSE RETURN(FALSE)
END_IF; END;

BEGIN IF (sizeof(QUERY(i <* agg | (i.name = 'upper limit'))) = 1) AND (sizeof(QUERY(i <* agg | (i.name = 'lower limit'))) = 1) THEN RETURN(TRUE)
ELSE RETURN(FALSE)
END_IF; END;

BEGIN IF (sizeof(QUERY(i <* agg | ('value_with_unit_mim.MEASURE_REPRESENTATION_ITEM' IN TYPEOF(i)))) <> 2) OR (sizeof(QUERY(i1 <* agg | ('value_with_unit_mim.MEASURE_REPRESENTATION_ITEM' IN TYPEOF(i1)) AND (SIZEOF(QUERY(i2 <* agg | ((i1\measure_with_unit.unit_component :=: i2\measure_with_unit.unit_component) OR (i1\measure_with_unit.unit_component :=: i2\measure_with_unit.unit_component)))) = 2))) = 2) THEN RETURN(TRUE)
ELSE RETURN(FALSE)
END_IF; END;
