function.algorithm:
LOCAL
  the_set : SET OF GENERIC:intype := [];
END_LOCAL;
IF SIZEOF(the_bag) > 0 THEN
  REPEAT i := 1 TO HIINDEX(the_bag) BY 1;
    the_set := the_set + the_bag[i];
  END_REPEAT;
END_IF;
RETURN(the_set);

LOCAL
  description_bag : BAG OF description_attribute := USEDIN(obj,
                     'FOUNDATION_REPRESENTATION_MIM.' + 
                     'DESCRIPTION_ATTRIBUTE.' + 'DESCRIBED_ITEM');
END_LOCAL;
IF SIZEOF(description_bag) = 1 THEN
  RETURN(description_bag[1].attribute_value);
ELSE
  RETURN(?);
END_IF;

LOCAL
  id_bag : BAG OF id_attribute := USEDIN(obj,
            'FOUNDATION_REPRESENTATION_MIM.' + 'ID_ATTRIBUTE.' + 
            'IDENTIFIED_ITEM');
END_LOCAL;
IF SIZEOF(id_bag) = 1 THEN
  RETURN(id_bag[1].attribute_value);
ELSE
  RETURN(?);
END_IF;

LOCAL
  next_items      : SET OF founded_item_select;
  new_check_items : SET OF founded_item_select;
  result_items    : SET OF founded_item_select;
END_LOCAL;
result_items := [];
new_check_items := checked_items + item;
next_items := QUERY ( z <* bag_to_set(USEDIN(item,'')) | ((
    'FOUNDATION_REPRESENTATION_MIM.REPRESENTATION_ITEM' IN TYPEOF(z)) 
    OR ('FOUNDATION_REPRESENTATION_MIM.FOUNDED_ITEM' IN TYPEOF(z))) );
IF SIZEOF(next_items) > 0 THEN
  REPEAT i := 1 TO HIINDEX(next_items) BY 1;
    IF NOT (next_items[i] IN new_check_items) THEN
      result_items := result_items + next_items[i] + using_items(
          next_items[i],new_check_items);
    END_IF;
  END_REPEAT;
END_IF;
RETURN(result_items);

LOCAL
  results            : SET OF representation;
  intermediate_items : SET OF founded_item_select;
  result_bag         : BAG OF representation;
END_LOCAL;
results := [];
result_bag := USEDIN(item,
    'FOUNDATION_REPRESENTATION_MIM.REPRESENTATION.ITEMS');
IF SIZEOF(result_bag) > 0 THEN
  REPEAT i := 1 TO HIINDEX(result_bag) BY 1;
    results := results + result_bag[i];
  END_REPEAT;
END_IF;
intermediate_items := using_items(item,[]);
IF SIZEOF(intermediate_items) > 0 THEN
  REPEAT i := 1 TO HIINDEX(intermediate_items) BY 1;
    result_bag := USEDIN(intermediate_items[i],
        'FOUNDATION_REPRESENTATION_MIM.REPRESENTATION.ITEMS');
    IF SIZEOF(result_bag) > 0 THEN
      REPEAT j := 1 TO HIINDEX(result_bag) BY 1;
        results := results + result_bag[j];
      END_REPEAT;
    END_IF;
  END_REPEAT;
END_IF;
RETURN(results);
