function.algorithm:
LOCAL
  result : BOOLEAN;
END_LOCAL;

IF ('FUNCTIONAL_DATA_AND_SCHEMATIC_REPRESENTATION_MIM_LF.SIMPLE_GENERIC_EXPRESSION' IN TYPEOF(arg1)) THEN
  RETURN (TRUE);
END_IF;
IF arg1 IN arg2 THEN
  RETURN (FALSE);
END_IF;
IF 'FUNCTIONAL_DATA_AND_SCHEMATIC_REPRESENTATION_MIM_LF.UNARY_GENERIC_EXPRESSION' IN TYPEOF(arg1) THEN
  RETURN (acyclic(arg1\unary_generic_expression.operand, arg2 + [arg1]));
END_IF;
IF 'FUNCTIONAL_DATA_AND_SCHEMATIC_REPRESENTATION_MIM_LF.BINARY_GENERIC_EXPRESSION' IN TYPEOF(arg1) THEN
  RETURN (acyclic(arg1\binary_generic_expression.operands[1], arg2 + [arg1]) AND acyclic(arg1\binary_generic_expression.operands[2], arg2 + [arg1]));
END_IF;
IF 'FUNCTIONAL_DATA_AND_SCHEMATIC_REPRESENTATION_MIM_LF.MULTIPLE_ARITY_GENERIC_EXPRESSION' IN TYPEOF(arg1) THEN
  result := TRUE;
  REPEAT i := 1 TO SIZEOF(arg1\multiple_arity_generic_expression.operands);
    result := result AND acyclic(arg1\multiple_arity_generic_expression.operands[i], arg2 + [arg1]);
  END_REPEAT;
  RETURN (result);
END_IF;

LOCAL
  i : INTEGER;
  local_composite_text : SET [0:?] OF composite_text;
  local_annotation_text : SET [0:?] OF annotation_text;
  local_children : SET [0:?] OF text_or_character;
END_LOCAL;

local_composite_text := QUERY(child <* child_text | ('FUNCTIONAL_DATA_AND_SCHEMATIC_REPRESENTATION_MIM_LF.COMPOSITE_TEXT' IN TYPEOF(child)));
IF (SIZEOF(local_composite_text) > 0) THEN
  REPEAT i := 1 TO HIINDEX(local_composite_text);
    IF (start_composite :=: local_composite_text[i]) THEN
      RETURN (FALSE);
    END_IF;
  END_REPEAT;
END_IF;
local_children := child_text;
IF (SIZEOF(local_composite_text)) > 0 THEN
  REPEAT i := 1 TO HIINDEX(local_composite_text);
    local_children := local_children + local_composite_text[i].collected_text;
  END_REPEAT;
END_IF;
local_annotation_text := QUERY(child <* child_text | ('FUNCTIONAL_DATA_AND_SCHEMATIC_REPRESENTATION_MIM_LF.ANNOTATION_TEXT' IN TYPEOF(child)));
IF (SIZEOF(local_annotation_text) > 0) THEN
  REPEAT i := 1 TO HIINDEX(local_annotation_text);
    local_children := local_children + QUERY(item <* local_annotation_text[i]\mapped_item.mapping_source.mapped_representation.items | SIZEOF(['FUNCTIONAL_DATA_AND_SCHEMATIC_REPRESENTATION_MIM_LF.ANNOTATION_TEXT', 'FUNCTIONAL_DATA_AND_SCHEMATIC_REPRESENTATION_MIM_LF.COMPOSITE_TEXT'] * TYPEOF(item)) > 0);
  END_REPEAT;
END_IF;
IF (local_children :<>: child_text) THEN
  RETURN (acyclic_composite_text(start_composite, local_children));
ELSE
  RETURN (TRUE);
END_IF;

IF NOT (('FUNCTIONAL_DATA_AND_SCHEMATIC_REPRESENTATION_MIM_LF.CURVE_REPLICA') IN TYPEOF(parent)) THEN
  RETURN (TRUE);
END_IF;
IF (parent :=: rep) THEN
  RETURN (FALSE);
ELSE
  RETURN (acyclic_curve_replica(rep, parent\curve_replica.parent_curve));
END_IF;

LOCAL
  items : SET OF representation_item;
END_LOCAL;

items := QUERY(item <* rep.items | 'FUNCTIONAL_DATA_AND_SCHEMATIC_REPRESENTATION_MIM_LF.MAPPED_ITEM' IN TYPEOF(item));
IF SIZEOF(items) = 0 THEN
  RETURN (FALSE);
ELSE
  REPEAT i := 1 TO HIINDEX(items);
    IF items[i]\mapped_item.mapping_source.mapped_representation :=: rep THEN
      RETURN (TRUE);
    ELSE
      RETURN (acyclic_mapped_item_usage(items[i]\mapped_item.mapping_source.mapped_representation));
    END_IF;
  END_REPEAT;
  RETURN (FALSE);
END_IF;

LOCAL
  x : SET OF representation_item;
  y : SET OF representation_item;
END_LOCAL;

x := QUERY(z <* children_set | 'FUNCTIONAL_DATA_AND_SCHEMATIC_REPRESENTATION_MIM_LF.MAPPED_ITEM' IN TYPEOF(z));
IF SIZEOF(x) > 0 THEN
  REPEAT i := 1 TO HIINDEX(x);
    IF x[i]\mapped_item.mapping_source.mapped_representation IN parent_set THEN
      RETURN (FALSE);
    END_IF;
    IF NOT acyclic_mapped_representation(parent_set + x[i]\mapped_item.mapping_source.mapped_representation, x[i]\mapped_item.mapping_source.mapped_representation.items) THEN
      RETURN (FALSE);
    END_IF;
  END_REPEAT;
END_IF;
x := children_set - x;
IF SIZEOF(x) > 0 THEN
  REPEAT i := 1 TO HIINDEX(x);
    y := QUERY(z <* bag_to_set(USEDIN(x[i], '')) | 'FUNCTIONAL_DATA_AND_SCHEMATIC_REPRESENTATION_MIM_LF.REPRESENTATION_ITEM' IN TYPEOF(z));
    IF NOT acyclic_mapped_representation(parent_set, y) THEN
      RETURN (FALSE);
    END_IF;
  END_REPEAT;
END_IF;
RETURN (TRUE);

LOCAL
  x : SET OF product_category_relationship;
  local_children : SET OF product_category;
END_LOCAL;

REPEAT i := 1 TO HIINDEX(children);
  IF relation.category :=: children[i] THEN
    RETURN (FALSE);
  END_IF;
END_REPEAT;
x := bag_to_set(USEDIN(relation.category, 'FUNCTIONAL_DATA_AND_SCHEMATIC_REPRESENTATION_MIM_LF.' + 'PRODUCT_CATEGORY_RELATIONSHIP.SUB_CATEGORY'));
local_children := children + relation.category;
IF SIZEOF(x) > 0 THEN
  REPEAT i := 1 TO HIINDEX(x);
    IF NOT acyclic_product_category_relationship(x[i], local_children) THEN
      RETURN (FALSE);
    END_IF;
  END_REPEAT;
END_IF;
RETURN (TRUE);

LOCAL
  the_set : SET OF GENERIC:intype := [];
END_LOCAL;

IF SIZEOF(the_bag) > 0 THEN
  REPEAT i := 1 TO HIINDEX(the_bag);
    the_set := the_set + the_bag[i];
  END_REPEAT;
END_IF;
RETURN (the_set);

IF NOT EXISTS(lgcl) THEN
  RETURN (FALSE);
END_IF;
IF lgcl <> TRUE THEN
  RETURN (FALSE);
END_IF;
RETURN (TRUE);

LOCAL
  d : direction := NVL(normalise(ref_direction), dummy_gri||direction([1.000000, 0.000000]));
END_LOCAL;

RETURN ([d, orthogonal_complement(d)]);

LOCAL
  a : SET OF text_alignment := [];
END_LOCAL;

REPEAT i := 1 TO HIINDEX(ct.collected_text);
  a := a + [ct.collected_text[i]\text_literal.alignment];
END_REPEAT;
RETURN (SIZEOF(a) = 1);

LOCAL
  f : SET OF font_select := [];
END_LOCAL;

REPEAT i := 1 TO HIINDEX(ct.collected_text);
  f := f + [ct.collected_text[i]\text_literal.font];
END_REPEAT;
RETURN (SIZEOF(f) <= 1);

LOCAL
  result : BOOLEAN := TRUE;
  k : INTEGER;
  sum : INTEGER;
END_LOCAL;

sum := knot_mult[1];
REPEAT i := 2 TO up_knots;
  sum := sum + knot_mult[i];
END_REPEAT;
IF (degree < 1) OR (up_knots < 2) OR (up_cp < degree) OR (sum <> (degree + up_cp + 2)) THEN
  result := FALSE;
  RETURN (result);
END_IF;
k := knot_mult[1];
IF (k < 1) OR (k > degree + 1) THEN
  result := FALSE;
  RETURN (result);
END_IF;
REPEAT i := 2 TO up_knots;
  IF (knot_mult[i] < 1) OR (knots[i] <= knots[i - 1]) THEN
    result := FALSE;
    RETURN (result);
  END_IF;
  k := knot_mult[i];
  IF (i < up_knots) AND (k > degree) THEN
    result := FALSE;
    RETURN (result);
  END_IF;
  IF (i = up_knots) AND (k > degree + 1) THEN
    result := FALSE;
    RETURN (result);
  END_IF;
END_REPEAT;
RETURN (result);

LOCAL
  result : BOOLEAN := TRUE;
END_LOCAL;

REPEAT i := 0 TO b.upper_index_on_control_points;
  IF b.weights[i] <= 0.000000 THEN
    result := FALSE;
    RETURN (result);
  END_IF;
END_REPEAT;
RETURN (result);

LOCAL
  result : dimensional_exponents := dimensional_exponents(0.000000, 0.000000, 0.000000, 0.000000, 0.000000, 0.000000, 0.000000);
END_LOCAL;

IF 'FUNCTIONAL_DATA_AND_SCHEMATIC_REPRESENTATION_MIM_LF.DERIVED_UNIT' IN TYPEOF(x) THEN
  REPEAT i := LOINDEX(x.elements) TO HIINDEX(x.elements);
    result.length_exponent := result.length_exponent + (x.elements[i].exponent * x.elements[i].unit.dimensions.length_exponent);
    result.mass_exponent := result.mass_exponent + (x.elements[i].exponent * x.elements[i].unit.dimensions.mass_exponent);
    result.time_exponent := result.time_exponent + (x.elements[i].exponent * x.elements[i].unit.dimensions.time_exponent);
    result.electric_current_exponent := result.electric_current_exponent + (x.elements[i].exponent * x.elements[i].unit.dimensions.electric_current_exponent);
    result.thermodynamic_temperature_exponent := result.thermodynamic_temperature_exponent + (x.elements[i].exponent * x.elements[i].unit.dimensions.thermodynamic_temperature_exponent);
    result.amount_of_substance_exponent := result.amount_of_substance_exponent + (x.elements[i].exponent * x.elements[i].unit.dimensions.amount_of_substance_exponent);
    result.luminous_intensity_exponent := result.luminous_intensity_exponent + (x.elements[i].exponent * x.elements[i].unit.dimensions.luminous_intensity_exponent);
  END_REPEAT;
ELSE
  result := x.dimensions;
END_IF;
RETURN (result);

LOCAL
  x : SET OF representation;
  y : representation_context;
  dim : dimension_count;
END_LOCAL;

IF 'FUNCTIONAL_DATA_AND_SCHEMATIC_REPRESENTATION_MIM_LF.CARTESIAN_POINT' IN TYPEOF(item) THEN
  dim := SIZEOF(item\cartesian_point.coordinates);
  RETURN (dim);
END_IF;
IF 'FUNCTIONAL_DATA_AND_SCHEMATIC_REPRESENTATION_MIM_LF.DIRECTION' IN TYPEOF(item) THEN
  dim := SIZEOF(item\direction.direction_ratios);
  RETURN (dim);
END_IF;
IF 'FUNCTIONAL_DATA_AND_SCHEMATIC_REPRESENTATION_MIM_LF.VECTOR' IN TYPEOF(item) THEN
  dim := SIZEOF(item\vector.orientation\direction.direction_ratios);
  RETURN (dim);
END_IF;
x := using_representations(item);
y := x[1].context_of_items;
dim := y\geometric_representation_context.coordinate_space_dimension;
RETURN (dim);

LOCAL
  typenames : SET OF STRING := stripped_typeof(expr);
  result : SET OF generic_variable := [];
  exprs : LIST OF generic_expression := [];
END_LOCAL;

IF 'GENERIC_LITERAL' IN typenames THEN
  RETURN (result);
END_IF;
IF 'GENERIC_VARIABLE' IN typenames THEN
  result := result + expr;
  RETURN (result);
END_IF;
IF 'QUANTIFIER_EXPRESSION' IN typenames THEN
  exprs := QUERY(ge <* expr\multiple_arity_generic_expression.operands | NOT (ge IN expr\quantifier_expression.variables));
  REPEAT i := 1 TO SIZEOF(exprs);
    result := result + free_variables_of(exprs[i]);
  END_REPEAT;
  REPEAT i := 1 TO SIZEOF(expr\quantifier_expression.variables);
    result := result - expr\quantifier_expression.variables[i];
  END_REPEAT;
  RETURN (result);
END_IF;
IF 'UNARY_GENERIC_EXPRESSION' IN typenames THEN
  RETURN (free_variables_of(expr\unary_generic_expression.operand));
END_IF;
IF 'BINARY_GENERIC_EXPRESSION' IN typenames THEN
  result := free_variables_of(expr\binary_generic_expression.operands[1]);
  RETURN (result + free_variables_of(expr\binary_generic_expression.operands[2]));
END_IF;
IF 'MULTIPLE_ARITY_GENERIC_EXPRESSION' IN typenames THEN
  REPEAT i := 1 TO SIZEOF(expr\multiple_arity_generic_expression.operands);
    result := result + free_variables_of(expr\multiple_arity_generic_expression.operands[i]);
  END_REPEAT;
  RETURN (result);
END_IF;
RETURN (result);

LOCAL
  description_bag : BAG OF description_attribute := (USEDIN(obj, 'FUNCTIONAL_DATA_AND_SCHEMATIC_REPRESENTATION_MIM_LF.' + 'DESCRIPTION_ATTRIBUTE.' + 'DESCRIBED_ITEM'));
END_LOCAL;

IF SIZEOF(description_bag) = 1 THEN
  RETURN (description_bag[1].attribute_value);
ELSE
  RETURN (?);
END_IF;

LOCAL
  id_bag : BAG OF id_attribute := (USEDIN(obj, 'FUNCTIONAL_DATA_AND_SCHEMATIC_REPRESENTATION_MIM_LF.' + 'ID_ATTRIBUTE.' + 'IDENTIFIED_ITEM'));
END_LOCAL;

IF SIZEOF(id_bag) = 1 THEN
  RETURN (id_bag[1].attribute_value);
ELSE
  RETURN (?);
END_IF;

LOCAL
  name_bag : BAG OF name_attribute := (USEDIN(obj, 'FUNCTIONAL_DATA_AND_SCHEMATIC_REPRESENTATION_MIM_LF.' + 'NAME_ATTRIBUTE.' + 'NAMED_ITEM'));
END_LOCAL;

IF SIZEOF(name_bag) = 1 THEN
  RETURN (name_bag[1].attribute_value);
ELSE
  RETURN (?);
END_IF;

LOCAL
  role_bag : BAG OF role_association := (USEDIN(obj, 'FUNCTIONAL_DATA_AND_SCHEMATIC_REPRESENTATION_MIM_LF.' + 'ROLE_ASSOCIATION.' + 'ITEM_WITH_ROLE'));
END_LOCAL;

IF SIZEOF(role_bag) = 1 THEN
  RETURN (role_bag[1].role);
ELSE
  RETURN (?);
END_IF;

LOCAL
  set_of_sets : SET OF class := [];
END_LOCAL;

IF (set_a = set_b) THEN
  RETURN (TRUE);
END_IF;
set_of_sets := set_of_sets + set_b;
RETURN (identical_to_one_of_set_of_sets(set_a, set_of_sets));

LOCAL
  i : INTEGER;
  initial_size : INTEGER;
  augmented_size : INTEGER;
  set_of_forward_equivalences : SET OF same_membership := [];
  set_of_backward_equivalences : SET OF same_membership := [];
  augmented_set_of_sets : SET OF class := [];
END_LOCAL;

IF (set_a IN set_of_sets) THEN
  RETURN (TRUE);
END_IF;
initial_size := SIZEOF(set_of_sets);
IF (initial_size = 0) THEN
  RETURN (FALSE);
END_IF;
REPEAT i := 1 TO initial_size;
  set_of_forward_equivalences := set_of_forward_equivalences + USEDIN(set_of_sets[i], 'FUNCTIONAL_DATA_AND_SCHEMATIC_REPRESENTATION_MIM_LF.SAME_MEMBERSHIP.SET_1');
  set_of_backward_equivalences := set_of_forward_equivalences + USEDIN(set_of_sets[i], 'FUNCTIONAL_DATA_AND_SCHEMATIC_REPRESENTATION_MIM_LF.SAME_MEMBERSHIP.SET_2');
END_REPEAT;
augmented_set_of_sets := set_of_sets;
IF (SIZEOF(set_of_forward_equivalences) > 0) THEN
  REPEAT i := 1 TO HIINDEX(set_of_forward_equivalences);
    augmented_set_of_sets := augmented_set_of_sets + set_of_forward_equivalences[i].set_2;
  END_REPEAT;
END_IF;
IF (SIZEOF(set_of_backward_equivalences) > 0) THEN
  REPEAT i := 1 TO HIINDEX(set_of_backward_equivalences);
    augmented_set_of_sets := augmented_set_of_sets + set_of_backward_equivalences[i].set_1;
  END_REPEAT;
END_IF;
augmented_size := SIZEOF(augmented_set_of_sets);
IF augmented_size = initial_size THEN
  RETURN (FALSE);
END_IF;
RETURN (identical_to_one_of_set_of_sets(set_a, augmented_set_of_sets));

LOCAL
  y : BAG OF representation_item;
END_LOCAL;

IF SIZEOF(USEDIN(item, 'FUNCTIONAL_DATA_AND_SCHEMATIC_REPRESENTATION_MIM_LF.REPRESENTATION.ITEMS') * cntxt.representations_in_context) > 0 THEN
  RETURN (TRUE);
ELSE
  y := QUERY(z <* USEDIN(item, '') | 'FUNCTIONAL_DATA_AND_SCHEMATIC_REPRESENTATION_MIM_LF.REPRESENTATION_ITEM' IN TYPEOF(z));
  IF SIZEOF(y) > 0 THEN
    REPEAT i := 1 TO HIINDEX(y);
      IF item_in_context(y[i], cntxt) THEN
        RETURN (TRUE);
      END_IF;
    END_REPEAT;
  END_IF;
END_IF;
RETURN (FALSE);

IF ((((year MOD 4) = 0) AND ((year MOD 100) <> 0)) OR ((year MOD 400) = 0)) THEN
  RETURN (TRUE);
ELSE
  RETURN (FALSE);
END_IF;

LOCAL
  n : INTEGER;
  res : ARRAY [low:u] OF GENERIC:T;
END_LOCAL;

n := SIZEOF(lis);
IF (n <> (u - low + 1)) THEN
  RETURN (?);
ELSE
  res := [lis[1]:n];
  REPEAT i := 2 TO n;
    res[low + i - 1] := lis[i];
  END_REPEAT;
  RETURN (res);
END_IF;

LOCAL
  types : SET OF STRING;
  refs_plus : SET OF maths_space;
END_LOCAL;

IF (spc IN refs) THEN
  RETURN (FALSE);
END_IF;
types := TYPEOF(spc);
refs_plus := refs + spc;
IF (schema_prefix + 'FINITE_SPACE') IN types THEN
  RETURN (bool(SIZEOF(QUERY(sp <* QUERY(mem <* spc\finite_space.members | (schema_prefix + 'MATHS_SPACE') IN TYPEOF(mem)) | NOT no_cyclic_space_reference(sp, refs_plus))) = 0));
END_IF;
IF (schema_prefix + 'UNIFORM_PRODUCT_SPACE') IN types THEN
  RETURN (no_cyclic_space_reference(spc\uniform_product_space.base, refs_plus));
END_IF;
IF (schema_prefix + 'LISTED_PRODUCT_SPACE') IN types THEN
  RETURN (bool(SIZEOF(QUERY(fac <* spc\listed_product_space.factors | NOT no_cyclic_space_reference(fac, refs_plus))) = 0));
END_IF;
IF (schema_prefix + 'EXTENDED_TUPLE_SPACE') IN types THEN
  RETURN (no_cyclic_space_reference(spc\extended_tuple_space.base, refs_plus) AND no_cyclic_space_reference(spc\extended_tuple_space.extender, refs_plus));
END_IF;
RETURN (TRUE);

LOCAL
  ndim : INTEGER;
  v : direction;
  result : vector_or_direction;
  vec : vector;
  mag : REAL;
END_LOCAL;

IF NOT EXISTS(arg) THEN
  result := ?;
ELSE
  ndim := arg.dim;
  IF 'FUNCTIONAL_DATA_AND_SCHEMATIC_REPRESENTATION_MIM_LF.VECTOR' IN TYPEOF(arg) THEN
    BEGIN
      v := dummy_gri||direction(arg.orientation.direction_ratios);
      IF arg.magnitude = 0.000000 THEN
        RETURN (?);
      ELSE
        vec := dummy_gri||vector(v, 1.000000);
      END_IF;
    END;
  ELSE
    v := dummy_gri||direction(arg.direction_ratios);
  END_IF;
  mag := 0.000000;
  REPEAT i := 1 TO ndim;
    mag := mag + v.direction_ratios[i] * v.direction_ratios[i];
  END_REPEAT;
  IF mag > 0.000000 THEN
    mag := SQRT(mag);
    REPEAT i := 1 TO ndim;
      v.direction_ratios[i] := v.direction_ratios[i] / mag;
    END_REPEAT;
    IF 'FUNCTIONAL_DATA_AND_SCHEMATIC_REPRESENTATION_MIM_LF.VECTOR' IN TYPEOF(arg) THEN
      vec.orientation := v;
      result := vec;
    ELSE
      result := v;
    END_IF;
  ELSE
    RETURN (?);
  END_IF;
END_IF;
RETURN (result);

LOCAL
  result : direction;
END_LOCAL;

IF (vec.dim <> 2) OR NOT EXISTS(vec) THEN
  RETURN (?);
ELSE
  result := dummy_gri||direction([ - vec.direction_ratios[2], vec.direction_ratios[1]]);
  RETURN (result);
END_IF;

LOCAL
  types : SET OF STRING := TYPEOF(arg);
  stypes : SET OF STRING := [];
  n : INTEGER := LENGTH(schema_prefix);
END_LOCAL;

REPEAT i := 1 TO SIZEOF(types);
  IF types[i][1:n] = schema_prefix THEN
    stypes := ;
  ELSE
    stypes := stypes + [types[i]];
  END_IF;
END_REPEAT;
RETURN (stypes);

LOCAL
  new_check_items : SET OF founded_item_select;
  result_items : SET OF founded_item_select;
  next_items : SET OF founded_item_select;
END_LOCAL;

result_items := [];
new_check_items := checked_items + item;
next_items := QUERY(z <* bag_to_set(USEDIN(item, '')) | ('FUNCTIONAL_DATA_AND_SCHEMATIC_REPRESENTATION_MIM_LF.REPRESENTATION_ITEM' IN TYPEOF(z)) OR ('FUNCTIONAL_DATA_AND_SCHEMATIC_REPRESENTATION_MIM_LF.FOUNDED_ITEM' IN TYPEOF(z)));
IF SIZEOF(next_items) > 0 THEN
  REPEAT i := 1 TO HIINDEX(next_items);
    IF NOT (next_items[i] IN new_check_items) THEN
      result_items := result_items + next_items[i] + using_items(next_items[i], new_check_items);
    END_IF;
  END_REPEAT;
END_IF;
RETURN (result_items);

LOCAL
  results : SET OF representation;
  result_bag : BAG OF representation;
  intermediate_items : SET OF founded_item_select;
END_LOCAL;

results := [];
result_bag := USEDIN(item, 'FUNCTIONAL_DATA_AND_SCHEMATIC_REPRESENTATION_MIM_LF.REPRESENTATION.ITEMS');
IF SIZEOF(result_bag) > 0 THEN
  REPEAT i := 1 TO HIINDEX(result_bag);
    results := results + result_bag[i];
  END_REPEAT;
END_IF;
intermediate_items := using_items(item, []);
IF SIZEOF(intermediate_items) > 0 THEN
  REPEAT i := 1 TO HIINDEX(intermediate_items);
    result_bag := USEDIN(intermediate_items[i], 'FUNCTIONAL_DATA_AND_SCHEMATIC_REPRESENTATION_MIM_LF.REPRESENTATION.ITEMS');
    IF SIZEOF(result_bag) > 0 THEN
      REPEAT j := 1 TO HIINDEX(result_bag);
        results := results + result_bag[j];
      END_REPEAT;
    END_IF;
  END_REPEAT;
END_IF;
RETURN (results);

IF SIZEOF(['FUNCTIONAL_DATA_AND_SCHEMATIC_REPRESENTATION_MIM_LF.POLYLINE', 'FUNCTIONAL_DATA_AND_SCHEMATIC_REPRESENTATION_MIM_LF.B_SPLINE_CURVE', 'FUNCTIONAL_DATA_AND_SCHEMATIC_REPRESENTATION_MIM_LF.ELLIPSE', 'FUNCTIONAL_DATA_AND_SCHEMATIC_REPRESENTATION_MIM_LF.CIRCLE'] * TYPEOF(crv)) = 1 THEN
  RETURN (TRUE);
ELSE
  IF (('FUNCTIONAL_DATA_AND_SCHEMATIC_REPRESENTATION_MIM_LF.TRIMMED_CURVE') IN TYPEOF(crv)) THEN
    IF SIZEOF(['FUNCTIONAL_DATA_AND_SCHEMATIC_REPRESENTATION_MIM_LF.LINE', 'FUNCTIONAL_DATA_AND_SCHEMATIC_REPRESENTATION_MIM_LF.PARABOLA', 'FUNCTIONAL_DATA_AND_SCHEMATIC_REPRESENTATION_MIM_LF.HYPERBOLA'] * TYPEOF(crv\trimmed_curve.basis_curve)) = 1 THEN
      RETURN (TRUE);
    ELSE
      RETURN (valid_basis_curve_in_2d_wireframe(crv\trimmed_curve.basis_curve));
    END_IF;
  ELSE
    IF (('FUNCTIONAL_DATA_AND_SCHEMATIC_REPRESENTATION_MIM_LF.OFFSET_CURVE_2D') IN TYPEOF(crv)) THEN
      RETURN (valid_basis_curve_in_2d_wireframe(crv\offset_curve_2d.basis_curve));
    ELSE
      IF (('FUNCTIONAL_DATA_AND_SCHEMATIC_REPRESENTATION_MIM_LF.CURVE_REPLICA') IN TYPEOF(crv)) THEN
        RETURN (valid_basis_curve_in_2d_wireframe(crv\curve_replica.parent_curve));
      ELSE
        IF (('FUNCTIONAL_DATA_AND_SCHEMATIC_REPRESENTATION_MIM_LF.COMPOSITE_CURVE') IN TYPEOF(crv)) THEN
          RETURN (SIZEOF(QUERY(ccs <* crv\composite_curve.segments | NOT (valid_basis_curve_in_2d_wireframe(ccs.parent_curve)))) = 0);
        END_IF;
      END_IF;
    END_IF;
  END_IF;
END_IF;
RETURN (FALSE);

CASE date.month_component OF 
  1:
    RETURN ({1 <= date.day_component <= 31});
  2:
    BEGIN
      IF (leap_year(date.year_component)) THEN
        RETURN ({1 <= date.day_component <= 29});
      ELSE
        RETURN ({1 <= date.day_component <= 28});
      END_IF;
    END;
  3:
    RETURN ({1 <= date.day_component <= 31});
  4:
    RETURN ({1 <= date.day_component <= 30});
  5:
    RETURN ({1 <= date.day_component <= 31});
  6:
    RETURN ({1 <= date.day_component <= 30});
  7:
    RETURN ({1 <= date.day_component <= 31});
  8:
    RETURN ({1 <= date.day_component <= 31});
  9:
    RETURN ({1 <= date.day_component <= 30});
  10:
    RETURN ({1 <= date.day_component <= 31});
  11:
    RETURN ({1 <= date.day_component <= 30});
  12:
    RETURN ({1 <= date.day_component <= 31});
END_CASE;
RETURN (FALSE);

IF ('REAL' IN TYPEOF(m)) THEN
  RETURN (m > 0.000000);
ELSE
  IF ('INTEGER' IN TYPEOF(m)) THEN
    RETURN (m > 0);
  ELSE
    RETURN (TRUE);
  END_IF;
END_IF;

IF EXISTS(time.second_component) THEN
  RETURN (EXISTS(time.minute_component));
ELSE
  RETURN (TRUE);
END_IF;

