LOCAL
  item : identification_item;
  role : identification_role;
END_LOCAL;

role := aia.role;
IF role.name = 'version' THEN
  REPEAT i := LOINDEX(aia.items) TO HIINDEX(aia.items);
    item := aia.items[i];
    IF (SIZEOF(['FUNCTIONAL_DATA_AND_SCHEMATIC_REPRESENTATION_MIM_LF.' + 'VERSIONABLE_ITEM'] * TYPEOF(item)) = 0) THEN
      RETURN (FALSE);
    END_IF;
  END_REPEAT;
  RETURN (TRUE);
ELSE
  RETURN (TRUE);
END_IF;

LOCAL
  number_of_input_instances : INTEGER;
  number_of_referring_instances : INTEGER;
  bag_of_referring_instances : BAG OF GENERIC := [];
  dependently_instantiated_flag : BOOLEAN;
  previous_in_chain_plus : LIST OF GENERIC := [];
  recursion : BOOLEAN;
  result : BOOLEAN := TRUE;
  set_of_types : SET OF STRING := [];
END_LOCAL;

IF EXISTS(set_of_input_instances) THEN
  number_of_input_instances := SIZEOF(set_of_input_instances);
  set_of_input_types := set_of_input_types + 'GENERIC';
  REPEAT i := 1 TO number_of_input_instances;
    bag_of_referring_instances := USEDIN(set_of_input_instances[i], '');
    IF EXISTS(bag_of_referring_instances) THEN
      number_of_referring_instances := SIZEOF(bag_of_referring_instances);
      dependently_instantiated_flag := FALSE;
      REPEAT j := 1 TO number_of_referring_instances;
        set_of_types := TYPEOF(bag_of_referring_instances[j]);
        IF set_of_types <= set_of_input_types THEN
          IF EXISTS(previous_in_chain) THEN
            recursion := TRUE;
            REPEAT k := 1 TO SIZEOF(previous_in_chain);
              IF previous_in_chain[k] :=: bag_of_referring_instances[j] THEN
                recursion := FALSE;
                ESCAPE;
              END_IF;
            END_REPEAT;
          END_IF;
          IF recursion THEN
            previous_in_chain_plus := previous_in_chain + set_of_input_instances[i];
            IF dependently_instantiated([bag_of_referring_instances[j]], set_of_input_types, previous_in_chain_plus) THEN
              dependently_instantiated_flag := TRUE;
              ESCAPE;
            ELSE
              SKIP;
            END_IF;
          END_IF;
        ELSE
          dependently_instantiated_flag := TRUE;
          ESCAPE;
        END_IF;
      END_REPEAT;
      IF NOT dependently_instantiated_flag THEN
        RETURN (FALSE);
      END_IF;
    ELSE
      RETURN (FALSE);
    END_IF;
  END_REPEAT;
ELSE
  RETURN (FALSE);
END_IF;
RETURN (TRUE);

rule.algorithm:
LOCAL
  number_of_input_instances : INTEGER;
  previous_in_chain : LIST OF GENERIC := [];
  set_of_input_types : SET OF STRING := [];
  all_instances : SET OF GENERIC := [];
END_LOCAL;

all_instances := ;
number_of_input_instances := SIZEOF(all_instances);
REPEAT i := 1 TO number_of_input_instances;
  set_of_input_types := set_of_input_types + TYPEOF(all_instances[i]);
END_REPEAT;
