function.algorithm:
LOCAL
	result: BOOLEAN;
END_LOCAL;

IF ('generic_expression_mim_generic_expressionS_SCHEMA.simple_generic_expression'
	IN TYPEOF (arg1)) 
THEN
	RETURN (TRUE);
END_IF;

IF arg1 IN arg2 
THEN 
	RETURN (FALSE);
END_IF;

IF 'generic_expression_mim_generic_expressionS_SCHEMA.unary_generic_expression' 
	IN TYPEOF (arg1) 
THEN 
	RETURN 
	(acyclic(arg1\unary_generic_expression.operand,arg2+[arg1]));
END_IF;

IF 'generic_expression_mim_generic_expressionS_SCHEMA.binary_generic_expression' 
	IN TYPEOF (arg1) 
THEN 
	RETURN 
	(acyclic(arg1\binary_generic_expression.operands[1],arg2+[arg1])
	AND
	acyclic(arg1\binary_generic_expression.operands[2],arg2+[arg1]));
END_IF;

IF 
'generic_expression_mim_generic_expressionS_SCHEMA.multiple_arity_generic_expression' 
	IN TYPEOF (arg1) 
THEN 
	result := TRUE;
	REPEAT i := 1 TO 
			SIZEOF (arg1\multiple_arity_generic_expression.operands);
		result := result AND
		acyclic(arg1\multiple_arity_generic_expression.operands[i], arg2+[arg1]);
	END_REPEAT;

	RETURN (result);
END_IF;

LOCAL
	result : SET OF generic_variable := [];
END_LOCAL;

IF 'generic_expression_mim_generic_expressionS_SCHEMA.generic_variable' 
	IN TYPEOF (arg) 
THEN 
	RETURN ([arg]);
END_IF;

IF 'generic_expression_mim_generic_expressionS_SCHEMA.unary_generic_expression' 
	IN TYPEOF (arg)
THEN 
	RETURN (used_variables (arg\unary_generic_expression.operand));
END_IF;

IF 'generic_expression_mim_generic_expressionS_SCHEMA.binary_generic_expression'
	IN TYPEOF (arg)
THEN 
	RETURN(used_variables(arg\binary_generic_expression.operands[1])
		+ used_variables (arg\binary_generic_expression.operands[2]));
END_IF;

IF
'generic_expression_mim_generic_expressionS_SCHEMA.multiple_arity_generic_expression' 
	IN TYPEOF (arg)
THEN
	REPEAT i := 1 TO 
		SIZEOF(arg\multiple_arity_generic_expression.operands);
		result := result + used_variables(
			arg\multiple_arity_generic_expression.operands[i]);
	END_REPEAT;

	RETURN (result);
END_IF;
RETURN ([ ]);
