  END_IF;
END_IF;
IF 'MANIFOLD_SURFACE_MIM.POSITIVE_LENGTH_MEASURE' IN TYPEOF(m.
    value_component) THEN
  IF derive_dimensional_exponents(m.unit_component) <> 
