'MEASURE_REPRESENTATION_ARM.PROPERTY_VALUE_REPRESENTATION' IN
TYPEOF(r)) | EXISTS(pvr.context_of_items.units) )) > 0);
  END_ENTITY; -- numerical_item_with_global_unit
