function.algorithm:
LOCAL
i: INTEGER;
END_LOCAL;
IF 'PARAMETERIZED_CATALOG_DATA_INFORMATION_ARM_LF.INT_LITERAL' IN TYPEOF(arg)
THEN
RETURN (TRUE);
END_IF;
IF 'PARAMETERIZED_CATALOG_DATA_INFORMATION_ARM_LF.REAL_LITERAL' IN TYPEOF(arg)
THEN
RETURN (FALSE);
END_IF;
IF 'PARAMETERIZED_CATALOG_DATA_INFORMATION_ARM_LF.INT_NUMERIC_VARIABLE' IN TYPEOF(arg)
THEN
RETURN (TRUE);
END_IF;
IF 'PARAMETERIZED_CATALOG_DATA_INFORMATION_ARM_LF.REAL_NUMERIC_VARIABLE' IN TYPEOF(arg)
THEN
RETURN (FALSE);
END_IF;
IF 'PARAMETERIZED_CATALOG_DATA_INFORMATION_ARM_LF.ABS_FUNCTION' IN TYPEOF(arg)
THEN
RETURN (Is_int_expr(arg\Unary_numeric_expression.operand));
END_IF;
IF 'NUMERIC_FUNCTION.MINUS_FUNCTION' IN TYPEOF(arg)
THEN
RETURN (Is_int_expr(arg\Unary_numeric_expression.operand));
END_IF;
IF ('PARAMETERIZED_CATALOG_DATA_INFORMATION_ARM_LF.SIN_FUNCTION' IN TYPEOF(arg))
OR ('PARAMETERIZED_CATALOG_DATA_INFORMATION_ARM_LF.COS_FUNCTION' IN TYPEOF(arg))
OR ('PARAMETERIZED_CATALOG_DATA_INFORMATION_ARM_LF.TAN_FUNCTION' IN TYPEOF(arg))
OR ('PARAMETERIZED_CATALOG_DATA_INFORMATION_ARM_LF.ASIN_FUNCTION' IN TYPEOF(arg))
OR ('PARAMETERIZED_CATALOG_DATA_INFORMATION_ARM_LF.ACOS_FUNCTION' IN TYPEOF(arg))
OR ('PARAMETERIZED_CATALOG_DATA_INFORMATION_ARM_LF.Atan_function' IN TYPEOF(arg))
OR ('PARAMETERIZED_CATALOG_DATA_INFORMATION_ARM_LF.EXP_FUNCTION' IN TYPEOF(arg))
OR ('PARAMETERIZED_CATALOG_DATA_INFORMATION_ARM_LF.LOG_FUNCTION' IN TYPEOF(arg))
OR ('PARAMETERIZED_CATALOG_DATA_INFORMATION_ARM_LF.LOG2_FUNCTION' IN TYPEOF(arg))
OR ('PARAMETERIZED_CATALOG_DATA_INFORMATION_ARM_LF.LOG10_FUNCTION' IN TYPEOF(arg))
OR ('PARAMETERIZED_CATALOG_DATA_INFORMATION_ARM_LF.SQUARE_ROOT_FUNCTION'
IN TYPEOF(arg))
THEN
RETURN (FALSE);
END_IF;
IF 	('PARAMETERIZED_CATALOG_DATA_INFORMATION_ARM_LF.PLUS_EXPRESSION' IN TYPEOF(arg))
OR ('PARAMETERIZED_CATALOG_DATA_INFORMATION_ARM_LF.MULT_EXPRESSION'
IN TYPEOF(arg))
OR ('PARAMETERIZED_CATALOG_DATA_INFORMATION_ARM_LF.MAXIMUM_FUNCTION'
IN TYPEOF(arg))
OR ('PARAMETERIZED_CATALOG_DATA_INFORMATION_ARM_LF.MINIMUM_FUNCTION'
IN TYPEOF(arg))
THEN
REPEAT i :=1 TO SIZEOF (
arg\Multiple_arity_numeric_expression.operands);
IF NOT
Is_int_expr(arg\Multiple_arity_numeric_expression.operands[i])
THEN
RETURN (FALSE);
END_IF;
END_REPEAT;
RETURN (TRUE);
END_IF;
IF 	('PARAMETERIZED_CATALOG_DATA_INFORMATION_ARM_LF.MINUS_EXPRESSION' IN TYPEOF(arg))
OR ('PARAMETERIZED_CATALOG_DATA_INFORMATION_ARM_LF.POWER_EXPRESSION'
IN TYPEOF(arg))
THEN
RETURN (Is_int_expr(arg\Binary_numeric_expression.operands[1])
AND Is_int_expr(arg\Binary_numeric_expression.operands[2]));
END_IF;
IF	('PARAMETERIZED_CATALOG_DATA_INFORMATION_ARM_LF.DIV_EXPRESSION' IN TYPEOF(arg))
OR ('PARAMETERIZED_CATALOG_DATA_INFORMATION_ARM_LF.MOD_EXPRESSION' IN TYPEOF(arg))
THEN
RETURN(TRUE);
END_IF;
IF 'PARAMETERIZED_CATALOG_DATA_INFORMATION_ARM_LF.SLASH_EXPRESSION' IN TYPEOF(arg)
THEN
RETURN (FALSE);
END_IF;
IF 'PARAMETERIZED_CATALOG_DATA_INFORMATION_ARM_LF.LENGTH_FUNCTION' IN TYPEOF(arg)
THEN
RETURN (TRUE);
END_IF;
IF 'PARAMETERIZED_CATALOG_DATA_INFORMATION_ARM_LF.VALUE_FUNCTION' IN TYPEOF(arg)
THEN
IF 'PARAMETERIZED_CATALOG_DATA_INFORMATION_ARM_LF.INT_VALUE_FUNCTION'
IN TYPEOF(arg)
THEN
RETURN (TRUE);
ELSE
RETURN (FALSE);
END_IF;
END_IF;
IF 'PARAMETERIZED_CATALOG_DATA_INFORMATION_ARM_LF.INTEGER_DEFINED_FUNCTION'
IN TYPEOF(arg)
THEN
RETURN(TRUE) ;
END_IF;
IF'PARAMETERIZED_CATALOG_DATA_INFORMATION_ARM_LF.REAL_DEFINED_FUNCTION' IN TYPEOF(arg)
THEN
RETURN(FALSE) ;
END_IF ;
IF 'PARAMETERIZED_CATALOG_DATA_INFORMATION_ARM_LF.BOOLEAN_DEFINED_FUNCTION'
IN TYPEOF(arg)
THEN
RETURN(FALSE) ;
END_IF ;
IF 'PARAMETERIZED_CATALOG_DATA_INFORMATION_ARM_LF.STRING_DEFINED_FUNCTION'
IN TYPEOF(arg)
THEN
RETURN (FALSE) ;
END_IF ;
RETURN (FALSE);

LOCAL
result: BOOLEAN;
END_LOCAL;
IF ('PARAMETERIZED_CATALOG_DATA_INFORMATION_ARM_LF.SIMPLE_GENERIC_EXPRESSION'
IN TYPEOF (arg1))
THEN
RETURN (TRUE);
END_IF;
IF arg1 IN arg2
THEN
RETURN (FALSE);
END_IF;
IF 'PARAMETERIZED_CATALOG_DATA_INFORMATION_ARM_LF.UNARY_GENERIC_EXPRESSION'
IN TYPEOF (arg1)
THEN
RETURN
(Acyclic(arg1\Unary_generic_expression.operand,arg2+[arg1]));
END_IF;
IF 'PARAMETERIZED_CATALOG_DATA_INFORMATION_ARM_LF.BINARY_GENERIC_EXPRESSION'
IN TYPEOF (arg1)
THEN
RETURN
(Acyclic(arg1\Binary_generic_expression.operands[1],arg2+[arg1])
AND
Acyclic(arg1\Binary_generic_expression.operands[2],arg2+[arg1]));
END_IF;
IF
'PARAMETERIZED_CATALOG_DATA_INFORMATION_ARM_LF.MULTIPLE_ARITY_GENERIC_EXPRESSION'
IN TYPEOF (arg1)
THEN
result := TRUE;
REPEAT i := 1 TO
SIZEOF (arg1\Multiple_arity_generic_expression.operands);
result := result AND
Acyclic(arg1\Multiple_arity_generic_expression.operands[i], arg2+[arg1]);
END_REPEAT;
RETURN (result);
END_IF;

LOCAL
result : SET OF Generic_variable := [];
END_LOCAL;
IF 'PARAMETERIZED_CATALOG_DATA_INFORMATION_ARM_LF.GENERIC_VARIABLE'
IN TYPEOF (arg)
THEN
RETURN ([arg]);
END_IF;
IF 'PARAMETERIZED_CATALOG_DATA_INFORMATION_ARM_LF.UNARY_GENERIC_EXPRESSION'
IN TYPEOF (arg)
THEN
RETURN (Used_variables (arg\Unary_generic_expression.operand));
END_IF;
IF 'PARAMETERIZED_CATALOG_DATA_INFORMATION_ARM_LF.BINARY_GENERIC_EXPRESSION'
IN TYPEOF (arg)
THEN
RETURN(Used_variables(arg\Binary_generic_expression.operands[1])
+ Used_variables (arg\Binary_generic_expression.operands[2]));
END_IF;
IF
'PARAMETERIZED_CATALOG_DATA_INFORMATION_ARM_LF.MULTIPLE_ARITY_GENERIC_EXPRESSION'
IN TYPEOF (arg)
THEN
REPEAT i := 1 TO
SIZEOF(arg\Multiple_arity_generic_expression.operands);
result := result + Used_variables(
arg\Multiple_arity_generic_expression.operands[i]);
END_REPEAT;
RETURN (result);
END_IF;
RETURN ([ ]);

LOCAL
the_set : SET OF GENERIC : intype := [];
i : INTEGER;
END_LOCAL;
IF SIZEOF(the_bag) > 0 THEN
REPEAT i := 1 TO HIINDEX(the_bag);
the_set := the_set + the_bag[i];
END_REPEAT;
END_IF;
RETURN(the_set);

CASE rep.name OF
'document content':
BEGIN
RETURN (
SIZEOF(QUERY(items <* rep\Representation.items |
NOT (items.name IN ['detail level', 'geometry type', 'real world scale'])))= 0
);
END;
'document creation':
BEGIN
IF NOT (SIZEOF(QUERY(items <* rep\Representation.items |
NOT (items.name IN ['creating interface', 'creating system', 'operating system'])))= 0
) THEN
RETURN(FALSE);
END_IF;
IF NOT (SIZEOF(QUERY(items <* rep\Representation.items |
NOT (items.name IN ['creating system'])))= 1
) THEN
RETURN(FALSE);
ELSE
RETURN(TRUE);
END_IF;
END;
'document format':
BEGIN
RETURN (
SIZEOF(QUERY(items <* rep\Representation.items |
NOT (items.name IN ['character code', 'data format', 'size format', 'size format standard'])))= 0
);
END;
'document size':
BEGIN
RETURN (
SIZEOF(QUERY(items <* rep\Representation.items |
NOT (items.name IN ['file size', 'page count'])))= 0
);
END;
OTHERWISE:
RETURN(UNKNOWN);
END_CASE;

LOCAL
x : SET OF representation_relationship;
END_LOCAL;
IF relation.rep_1 IN relatives THEN
RETURN (FALSE);
END_IF;
x := QUERY(r <* bag_to_set(USEDIN(relation.rep_1, 'PARAMETERIZED_CATALOG_DATA_INFORMATION_ARM_LF.' + 'REPRESENTATION_RELATIONSHIP.' + 'REP_2')) | specific_relation IN TYPEOF(r));
REPEAT i := 1 TO HIINDEX(x);
IF NOT acyclic_representation_relationship(x[i], relatives + relation.rep_1, specific_relation) THEN
RETURN (FALSE);
END_IF;
END_REPEAT;
RETURN (TRUE);

rule.algorithm:
LOCAL
assemblies_of_parts: SET OF Assembly_component_relationship := [];
END_LOCAL;

function.algorithm:
LOCAL
category_assignments: BAG OF Product_category_assignment;
categories: SET OF STRING:=[];
i: INTEGER;
END_LOCAL;
category_assignments := USEDIN(obj,'PARAMETERIZED_CATALOG_DATA_INFORMATION_ARM_LF.' + 'PRODUCT_CATEGORY_ASSIGNMENT.PRODUCTS');
REPEAT i := LOINDEX(category_assignments) TO HIINDEX(category_assignments) BY 1;
categories := categories + category_assignments[i].category.name;
END_REPEAT;
RETURN(categories);

rule.algorithm:
LOCAL
ndea_set : SET OF Effectivity_assignment := [];
END_LOCAL;
ndea_set := QUERY(ea <* Effectivity_assignment |
NOT ('PARAMETERIZED_CATALOG_DATA_INFORMATION_ARM_LF.DATED_EFFECTIVITY' IN TYPEOF(ea.assigned_effectivity)));

function.algorithm:
LOCAL
result: SET OF STRING:= [];
END_LOCAL;
REPEAT i:=1 TO HIINDEX(items);
result := result + TYPEOF(items[i]);
END_REPEAT;
RETURN(result);

IF ('PARAMETERIZED_CATALOG_DATA_INFORMATION_ARM_LF.CLASS_CONDITION_ASSOCIATION' IN TYPEOF(config.is_solution_for)) THEN
IF config.configuration_type = configuration_kind.usage THEN
RETURN(config.is_solution_for\Class_condition_association.condition_type ='part usage');
ELSE
RETURN(config.is_solution_for\Class_condition_association.condition_type ='design case');
END_IF;
END_IF;
IF ('PARAMETERIZED_CATALOG_DATA_INFORMATION_ARM_LF.CLASS_SPECIFICATION_ASSOCIATION' IN TYPEOF(config.is_solution_for)) THEN
IF config.configuration_type = configuration_kind.usage THEN
RETURN(config.is_solution_for\Class_specification_association.association_type ='part usage');
ELSE
RETURN(config.is_solution_for\Class_specification_association.association_type ='design case');
END_IF;
END_IF;
RETURN(FALSE);
