THEN
RETURN (TRUE);
END_IF;
IF 'PARAMETERIZED_CATALOG_DATA_INFORMATION_MIM_LF.REAL_NUMERIC_VARIABLE' IN TYPEOF(arg)
THEN
RETURN (FALSE);
END_IF;
