IF ('PARAMETERIZED_CATALOG_DATA_INFORMATION_MIM_LF.SIN_FUNCTION' IN TYPEOF(arg))
OR ('PARAMETERIZED_CATALOG_DATA_INFORMATION_MIM_LF.COS_FUNCTION' IN TYPEOF(arg))
OR ('PARAMETERIZED_CATALOG_DATA_INFORMATION_MIM_LF.TAN_FUNCTION' IN TYPEOF(arg))
OR ('PARAMETERIZED_CATALOG_DATA_INFORMATION_MIM_LF.ASIN_FUNCTION' IN TYPEOF(arg))
OR ('PARAMETERIZED_CATALOG_DATA_INFORMATION_MIM_LF.ACOS_FUNCTION' IN TYPEOF(arg))
OR ('PARAMETERIZED_CATALOG_DATA_INFORMATION_MIM_LF.ATAN_FUNCTION' IN TYPEOF(arg))
OR ('PARAMETERIZED_CATALOG_DATA_INFORMATION_MIM_LF.EXP_FUNCTION' IN TYPEOF(arg))
OR ('PARAMETERIZED_CATALOG_DATA_INFORMATION_MIM_LF.LOG_FUNCTION' IN TYPEOF(arg))
OR ('PARAMETERIZED_CATALOG_DATA_INFORMATION_MIM_LF.LOG2_FUNCTION' IN TYPEOF(arg))
OR ('PARAMETERIZED_CATALOG_DATA_INFORMATION_MIM_LF.LOG10_FUNCTION' IN TYPEOF(arg))
OR ('PARAMETERIZED_CATALOG_DATA_INFORMATION_MIM_LF.SQUARE_ROOT_FUNCTION'
IN TYPEOF(arg))
