c_hit : INTEGER := 0;
END_LOCAL;
REPEAT i:=1 TO HIINDEX(c_items);
c_types := c_types + ['PARAMETERIZED_CATALOG_DATA_INFORMATION_MIM_LF' + '.' + c_items[i]];
END_REPEAT;
REPEAT i:=1 TO HIINDEX(items);
IF (SIZEOF(c_types * TYPEOF(items[i])) = 1) THEN
c_hit := c_hit + 1;
END_IF;
END_REPEAT;
RETURN (SIZEOF(items) = c_hit);

LOCAL
y : BAG OF representation_item;
END_LOCAL;
IF SIZEOF(USEDIN(item,'PARAMETERIZED_CATALOG_DATA_INFORMATION_MIM_LF.REPRESENTATION.ITEMS')
* cntxt.representations_in_context) > 0 THEN
RETURN (TRUE);
ELSE y := QUERY(z <* USEDIN (item , '') |
'PARAMETERIZED_CATALOG_DATA_INFORMATION_MIM_LF.REPRESENTATION_ITEM' IN TYPEOF(z));
IF SIZEOF(y) > 0 THEN
