function.algorithm:
LOCAL
  x : SET OF representation_item;
  y : SET OF representation_item;
END_LOCAL;

x := QUERY(z <* children_set | 'PRODUCT_DATA_MANAGEMENT_MIM_LF.MAPPED_ITEM' IN TYPEOF(z));
IF SIZEOF(x) > 0 THEN
  REPEAT i := 1 TO HIINDEX(x);
    IF x[i]\mapped_item.mapping_source.mapped_representation IN parent_set THEN
      RETURN (FALSE);
    END_IF;
    IF NOT acyclic_mapped_representation(parent_set + x[i]\mapped_item.mapping_source.mapped_representation, x[i]\mapped_item.mapping_source.mapped_representation.items) THEN
      RETURN (FALSE);
    END_IF;
  END_REPEAT;
END_IF;
x := children_set - x;
IF SIZEOF(x) > 0 THEN
  REPEAT i := 1 TO HIINDEX(x);
    y := QUERY(z <* bag_to_set(USEDIN(x[i], '')) | 'PRODUCT_DATA_MANAGEMENT_MIM_LF.REPRESENTATION_ITEM' IN TYPEOF(z));
    IF NOT acyclic_mapped_representation(parent_set, y) THEN
      RETURN (FALSE);
    END_IF;
  END_REPEAT;
END_IF;
RETURN (TRUE);

LOCAL
  x : SET OF product_category_relationship;
  local_children : SET OF product_category;
END_LOCAL;

REPEAT i := 1 TO HIINDEX(children);
  IF relation.category :=: children[i] THEN
    RETURN (FALSE);
  END_IF;
END_REPEAT;
x := bag_to_set(USEDIN(relation.category, 'PRODUCT_DATA_MANAGEMENT_MIM_LF.PRODUCT_CATEGORY_RELATIONSHIP.SUB_CATEGORY'));
local_children := children + relation.category;
IF SIZEOF(x) > 0 THEN
  REPEAT i := 1 TO HIINDEX(x);
    IF NOT acyclic_product_category_relationship(x[i], local_children) THEN
      RETURN (FALSE);
    END_IF;
  END_REPEAT;
END_IF;
RETURN (TRUE);

LOCAL
  x : SET OF product_definition_relationship;
END_LOCAL;

IF relation.relating_product_definition IN relatives THEN
  RETURN (FALSE);
END_IF;
x := QUERY(pd <* bag_to_set(USEDIN(relation.relating_product_definition, 'PRODUCT_DATA_MANAGEMENT_MIM_LF.PRODUCT_DEFINITION_RELATIONSHIP.RELATED_PRODUCT_DEFINITION')) | specific_relation IN TYPEOF(pd));
REPEAT i := 1 TO HIINDEX(x);
  IF NOT acyclic_product_definition_relationship(x[i], relatives + relation.relating_product_definition, specific_relation) THEN
    RETURN (FALSE);
  END_IF;
END_REPEAT;
RETURN (TRUE);

LOCAL
  item : identification_item;
  role : identification_role;
END_LOCAL;

role := aia.role;
IF role.name = 'alias' THEN
  REPEAT i := LOINDEX(aia.items) TO HIINDEX(aia.items);
    item := aia.items[i];
    IF SIZEOF(['PRODUCT_DATA_MANAGEMENT_MIM_LF.' + 'ALIASABLE_ITEM'] * TYPEOF(item)) = 0 THEN
      RETURN (FALSE);
    END_IF;
  END_REPEAT;
  RETURN (TRUE);
ELSE
  RETURN (TRUE);
END_IF;

LOCAL
  sdr_set : SET OF shape_definition_representation := [];
  srr_set : SET OF shape_representation_relationship := [];
  sdr1_set : SET OF shape_definition_representation := [];
  pd_set : SET OF property_definition := [];
  pdr_set : SET OF product_definition_relationship := [];
  pds_set : SET OF product_definition_shape := [];
  prop_set : SET OF property_definition := [];
END_LOCAL;

pd_set := bag_to_set(USEDIN(assy.related_product_definition, 'PRODUCT_DATA_MANAGEMENT_MIM_LF.PROPERTY_DEFINITION.DEFINITION'));
pdr_set := QUERY(pdr <* bag_to_set(USEDIN(assy.related_product_definition, 'PRODUCT_DATA_MANAGEMENT_MIM_LF.PRODUCT_DEFINITION_RELATIONSHIP.RELATED_PRODUCT_DEFINITION')) | SIZEOF(USEDIN(pdr, 'PRODUCT_DATA_MANAGEMENT_MIM_LF.PROPERTY_DEFINITION.DEFINITION')) > 0);
IF SIZEOF(pd_set) > 0 THEN
  REPEAT i := 1 TO HIINDEX(pd_set);
    sdr_set := sdr_set + QUERY(pdr <* USEDIN(pd_set[i], 'PRODUCT_DATA_MANAGEMENT_MIM_LF.PROPERTY_DEFINITION_REPRESENTATION.DEFINITION') | 'PRODUCT_DATA_MANAGEMENT_MIM_LF.SHAPE_DEFINITION_REPRESENTATION' IN TYPEOF(pdr));
  END_REPEAT;
END_IF;
IF SIZEOF(pdr_set) > 0 THEN
  REPEAT i := 1 TO HIINDEX(pdr_set);
    prop_set := prop_set + bag_to_set(USEDIN(pdr_set[i], 'PRODUCT_DATA_MANAGEMENT_MIM_LF.PROPERTY_DEFINITION.DEFINITION'));
  END_REPEAT;
  IF SIZEOF(prop_set) > 0 THEN
    REPEAT i := 1 TO HIINDEX(prop_set);
      sdr_set := sdr_set + QUERY(pdr <* USEDIN(prop_set[i], 'PRODUCT_DATA_MANAGEMENT_MIM_LF.PROPERTY_DEFINITION_REPRESENTATION.DEFINITION') | 'PRODUCT_DATA_MANAGEMENT_MIM_LF.SHAPE_DEFINITION_REPRESENTATION' IN TYPEOF(pdr));
    END_REPEAT;
  END_IF;
END_IF;
IF SIZEOF(sdr_set) > 0 THEN
  REPEAT i := 1 TO HIINDEX(sdr_set);
    srr_set := QUERY(rr <* bag_to_set(USEDIN(sdr_set[i]\property_definition_representation.used_representation, 'PRODUCT_DATA_MANAGEMENT_MIM_LF.REPRESENTATION_RELATIONSHIP.REP_2')) | 'PRODUCT_DATA_MANAGEMENT_MIM_LF.SHAPE_REPRESENTATION_RELATIONSHIP' IN TYPEOF(rr));
    pd_set := bag_to_set(USEDIN(assy.relating_product_definition, 'PRODUCT_DATA_MANAGEMENT_MIM_LF.PROPERTY_DEFINITION.DEFINITION'));
    IF SIZEOF(pd_set) > 0 THEN
      REPEAT i := 1 TO HIINDEX(pd_set);
        sdr1_set := sdr1_set + QUERY(pdr <* USEDIN(pd_set[i], 'PRODUCT_DATA_MANAGEMENT_MIM_LF.PROPERTY_DEFINITION_REPRESENTATION.DEFINITION') | 'PRODUCT_DATA_MANAGEMENT_MIM_LF.SHAPE_DEFINITION_REPRESENTATION' IN TYPEOF(pdr));
      END_REPEAT;
    END_IF;
    IF (SIZEOF(sdr_set) > 0) AND (SIZEOF(sdr1_set) > 0) THEN
      IF SIZEOF(srr_set) > 0 THEN
        REPEAT j := 1 TO HIINDEX(srr_set);
          IF SIZEOF(QUERY(pdr <* bag_to_set(USEDIN(srr_set[j]\representation_relationship.rep_1, 'PRODUCT_DATA_MANAGEMENT_MIM_LF.PROPERTY_DEFINITION_REPRESENTATION.USED_REPRESENTATION')) | 'PRODUCT_DATA_MANAGEMENT_MIM_LF.SHAPE_DEFINITION_REPRESENTATION' IN TYPEOF(pdr)) * sdr1_set) >= 1 THEN
            pds_set := QUERY(x <* bag_to_set(USEDIN(assy, 'PRODUCT_DATA_MANAGEMENT_MIM_LF.PROPERTY_DEFINITION.DEFINITION')) | 'PRODUCT_DATA_MANAGEMENT_MIM_LF.PRODUCT_DEFINITION_SHAPE' IN TYPEOF(x));
            IF SIZEOF(pds_set) = 0 THEN
              RETURN (FALSE);
            END_IF;
            REPEAT k := 1 TO HIINDEX(pds_set);
              IF SIZEOF(QUERY(cdsr <* USEDIN(pds_set[k], 'PRODUCT_DATA_MANAGEMENT_MIM_LF.CONTEXT_DEPENDENT_SHAPE_REPRESENTATION.REPRESENTED_PRODUCT_RELATION') | cdsr.representation_relation :=: srr_set[j])) > 0 THEN
                RETURN (FALSE);
              END_IF;
            END_REPEAT;
          END_IF;
        END_REPEAT;
      END_IF;
    END_IF;
  END_REPEAT;
END_IF;
RETURN (TRUE);

LOCAL
  the_set : SET OF GENERIC:intype := [];
END_LOCAL;

IF SIZEOF(the_bag) > 0 THEN
  REPEAT i := 1 TO HIINDEX(the_bag);
    the_set := the_set + the_bag[i];
  END_REPEAT;
END_IF;
RETURN (the_set);

LOCAL
  u : LIST [2:3] OF direction;
  factor : REAL;
  d1 : direction;
  d2 : direction;
END_LOCAL;

IF dim = 3 THEN
  d1 := NVL(normalise(axis3), dummy_gri||direction([0.000000, 0.000000, 1.000000]));
  d2 := first_proj_axis(d1, axis1);
  u := [d2, second_proj_axis(d1, d2, axis2), d1];
ELSE
  IF EXISTS(axis1) THEN
    d1 := normalise(axis1);
    u := [d1, orthogonal_complement(d1)];
    IF EXISTS(axis2) THEN
      factor := dot_product(axis2, u[2]);
      IF factor < 0.000000 THEN
        u[2].direction_ratios[1] :=  - u[2].direction_ratios[1];
        u[2].direction_ratios[2] :=  - u[2].direction_ratios[2];
      END_IF;
    END_IF;
  ELSE
    IF EXISTS(axis2) THEN
      d1 := normalise(axis2);
      u := [orthogonal_complement(d1), d1];
      u[1].direction_ratios[1] :=  - u[1].direction_ratios[1];
      u[1].direction_ratios[2] :=  - u[1].direction_ratios[2];
    ELSE
      u := [dummy_gri||direction([1.000000, 0.000000]), dummy_gri||direction([0.000000, 1.000000])];
    END_IF;
  END_IF;
END_IF;
RETURN (u);

LOCAL
  d : direction := NVL(normalise(ref_direction), dummy_gri||direction([1.000000, 0.000000]));
END_LOCAL;

RETURN ([d, orthogonal_complement(d)]);

LOCAL
  d1 : direction;
  d2 : direction;
END_LOCAL;

d1 := NVL(normalise(axis), dummy_gri||direction([0.000000, 0.000000, 1.000000]));
d2 := first_proj_axis(d1, ref_direction);
RETURN ([d2, normalise(cross_product(d1, d2)).orientation, d1]);

LOCAL
  mag : REAL;
  res : direction;
  v1 : LIST [3:3] OF REAL;
  v2 : LIST [3:3] OF REAL;
  result : vector;
END_LOCAL;

IF NOT EXISTS(arg1) OR (arg1.dim = 2) OR NOT EXISTS(arg2) OR (arg2.dim = 2) THEN
  RETURN (?);
ELSE
  v1 := normalise(arg1).direction_ratios;
  v2 := normalise(arg2).direction_ratios;
  res := dummy_gri||direction([v1[2] * v2[3] - v1[3] * v2[2], v1[3] * v2[1] - v1[1] * v2[3], v1[1] * v2[2] - v1[2] * v2[1]]);
  mag := 0.000000;
  REPEAT i := 1 TO 3;
    mag := mag + res.direction_ratios[i] * res.direction_ratios[i];
  END_REPEAT;
  IF mag > 0.000000 THEN
    result := dummy_gri||vector(res, SQRT(mag));
  ELSE
    result := dummy_gri||vector(arg1, 0.000000);
  END_IF;
  RETURN (result);
END_IF;

LOCAL
  result : dimensional_exponents := dimensional_exponents(0.000000, 0.000000, 0.000000, 0.000000, 0.000000, 0.000000, 0.000000);
END_LOCAL;

IF 'PRODUCT_DATA_MANAGEMENT_MIM_LF.DERIVED_UNIT' IN TYPEOF(x) THEN
  REPEAT i := LOINDEX(x.elements) TO HIINDEX(x.elements);
    result.length_exponent := result.length_exponent + x.elements[i].exponent * x.elements[i].unit.dimensions.length_exponent;
    result.mass_exponent := result.mass_exponent + x.elements[i].exponent * x.elements[i].unit.dimensions.mass_exponent;
    result.time_exponent := result.time_exponent + x.elements[i].exponent * x.elements[i].unit.dimensions.time_exponent;
    result.electric_current_exponent := result.electric_current_exponent + x.elements[i].exponent * x.elements[i].unit.dimensions.electric_current_exponent;
    result.thermodynamic_temperature_exponent := result.thermodynamic_temperature_exponent + x.elements[i].exponent * x.elements[i].unit.dimensions.thermodynamic_temperature_exponent;
    result.amount_of_substance_exponent := result.amount_of_substance_exponent + x.elements[i].exponent * x.elements[i].unit.dimensions.amount_of_substance_exponent;
    result.luminous_intensity_exponent := result.luminous_intensity_exponent + x.elements[i].exponent * x.elements[i].unit.dimensions.luminous_intensity_exponent;
  END_REPEAT;
ELSE
  result := x.dimensions;
END_IF;
RETURN (result);

LOCAL
  x : SET OF representation;
  y : representation_context;
  dim : dimension_count;
END_LOCAL;

IF 'PRODUCT_DATA_MANAGEMENT_MIM_LF.CARTESIAN_POINT' IN TYPEOF(item) THEN
  dim := SIZEOF(item\cartesian_point.coordinates);
  RETURN (dim);
END_IF;
IF 'PRODUCT_DATA_MANAGEMENT_MIM_LF.DIRECTION' IN TYPEOF(item) THEN
  dim := SIZEOF(item\direction.direction_ratios);
  RETURN (dim);
END_IF;
IF 'PRODUCT_DATA_MANAGEMENT_MIM_LF.VECTOR' IN TYPEOF(item) THEN
  dim := SIZEOF(item\vector.orientation\direction.direction_ratios);
  RETURN (dim);
END_IF;
x := using_representations(item);
y := x[1].context_of_items;
dim := y\geometric_representation_context.coordinate_space_dimension;
RETURN (dim);

LOCAL
  scalar : REAL;
  vec1 : direction;
  vec2 : direction;
  ndim : INTEGER;
END_LOCAL;

IF NOT EXISTS(arg1) OR NOT EXISTS(arg2) THEN
  scalar := ?;
ELSE
  IF arg1.dim <> arg2.dim THEN
    scalar := ?;
  ELSE
    vec1 := normalise(arg1);
    vec2 := normalise(arg2);
    ndim := arg1.dim;
    scalar := 0.000000;
    REPEAT i := 1 TO ndim;
      scalar := scalar + vec1.direction_ratios[i] * vec2.direction_ratios[i];
    END_REPEAT;
  END_IF;
END_IF;
RETURN (scalar);

LOCAL
  x_axis : direction;
  v : direction;
  z : direction;
  x_vec : vector;
END_LOCAL;

IF NOT EXISTS(z_axis) THEN
  RETURN (?);
ELSE
  z := normalise(z_axis);
  IF NOT EXISTS(arg) THEN
    IF (z.direction_ratios <> [1.000000, 0.000000, 0.000000]) AND (z.direction_ratios <> [ - 1.000000, 0.000000, 0.000000]) THEN
      v := dummy_gri||direction([1.000000, 0.000000, 0.000000]);
    ELSE
      v := dummy_gri||direction([0.000000, 1.000000, 0.000000]);
    END_IF;
  ELSE
    IF arg.dim <> 3 THEN
      RETURN (?);
    END_IF;
    IF cross_product(arg, z).magnitude = 0.000000 THEN
      RETURN (?);
    ELSE
      v := normalise(arg);
    END_IF;
  END_IF;
  x_vec := scalar_times_vector(dot_product(v, z), z);
  x_axis := vector_difference(v, x_vec).orientation;
  x_axis := normalise(x_axis);
END_IF;
RETURN (x_axis);

LOCAL
  description_bag : BAG OF description_attribute := USEDIN(obj, 'PRODUCT_DATA_MANAGEMENT_MIM_LF.DESCRIPTION_ATTRIBUTE.DESCRIBED_ITEM');
END_LOCAL;

IF SIZEOF(description_bag) = 1 THEN
  RETURN (description_bag[1].attribute_value);
ELSE
  RETURN (?);
END_IF;

LOCAL
  id_bag : BAG OF id_attribute := USEDIN(obj, 'PRODUCT_DATA_MANAGEMENT_MIM_LF.ID_ATTRIBUTE.IDENTIFIED_ITEM');
END_LOCAL;

IF SIZEOF(id_bag) = 1 THEN
  RETURN (id_bag[1].attribute_value);
ELSE
  RETURN (?);
END_IF;

LOCAL
  name_bag : BAG OF name_attribute := USEDIN(obj, 'PRODUCT_DATA_MANAGEMENT_MIM_LF.NAME_ATTRIBUTE.NAMED_ITEM');
END_LOCAL;

IF SIZEOF(name_bag) = 1 THEN
  RETURN (name_bag[1].attribute_value);
ELSE
  RETURN (?);
END_IF;

LOCAL
  role_bag : BAG OF role_association := USEDIN(obj, 'PRODUCT_DATA_MANAGEMENT_MIM_LF.ROLE_ASSOCIATION.ITEM_WITH_ROLE');
END_LOCAL;

IF SIZEOF(role_bag) = 1 THEN
  RETURN (role_bag[1].role);
ELSE
  RETURN (?);
END_IF;

LOCAL
  initial_context : product_definition_context;
  context_associations : SET OF product_definition_context_association;
END_LOCAL;

initial_context := pd.frame_of_reference;
context_associations := QUERY(pdca <* bag_to_set(USEDIN(pd, 'PRODUCT_DATA_MANAGEMENT_MIM_LF.PRODUCT_DEFINITION_CONTEXT_ASSOCIATION.DEFINITION')) | (pdca.role.name = 'additional context') AND (pdca.frame_of_reference :=: initial_context));
RETURN (SIZEOF(context_associations) > 0);

LOCAL
  y : BAG OF representation_item;
END_LOCAL;

IF SIZEOF(USEDIN(item, 'PRODUCT_DATA_MANAGEMENT_MIM_LF.REPRESENTATION.ITEMS') * cntxt.representations_in_context) > 0 THEN
  RETURN (TRUE);
ELSE
  y := QUERY(z <* USEDIN(item, '') | 'PRODUCT_DATA_MANAGEMENT_MIM_LF.REPRESENTATION_ITEM' IN TYPEOF(z));
  IF SIZEOF(y) > 0 THEN
    REPEAT i := 1 TO HIINDEX(y);
      IF item_in_context(y[i], cntxt) THEN
        RETURN (TRUE);
      END_IF;
    END_REPEAT;
  END_IF;
END_IF;
RETURN (FALSE);

IF (year MOD 4 = 0) AND (year MOD 100 <> 0) OR (year MOD 400 = 0) THEN
  RETURN (TRUE);
ELSE
  RETURN (FALSE);
END_IF;

LOCAL
  ndim : INTEGER;
  v : direction;
  result : vector_or_direction;
  vec : vector;
  mag : REAL;
END_LOCAL;

IF NOT EXISTS(arg) THEN
  result := ?;
ELSE
  ndim := arg.dim;
  IF 'PRODUCT_DATA_MANAGEMENT_MIM_LF.VECTOR' IN TYPEOF(arg) THEN
    v := dummy_gri||direction(arg.orientation.direction_ratios);
    IF arg.magnitude = 0.000000 THEN
      RETURN (?);
    ELSE
      vec := dummy_gri||vector(v, 1.000000);
    END_IF;
  ELSE
    v := dummy_gri||direction(arg.direction_ratios);
  END_IF;
  mag := 0.000000;
  REPEAT i := 1 TO ndim;
    mag := mag + v.direction_ratios[i] * v.direction_ratios[i];
  END_REPEAT;
  IF mag > 0.000000 THEN
    mag := SQRT(mag);
    REPEAT i := 1 TO ndim;
      v.direction_ratios[i] := v.direction_ratios[i] / mag;
    END_REPEAT;
    IF 'PRODUCT_DATA_MANAGEMENT_MIM_LF.VECTOR' IN TYPEOF(arg) THEN
      vec.orientation := v;
      result := vec;
    ELSE
      result := v;
    END_IF;
  ELSE
    RETURN (?);
  END_IF;
END_IF;
RETURN (result);

LOCAL
  result : direction;
END_LOCAL;

IF (vec.dim <> 2) OR NOT EXISTS(vec) THEN
  RETURN (?);
ELSE
  result := dummy_gri||direction([ - vec.direction_ratios[2], vec.direction_ratios[1]]);
  RETURN (result);
END_IF;

LOCAL
  v : direction;
  mag : REAL;
  result : vector;
END_LOCAL;

IF NOT EXISTS(scalar) OR NOT EXISTS(vec) THEN
  RETURN (?);
ELSE
  IF 'PRODUCT_DATA_MANAGEMENT_MIM_LF.VECTOR' IN TYPEOF(vec) THEN
    v := dummy_gri||direction(vec.orientation.direction_ratios);
    mag := scalar * vec.magnitude;
  ELSE
    v := dummy_gri||direction(vec.direction_ratios);
    mag := scalar;
  END_IF;
  IF mag < 0.000000 THEN
    REPEAT i := 1 TO SIZEOF(v.direction_ratios);
      v.direction_ratios[i] :=  - v.direction_ratios[i];
    END_REPEAT;
    mag :=  - mag;
  END_IF;
  result := dummy_gri||vector(normalise(v), mag);
END_IF;
RETURN (result);

LOCAL
  y_axis : vector;
  v : direction;
  temp : vector;
END_LOCAL;

IF NOT EXISTS(arg) THEN
  v := dummy_gri||direction([0.000000, 1.000000, 0.000000]);
ELSE
  v := arg;
END_IF;
temp := scalar_times_vector(dot_product(v, z_axis), z_axis);
y_axis := vector_difference(v, temp);
temp := scalar_times_vector(dot_product(v, x_axis), x_axis);
y_axis := vector_difference(y_axis, temp);
y_axis := normalise(y_axis);
RETURN (y_axis.orientation);

IF NOT EXISTS(the_type) OR NOT ({0 <= criterion <= 3}) OR (SIZEOF(sub_names) = 0) THEN
  RETURN (UNKNOWN);
ELSE
  CASE criterion OF 
    0:
      RETURN (SIZEOF(sub_names * TYPEOF(the_type)) > 0);
    1:
      RETURN (SIZEOF(sub_names * TYPEOF(the_type)) = 0);
    2:
      RETURN (SIZEOF(sub_names * TYPEOF(the_type)) = 1);
    3:
      RETURN (SIZEOF(sub_names * TYPEOF(the_type)) <= 1);
  END_CASE;
END_IF;

LOCAL
  new_check_items : SET OF founded_item_select;
  result_items : SET OF founded_item_select;
  next_items : SET OF founded_item_select;
END_LOCAL;

result_items := [];
new_check_items := checked_items + item;
next_items := QUERY(z <* bag_to_set(USEDIN(item, '')) | ('PRODUCT_DATA_MANAGEMENT_MIM_LF.REPRESENTATION_ITEM' IN TYPEOF(z)) OR ('PRODUCT_DATA_MANAGEMENT_MIM_LF.FOUNDED_ITEM' IN TYPEOF(z)));
IF SIZEOF(next_items) > 0 THEN
  REPEAT i := 1 TO HIINDEX(next_items);
    IF NOT (next_items[i] IN new_check_items) THEN
      result_items := result_items + next_items[i] + using_items(next_items[i], new_check_items);
    END_IF;
  END_REPEAT;
END_IF;
RETURN (result_items);

LOCAL
  results : SET OF representation;
  result_bag : BAG OF representation;
  intermediate_items : SET OF founded_item_select;
END_LOCAL;

results := [];
result_bag := USEDIN(item, 'PRODUCT_DATA_MANAGEMENT_MIM_LF.REPRESENTATION.ITEMS');
IF SIZEOF(result_bag) > 0 THEN
  REPEAT i := 1 TO HIINDEX(result_bag);
    results := results + result_bag[i];
  END_REPEAT;
END_IF;
intermediate_items := using_items(item, []);
IF SIZEOF(intermediate_items) > 0 THEN
  REPEAT i := 1 TO HIINDEX(intermediate_items);
    result_bag := USEDIN(intermediate_items[i], 'PRODUCT_DATA_MANAGEMENT_MIM_LF.REPRESENTATION.ITEMS');
    IF SIZEOF(result_bag) > 0 THEN
      REPEAT j := 1 TO HIINDEX(result_bag);
        results := results + result_bag[j];
      END_REPEAT;
    END_IF;
  END_REPEAT;
END_IF;
RETURN (results);

CASE date.month_component OF 
  1:
    RETURN ({1 <= date.day_component <= 31});
  2:
    BEGIN
      IF leap_year(date.year_component) THEN
        RETURN ({1 <= date.day_component <= 29});
      ELSE
        RETURN ({1 <= date.day_component <= 28});
      END_IF;
    END;
  3:
    RETURN ({1 <= date.day_component <= 31});
  4:
    RETURN ({1 <= date.day_component <= 30});
  5:
    RETURN ({1 <= date.day_component <= 31});
  6:
    RETURN ({1 <= date.day_component <= 30});
  7:
    RETURN ({1 <= date.day_component <= 31});
  8:
    RETURN ({1 <= date.day_component <= 31});
  9:
    RETURN ({1 <= date.day_component <= 30});
  10:
    RETURN ({1 <= date.day_component <= 31});
  11:
    RETURN ({1 <= date.day_component <= 30});
  12:
    RETURN ({1 <= date.day_component <= 31});
END_CASE;
RETURN (FALSE);

IF 'REAL' IN TYPEOF(m) THEN
  RETURN (m > 0.000000);
ELSE
  IF 'INTEGER' IN TYPEOF(m) THEN
    RETURN (m > 0);
  ELSE
    RETURN (TRUE);
  END_IF;
END_IF;

IF EXISTS(time.second_component) THEN
  RETURN (EXISTS(time.minute_component));
ELSE
  RETURN (TRUE);
END_IF;

IF 'PRODUCT_DATA_MANAGEMENT_MIM_LF.LENGTH_MEASURE' IN TYPEOF(m.value_component) THEN
  IF derive_dimensional_exponents(m.unit_component) <> dimensional_exponents(1.000000, 0.000000, 0.000000, 0.000000, 0.000000, 0.000000, 0.000000) THEN
    RETURN (FALSE);
  END_IF;
END_IF;
IF 'PRODUCT_DATA_MANAGEMENT_MIM_LF.MASS_MEASURE' IN TYPEOF(m.value_component) THEN
  IF derive_dimensional_exponents(m.unit_component) <> dimensional_exponents(0.000000, 1.000000, 0.000000, 0.000000, 0.000000, 0.000000, 0.000000) THEN
    RETURN (FALSE);
  END_IF;
END_IF;
IF 'PRODUCT_DATA_MANAGEMENT_MIM_LF.TIME_MEASURE' IN TYPEOF(m.value_component) THEN
  IF derive_dimensional_exponents(m.unit_component) <> dimensional_exponents(0.000000, 0.000000, 1.000000, 0.000000, 0.000000, 0.000000, 0.000000) THEN
    RETURN (FALSE);
  END_IF;
END_IF;
IF 'PRODUCT_DATA_MANAGEMENT_MIM_LF.ELECTRIC_CURRENT_MEASURE' IN TYPEOF(m.value_component) THEN
