IF (SIZEOF(agg) = 2) AND ((SIZEOF(QUERY(i1 <* agg | 'PRODUCT_DATA_MANAGEMENT_MIM_LF.MEASURE_REPRESENTATION_ITEM' IN TYPEOF(i1))) = 2) OR (SIZEOF(QUERY(i2 <* agg | 'PRODUCT_DATA_MANAGEMENT_MIM_LF.VALUE_REPRESENTATION_ITEM' IN TYPEOF(i2))) = 2)) THEN
  RETURN (TRUE);
ELSE
  RETURN (FALSE);
END_IF;

IF (SIZEOF(QUERY(i <* agg | i.name = 'upper limit')) = 1) AND (SIZEOF(QUERY(i <* agg | i.name = 'lower limit')) = 1) THEN
  RETURN (TRUE);
ELSE
  RETURN (FALSE);
END_IF;

IF SIZEOF(QUERY(i1 <* agg | ('PRODUCT_DATA_MANAGEMENT_MIM_LF.MEASURE_REPRESENTATION_ITEM' IN TYPEOF(i1)) AND (SIZEOF(QUERY(i2 <* agg | ('PRODUCT_DATA_MANAGEMENT_MIM_LF.MEASURE_REPRESENTATION_ITEM' IN TYPEOF(i2)) AND (i1 :<>: i2) AND (i1\measure_with_unit.unit_component :=: i2\measure_with_unit.unit_component))) = 1))) = 2 THEN
  RETURN (TRUE);
ELSE
  RETURN (FALSE);
END_IF;

LOCAL
  result : vector;
  res : direction;
  vec1 : direction;
  vec2 : direction;
  mag : REAL;
  mag1 : REAL;
  mag2 : REAL;
  ndim : INTEGER;
END_LOCAL;

IF NOT EXISTS(arg1) OR NOT EXISTS(arg2) OR (arg1.dim <> arg2.dim) THEN
  RETURN (?);
ELSE
  IF 'PRODUCT_DATA_MANAGEMENT_MIM_LF.VECTOR' IN TYPEOF(arg1) THEN
    mag1 := arg1.magnitude;
    vec1 := arg1.orientation;
  ELSE
    mag1 := 1.000000;
    vec1 := arg1;
  END_IF;
  IF 'PRODUCT_DATA_MANAGEMENT_MIM_LF.VECTOR' IN TYPEOF(arg2) THEN
    mag2 := arg2.magnitude;
    vec2 := arg2.orientation;
  ELSE
    mag2 := 1.000000;
    vec2 := arg2;
  END_IF;
  vec1 := normalise(vec1);
  vec2 := normalise(vec2);
  ndim := SIZEOF(vec1.direction_ratios);
  mag := 0.000000;
  res := dummy_gri||direction(vec1.direction_ratios);
  REPEAT i := 1 TO ndim;
    res.direction_ratios[i] := mag1 * vec1.direction_ratios[i] + mag2 * vec2.direction_ratios[i];
    mag := mag + res.direction_ratios[i] * res.direction_ratios[i];
  END_REPEAT;
  IF mag > 0.000000 THEN
    result := dummy_gri||vector(res, SQRT(mag));
  ELSE
    result := dummy_gri||vector(vec1, 0.000000);
  END_IF;
END_IF;
RETURN (result);

LOCAL
  item : identification_item;
  role : identification_role;
END_LOCAL;

role := aia.role;
IF role.name = 'version' THEN
  REPEAT i := LOINDEX(aia.items) TO HIINDEX(aia.items);
    item := aia.items[i];
    IF SIZEOF(['PRODUCT_DATA_MANAGEMENT_MIM_LF.' + 'VERSIONABLE_ITEM'] * TYPEOF(item)) = 0 THEN
      RETURN (FALSE);
    END_IF;
  END_REPEAT;
  RETURN (TRUE);
ELSE
  RETURN (TRUE);
END_IF;

rule.algorithm:
LOCAL
  document_definitions : SET OF product_definition := [];
END_LOCAL;

document_definitions := QUERY(pd <* product_definition | pd.frame_of_reference.name IN ['digital document definition', 'physical document definition']);

LOCAL
  part_definitions : SET OF product_definition := [];
END_LOCAL;

part_definitions := QUERY(pd <* product_definition | pd.frame_of_reference.name = 'part definition');
