function.algorithm:
IF ('REAL' IN TYPEOF(m)) THEN
  RETURN (m > 0.000000);
ELSE
  IF ('INTEGER' IN TYPEOF(m)) THEN
    RETURN (m > 0);
  ELSE
    RETURN (TRUE);
  END_IF;
END_IF;

LOCAL
  i : INTEGER;
END_LOCAL;

IF 'SYSTEM_BEHAVIOUR_MIM_LF.SIMPLE_NUMERIC_EXPRESSION' IN TYPEOF(arg) THEN
  RETURN (TRUE);
END_IF;
IF 'SYSTEM_BEHAVIOUR_MIM_LF.SQL_MAPPABLE_DEFINED_FUNCTION' IN TYPEOF(arg) THEN
  RETURN (TRUE);
END_IF;
IF 'SYSTEM_BEHAVIOUR_MIM_LF.MINUS_FUNCTION' IN TYPEOF(arg) THEN
  RETURN (is_SQL_mappable(arg\unary_numeric_expression.operand));
END_IF;
IF ('SYSTEM_BEHAVIOUR_MIM_LF.ABS_FUNCTION' IN TYPEOF(arg)) OR ('SYSTEM_BEHAVIOUR_MIM_LF.SIN_FUNCTION' IN TYPEOF(arg)) OR ('SYSTEM_BEHAVIOUR_MIM_LF.COS_FUNCTION' IN TYPEOF(arg)) OR ('SYSTEM_BEHAVIOUR_MIM_LF.TAN_FUNCTION' IN TYPEOF(arg)) OR ('SYSTEM_BEHAVIOUR_MIM_LF.ASIN_FUNCTION' IN TYPEOF(arg)) OR ('SYSTEM_BEHAVIOUR_MIM_LF.ACOS_FUNCTION' IN TYPEOF(arg)) OR ('SYSTEM_BEHAVIOUR_MIM_LF.ATAN_FUNCTION' IN TYPEOF(arg)) OR ('SYSTEM_BEHAVIOUR_MIM_LF.EXP_FUNCTION' IN TYPEOF(arg)) OR ('SYSTEM_BEHAVIOUR_MIM_LF.LOG_FUNCTION' IN TYPEOF(arg)) OR ('SYSTEM_BEHAVIOUR_MIM_LF.LOG2_FUNCTION' IN TYPEOF(arg)) OR ('SYSTEM_BEHAVIOUR_MIM_LF.LOG10_FUNCTION' IN TYPEOF(arg)) OR ('SYSTEM_BEHAVIOUR_MIM_LF.SQUARE_ROOT_FUNCTION' IN TYPEOF(arg)) OR ('SYSTEM_BEHAVIOUR_MIM_LF.VALUE_FUNCTION' IN TYPEOF(arg)) OR ('SYSTEM_BEHAVIOUR_MIM_LF.LENGTH_FUNCTION' IN TYPEOF(arg)) THEN
  RETURN (FALSE);
END_IF;
IF ('SYSTEM_BEHAVIOUR_MIM_LF.PLUS_EXPRESSION' IN TYPEOF(arg)) OR ('SYSTEM_BEHAVIOUR_MIM_LF.MULT_EXPRESSION' IN TYPEOF(arg)) OR ('SYSTEM_BEHAVIOUR_MIM_LF.MAXIMUM_FUNCTION' IN TYPEOF(arg)) OR ('SYSTEM_BEHAVIOUR_MIM_LF.MINIMUM_FUNCTION' IN TYPEOF(arg)) THEN
  REPEAT i := 1 TO SIZEOF(arg\multiple_arity_numeric_expression.operands);
    IF NOT is_SQL_mappable(arg\multiple_arity_numeric_expression.operands[i]) THEN
      RETURN (FALSE);
    END_IF;
  END_REPEAT;
  RETURN (TRUE);
END_IF;
IF ('SYSTEM_BEHAVIOUR_MIM_LF.MINUS_EXPRESSION' IN TYPEOF(arg)) OR ('SYSTEM_BEHAVIOUR_MIM_LF.SLASH_EXPRESSION' IN TYPEOF(arg)) THEN
  RETURN (is_SQL_mappable(arg\binary_numeric_expression.operands[1]) AND is_SQL_mappable(arg\binary_numeric_expression.operands[2]));
END_IF;
IF ('SYSTEM_BEHAVIOUR_MIM_LF.DIV_EXPRESSION' IN TYPEOF(arg)) OR ('SYSTEM_BEHAVIOUR_MIM_LF.MOD_EXPRESSION' IN TYPEOF(arg)) OR ('SYSTEM_BEHAVIOUR_MIM_LF.POWER_EXPRESSION' IN TYPEOF(arg)) THEN
  RETURN (FALSE);
END_IF;
IF 'SYSTEM_BEHAVIOUR_MIM_LF.SIMPLE_BOOLEAN_EXPRESSION' IN TYPEOF(arg) THEN
  RETURN (TRUE);
END_IF;
IF 'SYSTEM_BEHAVIOUR_MIM_LF.NOT_EXPRESSION' IN TYPEOF(arg) THEN
  RETURN (is_SQL_mappable(arg\unary_generic_expression.operand));
END_IF;
IF ('SYSTEM_BEHAVIOUR_MIM_LF.ODD_FUNCTION' IN TYPEOF(arg)) OR ('SYSTEM_BEHAVIOUR_MIM_LF.XOR_EXPRESSION' IN TYPEOF(arg)) THEN
  RETURN (FALSE);
END_IF;
IF ('SYSTEM_BEHAVIOUR_MIM_LF.AND_EXPRESSION' IN TYPEOF(arg)) OR ('SYSTEM_BEHAVIOUR_MIM_LF.OR_EXPRESSION' IN TYPEOF(arg)) THEN
  REPEAT i := 1 TO SIZEOF(arg\multiple_arity_boolean_expression.operands);
    IF NOT is_SQL_mappable(arg\multiple_arity_boolean_expression.operands[i]) THEN
      RETURN (FALSE);
    END_IF;
  END_REPEAT;
  RETURN (TRUE);
END_IF;
IF 'SYSTEM_BEHAVIOUR_MIM_LF.EQUALS_EXPRESSION' IN TYPEOF(arg) THEN
  RETURN (is_SQL_mappable(arg\binary_generic_expression.operands[1]) AND is_SQL_mappable(arg\binary_generic_expression.operands[2]));
END_IF;
IF ('SYSTEM_BEHAVIOUR_MIM_LF.COMPARISON_EQUAL' IN TYPEOF(arg)) OR ('SYSTEM_BEHAVIOUR_MIM_LF.COMPARISON_GREATER' IN TYPEOF(arg)) OR ('SYSTEM_BEHAVIOUR_MIM_LF.COMPARISON_GREATER_EQUAL' IN TYPEOF(arg)) OR ('SYSTEM_BEHAVIOUR_MIM_LF.COMPARISON_LESS' IN TYPEOF(arg)) OR ('SYSTEM_BEHAVIOUR_MIM_LF.COMPARISON_LESS_EQUAL' IN TYPEOF(arg)) OR ('SYSTEM_BEHAVIOUR_MIM_LF.COMPARISON_NOT_EQUAL' IN TYPEOF(arg)) OR ('SYSTEM_BEHAVIOUR_MIM_LF.LIKE_EXPRESSION' IN TYPEOF(arg)) THEN
  RETURN (is_SQL_mappable(arg\comparison_expression.operands[1]) AND is_SQL_mappable(arg\comparison_expression.operands[2]));
END_IF;
IF 'SYSTEM_BEHAVIOUR_MIM_LF.INTERVAL_EXPRESSION' IN TYPEOF(arg) THEN
  RETURN (is_SQL_mappable(arg\interval_expression.interval_low) AND is_SQL_mappable(arg\interval_expression.interval_high) AND is_SQL_mappable(arg\interval_expression.interval_item));
END_IF;
IF ('SYSTEM_BEHAVIOUR_MIM_LF.NUMERIC_DEFINED_FUNCTION' IN TYPEOF(arg)) OR ('SYSTEM_BEHAVIOUR_MIM_LF.BOOLEAN_DEFINED_FUNCTION' IN TYPEOF(arg)) OR ('SYSTEM_BEHAVIOUR_MIM_LF.STRING_DEFINED_FUNCTION' IN TYPEOF(arg)) THEN
  RETURN (FALSE);
END_IF;
IF 'SYSTEM_BEHAVIOUR_MIM_LF.SIMPLE_STRING_EXPRESSION' IN TYPEOF(arg) THEN
  RETURN (TRUE);
END_IF;
IF ('SYSTEM_BEHAVIOUR_MIM_LF.INDEX_EXPRESSION' IN TYPEOF(arg)) OR ('SYSTEM_BEHAVIOUR_MIM_LF.SUBSTRING_EXPRESSION' IN TYPEOF(arg)) OR ('SYSTEM_BEHAVIOUR_MIM_LF.CONCAT_EXPRESSION' IN TYPEOF(arg)) OR ('SYSTEM_BEHAVIOUR_MIM_LF.FORMAT_FUNCTION' IN TYPEOF(arg)) THEN
  RETURN (FALSE);
END_IF;
RETURN (FALSE);

LOCAL
  id_bag : BAG OF id_attribute := (USEDIN(obj, 'SYSTEM_BEHAVIOUR_MIM_LF.' + 'ID_ATTRIBUTE.' + 'IDENTIFIED_ITEM'));
END_LOCAL;

IF SIZEOF(id_bag) = 1 THEN
  RETURN (id_bag[1].attribute_value);
ELSE
  RETURN (?);
END_IF;

LOCAL
  description_bag : BAG OF description_attribute := (USEDIN(obj, 'SYSTEM_BEHAVIOUR_MIM_LF.' + 'DESCRIPTION_ATTRIBUTE.' + 'DESCRIBED_ITEM'));
END_LOCAL;

IF SIZEOF(description_bag) = 1 THEN
  RETURN (description_bag[1].attribute_value);
ELSE
  RETURN (?);
END_IF;

LOCAL
  name_bag : BAG OF name_attribute := (USEDIN(obj, 'SYSTEM_BEHAVIOUR_MIM_LF.' + 'NAME_ATTRIBUTE.' + 'NAMED_ITEM'));
END_LOCAL;

IF SIZEOF(name_bag) = 1 THEN
  RETURN (name_bag[1].attribute_value);
ELSE
  RETURN (?);
END_IF;

LOCAL
  role_bag : BAG OF role_association := (USEDIN(obj, 'SYSTEM_BEHAVIOUR_MIM_LF.' + 'ROLE_ASSOCIATION.' + 'ITEM_WITH_ROLE'));
END_LOCAL;

IF SIZEOF(role_bag) = 1 THEN
  RETURN (role_bag[1].role);
ELSE
  RETURN (?);
END_IF;

LOCAL
  results : SET OF representation;
  result_bag : BAG OF representation;
  intermediate_items : SET OF founded_item_select;
END_LOCAL;

results := [];
result_bag := USEDIN(item, 'SYSTEM_BEHAVIOUR_MIM_LF.REPRESENTATION.ITEMS');
IF SIZEOF(result_bag) > 0 THEN
  REPEAT i := 1 TO HIINDEX(result_bag);
    results := results + result_bag[i];
  END_REPEAT;
END_IF;
intermediate_items := using_items(item, []);
IF SIZEOF(intermediate_items) > 0 THEN
  REPEAT i := 1 TO HIINDEX(intermediate_items);
    result_bag := USEDIN(intermediate_items[i], 'SYSTEM_BEHAVIOUR_MIM_LF.REPRESENTATION.ITEMS');
    IF SIZEOF(result_bag) > 0 THEN
      REPEAT j := 1 TO HIINDEX(result_bag);
        results := results + result_bag[j];
      END_REPEAT;
    END_IF;
  END_REPEAT;
END_IF;
RETURN (results);

LOCAL
  new_check_items : SET OF founded_item_select;
  result_items : SET OF founded_item_select;
  next_items : SET OF founded_item_select;
END_LOCAL;

result_items := [];
new_check_items := checked_items + item;
next_items := QUERY(z <* bag_to_set(USEDIN(item, '')) | ('SYSTEM_BEHAVIOUR_MIM_LF.REPRESENTATION_ITEM' IN TYPEOF(z)) OR ('SYSTEM_BEHAVIOUR_MIM_LF.FOUNDED_ITEM' IN TYPEOF(z)));
IF SIZEOF(next_items) > 0 THEN
  REPEAT i := 1 TO HIINDEX(next_items);
    IF NOT (next_items[i] IN new_check_items) THEN
      result_items := result_items + next_items[i] + using_items(next_items[i], new_check_items);
    END_IF;
  END_REPEAT;
END_IF;
RETURN (result_items);

LOCAL
  the_set : SET OF GENERIC:intype := [];
END_LOCAL;

IF SIZEOF(the_bag) > 0 THEN
  REPEAT i := 1 TO HIINDEX(the_bag);
    the_set := the_set + the_bag[i];
  END_REPEAT;
END_IF;
RETURN (the_set);

LOCAL
  i : INTEGER := 0;
END_LOCAL;

