END_IF;
IF ('SYSTEM_BEHAVIOUR_MIM_LF.DIV_EXPRESSION' IN TYPEOF(arg)) OR ('SYSTEM_BEHAVIOUR_MIM_LF.MOD_EXPRESSION' IN TYPEOF(arg)) THEN
  RETURN (TRUE);
END_IF;
IF 'SYSTEM_BEHAVIOUR_MIM_LF.SLASH_EXPRESSION' IN TYPEOF(arg) THEN
  RETURN (FALSE);
END_IF;
IF 'SYSTEM_BEHAVIOUR_MIM_LF.LENGTH_FUNCTION' IN TYPEOF(arg) THEN
  RETURN (TRUE);
END_IF;
IF 'SYSTEM_BEHAVIOUR_MIM_LF.VALUE_FUNCTION' IN TYPEOF(arg) THEN
  IF 'SYSTEM_BEHAVIOUR_MIM_LF.INT_VALUE_FUNCTION' IN TYPEOF(arg) THEN
    RETURN (TRUE);
  ELSE
    RETURN (FALSE);
  END_IF;
END_IF;
IF 'SYSTEM_BEHAVIOUR_MIM_LF.INTEGER_DEFINED_FUNCTION' IN TYPEOF(arg) THEN
  RETURN (TRUE);
END_IF;
IF 'SYSTEM_BEHAVIOUR_MIM_LF.REAL_DEFINED_FUNCTION' IN TYPEOF(arg) THEN
