  RETURN (FALSE);
END_IF;
IF 'SYSTEM_BEHAVIOUR_MIM_LF.STRING_DEFINED_FUNCTION' IN TYPEOF(arg) THEN
  RETURN (FALSE);
END_IF;
RETURN (FALSE);

LOCAL
  result : BOOLEAN := TRUE;
END_LOCAL;

IF ('SYSTEM_BEHAVIOUR_MIM_LF.SIMPLE_GENERIC_EXPRESSION' IN TYPEOF(arg1)) THEN
  RETURN (TRUE);
END_IF;
IF arg1 IN arg2 THEN
  RETURN (FALSE);
END_IF;
IF 'SYSTEM_BEHAVIOUR_MIM_LF.UNARY_GENERIC_EXPRESSION' IN TYPEOF(arg1) THEN
  RETURN (acyclic(arg1\unary_generic_expression.operand, arg2 + [arg1]));
END_IF;
IF 'SYSTEM_BEHAVIOUR_MIM_LF.BINARY_GENERIC_EXPRESSION' IN TYPEOF(arg1) THEN
  RETURN (acyclic(arg1\binary_generic_expression.operands[1], arg2 + [arg1]) AND acyclic(arg1\binary_generic_expression.operands[2], arg2 + [arg1]));
END_IF;
IF 'SYSTEM_BEHAVIOUR_MIM_LF.MULTIPLE_ARITY_GENERIC_EXPRESSION' IN TYPEOF(arg1) THEN
  result := TRUE;
  REPEAT i := 1 TO SIZEOF(arg1\multiple_arity_generic_expression.operands);
    result := result AND acyclic(arg1\multiple_arity_generic_expression.operands[i], arg2 + [arg1]);
  END_REPEAT;
  RETURN (result);
END_IF;
RETURN (result);

