#1421 = CIRCLE(’Circ3’,#1420, 50.0);
#1422 = EDGE_CURVE(’EdgePQ’, #1401, #1403, #1412, .T.);
#1423 = EDGE_CURVE(’EdgePU’, #1401, #1411, #1413, .T.);
#1424 = EDGE_CURVE(’EdgeRS’, #1405, #1407, #1414, .T.);
#1425 = EDGE_CURVE(’EdgeUT’, #1411, #1409, #1415, .T.);
