#1438 = EDGE_LOOP(’ELQRST’, (#1433, #1431, #1434, #1436));
#1439 = FACE_OUTER_BOUND(’SubCylFac2Bd’, #1438, .T.);
#1440 = FACE_OUTER_BOUND(’SubFrontBd’, #1437, .T.);
#1441 = SUBFACE(’SubCylF2’, (#1439), #1327);
