#17= (GEOMETRIC_REPRESENTATION_CONTEXT(3)
         GLOBAL_UNIT_ASSIGNED_CONTEXT((#15,#16))
         REPRESENTATION_CONTEXT('',''));
#20= PRODUCT_DEFINITION_SHAPE('',$,#1);
#21= ID_ATTRIBUTE('',#20);
#22= SHAPE_DEFINITION_REPRESENTATION(#20,#12);
#23= DESCRIPTION_ATTRIBUTE('',#22);
