function.algorithm:
IF SIZEOF (['AIC_MANIFOLD_SURFACE.BOUNDED_CURVE', 'AIC_MANIFOLD_SURFACE.CONIC', 'AIC_MANIFOLD_SURFACE.CURVE_REPLICA', 'AIC_MANIFOLD_SURFACE.LINE', 'AIC_MANIFOLD_SURFACE.OFFSET_CURVE_3D'] * TYPEOF(cv)) > 1 THEN RETURN(FALSE); END_IF;
(* b_spline_curves shall not self-intersect *) IF (('AIC_MANIFOLD_SURFACE.B_SPLINE_CURVE' IN TYPEOF (cv)) AND (cv\b_spline_curve.self_intersect = FALSE)OR (cv\b_spline_curve.self_intersect = UNKNOWN)) THEN RETURN(TRUE); ELSE
(* conics and lines are valid curve types *) IF SIZEOF (['AIC_MANIFOLD_SURFACE.CONIC', 'AIC_MANIFOLD_SURFACE.LINE'] * TYPEOF (cv)) = 1 THEN RETURN(TRUE); ELSE
(* a curve_replica shall reference a valid curve *) IF 'AIC_MANIFOLD_SURFACE.CURVE_REPLICA' IN TYPEOF(cv) THEN RETURN (msf_curve_check(cv\curve_replica.parent_curve)); ELSE
(* an offset_curve_3d shall not self-intersect and shall reference a valid curve; a polyline is not a valid basis_curve *) IF (('AIC_MANIFOLD_SURFACE.OFFSET_CURVE_3D' IN TYPEOF (cv)) AND ((cv\offset_curve_3d.self_intersect = FALSE) OR (cv\offset_curve_3d.self_intersect = UNKNOWN)) AND (NOT ('AIC_MANIFOLD_SURFACE.POLYLINE' IN TYPEOF (cv\offset_curve_3d.basis_curve)))) THEN RETURN (msf_curve_check(cv\offset_curve_3d.basis_curve)); ELSE
(* a pcurve shall reference a valid curve and a valid basis_surface *) IF 'AIC_MANIFOLD_SURFACE.PCURVE' IN TYPEOF(cv) THEN RETURN ((msf_curve_check (cv\pcurve.reference_to_curve\representation.items[1])) AND (msf_surface_check(cv\pcurve.basis_surface))); ELSE
(* a surface_curve references a curve_3d and one or two pcurves or one or two surfaces or one of each; all of these references shall be valid *) IF 'AIC_MANIFOLD_SURFACE.SURFACE_CURVE' IN TYPEOF(cv) THEN
(* if the curve reference is correct, check also the rest *) IF msf_curve_check(cv\surface_curve.curve_3d) THEN REPEAT i := 1 TO SIZEOF (cv\surface_curve.associated_geometry);
(* do for one or two associated_geometrys: *) IF 'AIC_MANIFOLD_SURFACE.SURFACE' IN TYPEOF (cv\surface_curve.associated_geometry[i]) THEN IF NOT msf_surface_check (cv\surface_curve.associated_geometry[i]) THEN RETURN(FALSE); END_IF; ELSE IF 'AIC_MANIFOLD_SURFACE.PCURVE' IN TYPEOF (cv\surface_curve.associated_geometry[i]) THEN IF NOT msf_curve_check (cv\surface_curve.associated_geometry[i]) THEN RETURN(FALSE); END_IF; END_IF; END_IF; END_REPEAT; RETURN(TRUE); END_IF; ELSE
(* a polyline shall have at least 3 points *) IF 'AIC_MANIFOLD_SURFACE.POLYLINE' IN TYPEOF(cv) THEN IF (SIZEOF (cv\polyline.points) >= 3) THEN RETURN (TRUE); END_IF; END_IF; END_IF; END_IF; END_IF; END_IF; END_IF; END_IF; (* FALSE is returned if the input parameter cv is not a valid curve. *) RETURN (FALSE);

IF 'AIC_MANIFOLD_SURFACE.ELEMENTARY_SURFACE' IN TYPEOF(surf) THEN
    RETURN(TRUE);
  ELSE 

    (* a swept_surface shall have a valid sweeping curve
     *)
    IF 'AIC_MANIFOLD_SURFACE.SWEPT_SURFACE' IN TYPEOF (surf) THEN 
      RETURN (msf_curve_check(surf\swept_surface.swept_curve));  
    ELSE 

      (* an offset_surface shall not self-intersect and shall
         reference a valid surface
       *)
      IF (('AIC_MANIFOLD_SURFACE.OFFSET_SURFACE' IN TYPEOF (surf)) AND
        (surf\offset_surface.self_intersect = FALSE) OR 
        (surf\offset_surface.self_intersect = UNKNOWN)) THEN
        RETURN (msf_surface_check(surf\offset_surface.basis_surface));
      ELSE 

        (* a surface_replica shall have a valid parent surface
         *)
        IF 'AIC_MANIFOLD_SURFACE.SURFACE_REPLICA' IN TYPEOF(surf) THEN 
          RETURN(msf_surface_check(surf\surface_replica.parent_surface)); 
        ELSE

          (* a b_spline_surface shall not self-intersect
           *)
          IF (('AIC_MANIFOLD_SURFACE.B_SPLINE_SURFACE' IN TYPEOF(surf)) AND
            (surf\b_spline_surface.self_intersect = FALSE) OR
            (surf\b_spline_surface.self_intersect = UNKNOWN)) THEN
            RETURN(TRUE);
          END_IF;
        END_IF; 
      END_IF; 
    END_IF; 
  END_IF; 
  RETURN(FALSE);
